<body>
    <h1>Applying Graphics Filters Using Convolution</h1>
    <p>This demo allows you to apply convolution filters to a pixelated graphical representation of a letter.</p>

    <div id="control-panel">
        <div class="slider-container">
            <label for="slider-convolver">convolver</label>
            <button onclick="decrementSlider('slider-convolver')">-</button>
            <input type="range" id="slider-convolver" min="1" max="5" value="2" step="1">
            <button id="convolver-plus-btn" onclick="incrementSlider('slider-convolver')">+</button>
        </div>
        <div class="slider-container">
            <label for="slider-image-size">image size</label>
            <button onclick="decrementSlider('slider-image-size')">-</button>
            <input type="range" id="slider-image-size" min="10" max="40" value="24" step="1">
            <button id="image-size-plus-btn" onclick="incrementSlider('slider-image-size')">+</button>
        </div>
        <div>
            <label for="input-letter">pick a letter:</label>
            <input type="text" id="input-letter" maxlength="1" value="a">
        </div>
        <div class="radio-group">
            <label for="radio-shadow">shadow</label>
            <input type="radio" name="filter" id="radio-shadow" value="shadow" checked>
            <label for="radio-blur">blur</label>
            <input type="radio" name="filter" id="radio-blur" value="blur">
        </div>
    </div>

    <div id="visualization-area">
        <div>
            <p>before</p>
            <div id="canvas-before-container"></div>
        </div>
        <div>
            <p>after</p>
            <div id="canvas-after-container"></div>
        </div>
    </div>

    <script>
        let beforeCanvas, afterCanvas;

        function setup() {
            beforeCanvas = createCanvas(200, 200);
            beforeCanvas.parent('canvas-before-container');
            afterCanvas = createCanvas(200, 200);
            afterCanvas.parent('canvas-after-container');
            updateVisualization();
        }

        function updateVisualization() {
            const convolver = parseInt(document.getElementById('slider-convolver').value);
            const imageSize = parseInt(document.getElementById('slider-image-size').value);
            const letter = document.getElementById('input-letter').value;
            const filterType = document.querySelector('input[name="filter"]:checked').value;

            const beforeGrid = generateBeforeGrid(imageSize, letter);
            const kernel = generateKernel(convolver, filterType);
            const afterGrid = applyConvolution(beforeGrid, kernel);

            drawGrid(beforeCanvas, beforeGrid, imageSize);
            drawGrid(afterCanvas, afterGrid, imageSize);
        }

        function generateBeforeGrid(N, letter) {
            const grid = Array.from({ length: N }, () => Array(N).fill(0));
            textAlign(CENTER, CENTER);
            textSize(N * 0.8);
            fill(0);
            noStroke();
            text(letter, N / 2, N / 2);
            return grid.map((row, y) => row.map((cell, x) => getPixel(x, y, N)));
        }

        function getPixel(x, y, N) {
            const pixel = get(x * 200 / N, y * 200 / N);
            return pixel.brightness() < 128 ? 1 : 0;
        }

        function generateKernel(k, filterType) {
            const D = 2 * k + 1;
            const kernel = Array.from({ length: D }, () => Array(D).fill(0));

            if (filterType === 'blur') {
                for (let i = 0; i < D; i++) {
                    for (let j = 0; j < D; j++) {
                        kernel[i][j] = 1.0 / (D * D);
                    }
                }
            } else if (filterType === 'shadow') {
                for (let i = 0; i < k; i++) {
                    for (let j = 0; j < k; j++) {
                        kernel[k + i][k + j] = 1.0 / (k * k);
                    }
                }
            }

            return kernel;
        }

        function applyConvolution(grid, kernel) {
            const N = grid.length;
            const D = kernel.length;
            const result = Array.from({ length: N }, () => Array(N).fill(0));

            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    let sum = 0;
                    for (let ki = 0; ki < D; ki++) {
                        for (let kj = 0; kj < D; kj++) {
                            const ii = i + ki - k;
                            const jj = j + kj - k;
                            if (ii >= 0 && ii < N && jj >= 0 && jj < N) {
                                sum += grid[ii][jj] * kernel[ki][kj];
                            }
                        }
                    }
                    result[i][j] = sum;
                }
            }

            return result;
        }

        function drawGrid(canvas, grid, N) {
            canvas.clear();
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    const colorValue = 255 * (1 - grid[i][j]);
                    canvas.fill(colorValue);
                    canvas.noStroke();
                    canvas.rect(j * 200 / N, i * 200 / N, 200 / N, 200 / N);
                }
            }
        }

        function incrementSlider(id) {
            const slider = document.getElementById(id);
            slider.value = Math.min(parseInt(slider.max), parseInt(slider.value) + parseInt(slider.step));
            updateVisualization();
        }

        function decrementSlider(id) {
            const slider = document.getElementById(id);
            slider.value = Math.max(parseInt(slider.min), parseInt(slider.value) - parseInt(slider.step));
            updateVisualization();
        }

        document.getElementById('input-letter').addEventListener('keydown', (event) => {
            if (event.key === 'ArrowUp' || event.key === 'ArrowDown') {
                event.preventDefault();
                let letter = document.getElementById('input-letter').value;
                if (event.key === 'ArrowUp') {
                    letter = String.fromCharCode(((letter.charCodeAt(0) - 97 + 1) % 26) + 97);
                } else if (event.key === 'ArrowDown') {
                    letter = String.fromCharCode(((letter.charCodeAt(0) - 97 - 1 + 26) % 26) + 97);
                }
                document.getElementById('input-letter').value = letter;
                updateVisualization();
            }
        });

        document.getElementById('slider-convolver').addEventListener('input', updateVisualization);
        document.getElementById('slider-image-size').addEventListener('input', updateVisualization);
        document.getElementById('input-letter').addEventListener('input', updateVisualization);
        document.querySelectorAll('input[name="filter"]').forEach(input => input.addEventListener('change', updateVisualization));
    </script>
</body>
</html>
