            margin-bottom: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="view-switcher">
                <button id="btn-crystallography" class="active">crystallography</button>
                <button id="btn-contact-map">contact map</button>
            </div>
            <div id="crystallography-controls">
                <label for="checkbox-lateral-chains">lateral chains</label>
                <input type="checkbox" id="checkbox-lateral-chains" checked>
                <label for="slider-gray-level">gray level</label>
                <input type="range" id="slider-gray-level" min="0" max="1" step="0.01" value="0.5">
                <span id="gray-level-value">0.5</span>
                <label for="slider-browse-sequence">browse the sequence</label>
                <input type="range" id="slider-browse-sequence" min="1" max="400" step="1" value="1">
                <span id="browse-sequence-value">1</span>
                <label for="select-residue">jump to a residue</label>
                <select id="select-residue"></select>
            </div>
            <div id="shared-controls">
                <label for="slider-residue-x">jump to atom x</label>
                <input type="range" id="slider-residue-x" min="1" max="400" step="1" value="50">
                <span id="residue-x-value">50</span>
                <label for="slider-residue-y">jump to atom y</label>
                <input type="range" id="slider-residue-y" min="1" max="400" step="1" value="52">
                <span id="residue-y-value">52</span>
                <label for="slider-rmsd">RMSD</label>
                <input type="range" id="slider-rmsd" min="0" max="15" step="0.1" value="4.9">
                <span id="rmsd-value">4.9</span>
                <label for="slider-plot-size">plot size</label>
                <input type="range" id="slider-plot-size" min="200" max="800" step="1" value="400">
                <span id="plot-size-value">400</span>
            </div>
        </div>
        <div id="visualization-area">
            <button id="btn-reset" style="position: absolute; top: 10px; right: 10px;">+</button>
            <p id="info-display"></p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

    <script>
        const aminoAcids = ['A', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'K', 'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'V', 'W', 'Y'];

        function generateProteinData(numResidues) {
            const proteinData = [];
            let prevPos = new THREE.Vector3(0, 0, 0);
            for (let i = 0; i < numResidues; i++) {
                const resName = aminoAcids[Math.floor(Math.random() * aminoAcids.length)];
                const resSeq = i + 1;
                const caPos = prevPos.clone().add(new THREE.Vector3(Math.random() - 0.5, Math.random() - 0.5, Math.random() - 0.5).multiplyScalar(5));
                const atoms = [
                    { atomName: 'CA', x: caPos.x, y: caPos.y, z: caPos.z },
                    { atomName: 'N', x: caPos.x - 1.45, y: caPos.y + 0.12, z: caPos.z + 0.12 },
                    { atomName: 'C', x: caPos.x + 1.54, y: caPos.y - 0.11, z: caPos.z - 0.11 },
                    { atomName: 'O', x: caPos.x + 1.54, y: caPos.y - 0.11, z: caPos.z - 0.11 },
                    { atomName: 'CB', x: caPos.x - 0.52, y: caPos.y + 0.87, z: caPos.z - 0.14 }
                ];
                proteinData.push({ resName, resSeq, atoms });
                prevPos = caPos;
            }
            return proteinData;
        }

        const proteinData = generateProteinData(400);

        const canvasContainer = document.getElementById('canvas-container');
        const infoDisplay = document.getElementById('info-display');

        let scene, camera, renderer, controls;
        let crystallographyControls = true;
        let lateralChains = true;
        let grayLevel = 0.5;
        let browseSequence = 1;
        let residueX = 50;
        let residueY = 52;
        let rmsd = 4.9;
        let plotSize = 400;

        function init3D() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(75, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 1000);
            camera.position.z = 200;

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvasContainer.appendChild(renderer.domElement);

            controls = new THREE.OrbitControls(camera, renderer.domElement);

            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1).normalize();
            scene.add(directionalLight);

            proteinData.forEach(residue => {
                residue.atoms.forEach(atom => {
                    const geometry = new THREE.SphereGeometry(0.5, 32, 32);
                    let color = 0x0000ff;
                    if (atom.atomName === 'N' || atom.atomName === 'C' || atom.atomName === 'O' || atom.atomName === 'CB') {
                        color = 0x0000ff;
                    }
                    const material = new THREE.MeshLambertMaterial({ color });
                    const sphere = new THREE.Mesh(geometry, material);
                    sphere.position.set(atom.x, atom.y, atom.z);
                    scene.add(sphere);
                });
            });

            const boxHelper = new THREE.BoxHelper(scene, 0x808080);
            scene.add(boxHelper);

            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        function init2D() {
            const sketch = function(p) {
                p.setup = function() {
                    p.createCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
                };

                p.draw = function() {
                    p.background(255);
                    p.stroke(150);
                    p.line(0, p.height, p.width, p.height);
                    p.line(p.width, 0, p.width, p.height);

                    for (let i = 1; i <= 400; i += 100) {
                        p.text(i, 5, i);
                        p.text(i, i, p.height - 5);
                    }

                    for (let i = 0; i < proteinData.length; i++) {
                        for (let j = 0; j < proteinData.length; j++) {
                            const dist = proteinData[i].atoms.find(a => a.atomName === 'CA').position.distanceTo(proteinData[j].atoms.find(a => a.atomName === 'CA').position);
                            if (dist <= rmsd) {
                                p.fill(0);
                                p.noStroke();
                                p.ellipse(i, j, 2, 2);
                            }
                        }
                    }

                    p.stroke(255, 0, 0);
                    p.line(residueX, 0, residueX, p.height);
                    p.line(0, residueY, p.width, residueY);
                };
            };

            new p5(sketch, canvasContainer);
        }

        function updateInfoDisplay() {
            const resA = proteinData[browseSequence - 1];
            const resX = proteinData[residueX - 1];
            const resY = proteinData[residueY - 1];
            infoDisplay.textContent = `Res A: ${resA.resName}${resA.resSeq} | Res X: ${resX.resName}${resX.resSeq} | Res Y: ${resY.resName}${resY.resSeq}`;
        }

        function handleSliderChange(event) {
            const target = event.target;
            const value = target.value;
            const id = target.id;
            document.getElementById(`${id}-value`).textContent = value;

            switch (id) {
                case 'slider-gray-level':
                    grayLevel = parseFloat(value);
                    break;
                case 'slider-browse-sequence':
                    browseSequence = parseInt(value);
                    break;
                case 'slider-residue-x':
                    residueX = parseInt(value);
                    break;
                case 'slider-residue-y':
                    residueY = parseInt(value);
                    break;
                case 'slider-rmsd':
                    rmsd = parseFloat(value);
                    break;
                case 'slider-plot-size':
                    plotSize = parseInt(value);
                    canvasContainer.style.width = `${plotSize}px`;
                    canvasContainer.style.height = `${plotSize}px`;
                    if (crystallographyControls) {
                        camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
                        camera.updateProjectionMatrix();
                        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
                    } else {
                        p5.resizeCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
                    }
                    break;
            }

            updateInfoDisplay();
        }

        document.getElementById('btn-crystallography').addEventListener('click', () => {
            document.getElementById('btn-crystallography').classList.add('active');
            document.getElementById('btn-contact-map').classList.remove('active');
            crystallographyControls = true;
            canvasContainer.innerHTML = '';
            init3D();
        });

        document.getElementById('btn-contact-map').addEventListener('click', () => {
            document.getElementById('btn-contact-map').classList.add('active');
            document.getElementById('btn-crystallography').classList.remove('active');
            crystallographyControls = false;
            canvasContainer.innerHTML = '';
            init2D();
        });

        document.getElementById('checkbox-lateral-chains').addEventListener('change', (event) => {
            lateralChains = event.target.checked;
        });

        document.querySelectorAll('input[type="range"]').forEach(input => {
            input.addEventListener('input', handleSliderChange);
        });

        document.getElementById('select-residue').addEventListener('change', (event) => {
            const selectedResidue = event.target.value;
            for (let i = 0; i < proteinData.length; i++) {
                if (proteinData[i].resName === selectedResidue) {
                    document.getElementById('slider-browse-sequence').value = i + 1;
                    browseSequence = i + 1;
                    break;
                }
            }
            handleSliderChange({ target: document.getElementById('slider-browse-sequence') });
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            camera.position.set(200, 200, 200);
            controls.reset();
        });

        document.getElementById('select-residue').innerHTML = aminoAcids.map(aa => `<option value="${aa}">${aa}</option>`).join('');

        init3D();
    </script>
</body>
</html>
