            background-color: lightgreen;
        }
        #canvas-container {
            width: 100%;
            height: 600px;
            margin-bottom: 20px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div class="control-panel">
            <div>
                <input type="radio" id="radio-rivers" name="quiz-type" value="rivers" checked>
                <label for="radio-rivers">rivers</label>
                <input type="radio" id="radio-lakes" name="quiz-type" value="lakes">
                <label for="radio-lakes">lakes</label>
            </div>
            <div>
                <button id="btn-reset">reset</button>
            </div>
            <div>
                <input type="checkbox" id="check-show-locations" checked>
                <label for="check-show-locations">show locations</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-some">
                <label for="check-show-some">show some rivers and lakes</label>
            </div>
        </div>
        <p id="p-position">Current position {-172, -19}</p>
        <div id="table-container">
            <table id="table-quiz"></table>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const riversData = [
            { name: 'Nile', lon: 31.2, lat: 30.2, found: false },
            { name: 'Saint Lawrence', lon: -64.5, lat: 49.5, found: false },
            // Add remaining river data here
        ];

        const lakesData = [
            { name: 'Onega', lon: 35.5, lat: 61.7, found: false },
            { name: 'Erie', lon: -81.2, lat: 42.2, found: false },
            // Add remaining lake data here
        ];

        let mapImage;
        let currentQuizType = 'rivers';
        let currentData;

        function preload() {
            mapImage = loadImage('https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg');
        }

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            currentData = currentQuizType === 'rivers' ? riversData : lakesData;
            populateTable();
        }

        function draw() {
            background(255);
            image(mapImage, 0, 0, width, height);

            if (document.getElementById('check-show-locations').checked) {
                currentData.forEach(location => {
                    if (!location.found) {
                        const [x, y] = mapToPixels(location.lon, location.lat);
                        fill('red');
                        noStroke();
                        circle(x, y, 10);
                    }
                });
            }

            if (document.getElementById('check-show-some').checked) {
                const shownLocations = shuffle(currentData.filter(loc => !loc.found)).slice(0, 3);
                shownLocations.forEach(location => {
                    const [x, y] = mapToPixels(location.lon, location.lat);
                    fill('white');
                    textSize(12);
                    text(location.name, x + 10, y);
                });
            }

            fill(173, 216, 230, 150);
            noStroke();
            circle(mouseX, mouseY, 30);
        }

        function mouseMoved() {
            const [lon, lat] = mapToGeo(mouseX, mouseY);
            document.getElementById('p-position').textContent = `Current position {${Math.round(lon)}, ${Math.round(lat)}}`;
        }

        function mouseClicked() {
            currentData.some(location => {
                if (!location.found) {
                    const [x, y] = mapToPixels(location.lon, location.lat);
                    if (dist(mouseX, mouseY, x, y) < 15) {
                        location.found = true;
                        const table = document.getElementById('table-quiz');
                        const rows = table.getElementsByTagName('tr');
                        for (let i = 1; i < rows.length; i++) {
                            const cells = rows[i].getElementsByTagName('td');
                            if (cells[0].textContent === location.name) {
                                cells[1].textContent = 'True';
                                cells[1].classList.add('found');
                                break;
                            }
                        }
                        return true;
                    }
                }
                return false;
            });
            redraw();
        }

        function mapToPixels(lon, lat) {
            const x = map(lon, -180, 180, 0, width);
            const y = map(lat, 90, -90, 0, height);
            return [x, y];
        }

        function mapToGeo(x, y) {
            const lon = map(x, 0, width, -180, 180);
            const lat = map(y, 0, height, 90, -90);
            return [lon, lat];
        }

        function populateTable() {
            const table = document.getElementById('table-quiz');
            table.innerHTML = '';
            const headerRow = table.insertRow();
            const dataRow = table.insertRow();

            currentData.forEach(location => {
                const nameCell = headerRow.insertCell();
                nameCell.textContent = location.name;

                const statusCell = dataRow.insertCell();
                statusCell.textContent = 'False';
            });
        }

        document.getElementById('radio-rivers').addEventListener('change', () => {
            if (currentQuizType !== 'rivers') {
                currentQuizType = 'rivers';
                currentData = riversData;
                resetQuiz();
            }
        });

        document.getElementById('radio-lakes').addEventListener('change', () => {
            if (currentQuizType !== 'lakes') {
                currentQuizType = 'lakes';
                currentData = lakesData;
                resetQuiz();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            resetQuiz();
        });

        function resetQuiz() {
            currentData.forEach(location => location.found = false);
            populateTable();
            redraw();
        }
    </script>
</body>
</html>
