<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Interactive Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #control-panel {
            width: 100%;
            padding: 20px;
            background-color: #f4f4f4;
            display: flex;
            justify-content: center;
            align-items: center;
        }
        #visualization-area {
            display: flex;
            flex-direction: row;
            width: 80%;
            margin-top: 20px;
        }
        #function-plot-container, #sum-plot-container {
            position: relative;
            flex: 1;
        }
        #function-plot-container {
            flex-grow: 2;
        }
        #sum-plot-container {
            flex-grow: 1;
        }
        #canvas-function, #canvas-sum {
            border: 1px solid #ccc;
        }
        #formula-label {
            position: absolute;
            top: 10px;
            left: 10px;
            background-color: rgba(255, 255, 255, 0.8);
            padding: 5px;
            border-radius: 5px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="control-panel">
        <label for="slider-n">n</label>
        <input type="range" id="slider-n" min="1" max="20" step="1" value="1">
        <input type="number" id="input-n" min="1" max="20" step="1" value="1">
    </div>
    <div id="visualization-area">
        <div id="function-plot-container">
            <canvas id="canvas-function"></canvas>
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
        </div>
        <div id="sum-plot-container">
            <canvas id="canvas-sum"></canvas>
        </div>
    </div>

    <script>
        const colors = ['#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', '#6495ed', '#9370db', '#8a2be2'];

        let n = 1;

        const slider = document.getElementById('slider-n');
        const input = document.getElementById('input-n');

        slider.addEventListener('input', () => {
            n = parseInt(slider.value);
            input.value = n;
            drawPlots();
        });

        input.addEventListener('input', () => {
            n = parseInt(input.value);
            slider.value = n;
            drawPlots();
        });

        function setupFunctionPlot() {
            const canvasFunction = document.getElementById('canvas-function');
            const functionSketch = new p5((p) => {
                p.setup = () => {
                    p.createCanvas(canvasFunction.clientWidth, canvasFunction.clientHeight);
                };

                p.draw = () => {
                    p.clear();
                    p.background(255);
                    p.stroke(0);
                    p.strokeWeight(1);

                    // Draw axes
                    p.line(0, p.height / 2, p.width, p.height / 2); // X-axis
                    p.line(p.width / 2, 0, p.width / 2, p.height); // Y-axis

                    // Draw ticks and labels
                    for (let i = 1; i <= 10; i++) {
                        p.line(i * p.width / 10.5, p.height / 2 - 5, i * p.width / 10.5, p.height / 2 + 5);
                        p.text(i, i * p.width / 10.5, p.height / 2 + 15);
                    }
                    for (let i = 0; i <= 2; i++) {
                        p.line(p.width / 2 - 5, p.height - i * p.height / 2.2, p.width / 2 + 5, p.height - i * p.height / 2.2);
                        p.text(i, p.width / 2 - 15, p.height - i * p.height / 2.2 + 5);
                    }

                    // Draw function y = 2/x
                    p.stroke(0, 0, 255);
                    p.noFill();
                    p.beginShape();
                    for (let x = 0.5; x <= 10; x += 0.01) {
                        let y = 2 / x;
                        p.vertex(x * p.width / 10.5, p.height - y * p.height / 2.2);
                    }
                    p.endShape();

                    // Draw rectangles
                    for (let k = 1; k <= n; k++) {
                        let y1 = 2 / (k + 1);
                        let y2 = 2 / k;
                        p.fill(colors[k % colors.length]);
                        p.rect(k * p.width / 10.5, p.height - y2 * p.height / 2.2, p.width / 10.5, (y2 - y1) * p.height / 2.2);

                        if (k <= 3) {
                            p.stroke(128, 128, 128, 128);
                            p.drawingContext.setLineDash([5, 5]);
                            p.line(k * p.width / 10.5, p.height / 2, k * p.width / 10.5, p.height - y2 * p.height / 2.2);
                            p.drawingContext.setLineDash([]);
                        }
                    }
                };
            }, canvasFunction);
        }

        function setupSumPlot() {
            const canvasSum = document.getElementById('canvas-sum');
            const sumSketch = new p5((p) => {
                p.setup = () => {
                    p.createCanvas(canvasSum.clientWidth, canvasSum.clientHeight);
                };

                p.draw = () => {
                    p.clear();
                    p.background(255);
                    p.stroke(0);
                    p.strokeWeight(1);

                    // Draw axes
                    p.line(0, p.height / 2, p.width, p.height / 2); // X-axis
                    p.line(p.width / 2, 0, p.width / 2, p.height); // Y-axis

                    // Draw ticks and labels
                    for (let i = 1; i <= 1; i++) {
                        p.line(i * p.width / 1.5, p.height / 2 - 5, i * p.width / 1.5, p.height / 2 + 5);
                        p.text(i, i * p.width / 1.5, p.height / 2 + 15);
                    }
                    for (let i = 0; i <= 2; i++) {
                        p.line(p.width / 2 - 5, p.height - i * p.height / 2.2, p.width / 2 + 5, p.height - i * p.height / 2.2);
                        p.text(i, p.width / 2 - 15, p.height - i * p.height / 2.2 + 5);
                    }

                    // Draw rectangles
                    let yStack = 0;
                    for (let k = n; k >= 1; k--) {
                        let h = 2 / k - 2 / (k + 1);
                        p.fill(colors[k % colors.length]);
                        p.rect(0, p.height - yStack * p.height / 2.2, p.width, h * p.height / 2.2);
                        yStack += h;
                    }
                };
            }, canvasSum);
        }

        function drawPlots() {
            setupFunctionPlot();
            setupSumPlot();
        }

        drawPlots();
    </script>
</body>
</html>
