            <div class="slider-container" style="display: none;">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1">0.4545</span>
            </div>
            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                <span id="value-ds">0.6</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        let currentModel = 'monod';
        let currentView = 'bifurcation';

        const params = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        function updatePlot() {
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        function drawBifurcationDiagram() {
            const x2f = params.x2f;
            const umax = params.umax;
            const km = params.km;
            const k1 = params.k1;
            const ds = params.ds;

            const x2Values = Array.from({ length: 100 }, (_, i) => i * x2f / 100);
            const DValues = x2Values.map(x2 => {
                if (currentModel === 'monod') {
                    return umax * x2 / (km + x2);
                } else {
                    return umax * x2 / (km + x2 + k1 * x2 * x2);
                }
            });

            const data = [
                {
                    x: DValues,
                    y: x2Values,
                    mode: 'lines',
                    name: 'Nontrivial Steady State',
                    line: { color: 'blue' }
                }
            ];

            if (currentModel === 'si') {
                const x2Crit = Math.sqrt(km / k1);
                const stableX2 = x2Values.filter(x2 => x2 < x2Crit);
                const unstableX2 = x2Values.filter(x2 => x2 >= x2Crit);

                data.push({
                    x: stableX2.map(x2 => umax * x2 / (km + x2 + k1 * x2 * x2)),
                    y: stableX2,
                    mode: 'lines',
                    name: 'Stable Nontrivial Steady State',
                    line: { color: 'blue' }
                });
                data.push({
                    x: unstableX2.map(x2 => umax * x2 / (km + x2 + k1 * x2 * x2)),
                    y: unstableX2,
                    mode: 'lines',
                    name: 'Unstable Nontrivial Steady State',
                    line: { color: 'red', dash: 'dash' }
                });
            }

            const D_bif = umax * x2f / (km + x2f);
            data.push({
                x: [0, D_bif],
                y: [x2f, x2f],
                mode: 'lines',
                name: 'Trivial Steady State (Unstable)',
                line: { color: 'red', dash: 'dash' }
            });
            data.push({
                x: [D_bif, 1.0],
                y: [x2f, x2f],
                mode: 'lines',
                name: 'Trivial Steady State (Stable)',
                line: { color: 'blue' }
            });

            const layout = {
                title: 'Bifurcation Diagram',
                xaxis: { title: 'Dilution Rate' },
                yaxis: { title: 'X₂' },
                shapes: [{
                    type: 'line',
                    x0: ds,
                    y0: 0,
                    x1: ds,
                    y1: 6.0,
                    line: { color: 'gray', dash: 'dot' }
                }]
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        function drawNontrivialStatePlot() {
            const x2f = params.x2f;
            const umax = params.umax;
            const km = params.km;
            const k1 = params.k1;
            const ds = params.ds;

            const x2Values = Array.from({ length: 100 }, (_, i) => i * 7 / 100);
            const muValues = x2Values.map(x2 => umax * x2 / (km + x2 + k1 * x2 * x2));

            const data = [
                {
                    x: x2Values,
                    y: muValues,
                    mode: 'lines',
                    name: 'μ(x₂)',
                    line: { color: 'blue' }
                },
                {
                    x: [0, 7],
                    y: [ds, ds],
                    mode: 'lines',
                    name: 'Dₛ',
                    line: { color: 'gray' }
                }
            ];

            const D_upper = umax / (1 + 2 * Math.sqrt(k1 * km));
            const D_lower = umax * x2f / (km + x2f + k1 * x2f * x2f);

            data.push({
                x: [0, 7],
                y: [D_upper, D_upper],
                mode: 'lines',
                name: 'D_upper',
                line: { color: 'red', dash: 'dash' }
            });
            data.push({
                x: [0, 7],
                y: [D_lower, D_lower],
                mode: 'lines',
                name: 'D_lower',
                line: { color: 'red', dash: 'dash' }
            });

            const layout = {
                title: 'Nontrivial Steady State Plot',
                xaxis: { title: 'Substrate Concentration' },
                yaxis: { title: 'Specific Growth Rate Coefficient' },
                shapes: [{
                    type: 'rect',
                    x0: 0,
                    y0: D_lower,
                    x1: 7,
                    y1: D_upper,
                    fillcolor: 'rgba(255, 165, 0, 0.3)',
                    line: { color: 'rgba(255, 165, 0, 0)' }
                }]
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        document.getElementById('btn-monod').addEventListener('click', () => {
            currentModel = 'monod';
            document.getElementById('btn-monod').classList.add('active');
            document.getElementById('btn-si').classList.remove('active');
            document.querySelector('.slider-container:last-child').style.display = 'none';
            updatePlot();
        });

        document.getElementById('btn-si').addEventListener('click', () => {
            currentModel = 'si';
            document.getElementById('btn-si').classList.add('active');
            document.getElementById('btn-monod').classList.remove('active');
            document.querySelector('.slider-container:last-child').style.display = 'block';
            updatePlot();
        });

        document.getElementById('btn-bifurcation').addEventListener('click', () => {
            currentView = 'bifurcation';
            document.getElementById('btn-bifurcation').classList.add('active');
            document.getElementById('btn-nontrivial').classList.remove('active');
            updatePlot();
        });

        document.getElementById('btn-nontrivial').addEventListener('click', () => {
            currentView = 'nontrivial';
            document.getElementById('btn-nontrivial').classList.add('active');
            document.getElementById('btn-bifurcation').classList.remove('active');
            updatePlot();
        });

        document.querySelectorAll('.slider-container input[type="range"]').forEach(slider => {
            slider.addEventListener('input', () => {
                params[slider.id.replace('slider-', '')] = parseFloat(slider.value);
                document.getElementById(`value-${slider.id.replace('slider-', '')}`).textContent = slider.value.toFixed(2);
                updatePlot();
            });
        });

        updatePlot();
    </script>
</body>
</html>
