    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth / 2, 600);
        document.getElementById('globe-container').appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        const geometry = new THREE.SphereGeometry(5, 32, 32);
        const material = new THREE.MeshPhongMaterial({ map: generateProceduralTexture() });
        const earth = new THREE.Mesh(geometry, material);
        scene.add(earth);

        const light = new THREE.DirectionalLight(0xffffff, 1);
        light.position.set(0, 1, -1).normalize();
        scene.add(light);
        scene.add(new THREE.AmbientLight(0x404040));

        camera.position.z = 15;

        function generateProceduralTexture() {
            const canvas = document.createElement('canvas');
            canvas.width = 256;
            canvas.height = 256;
            const context = canvas.getContext('2d');
            context.fillStyle = 'blue';
            context.fillRect(0, 0, 256, 256);
            context.fillStyle = 'green';
            context.beginPath();
            context.arc(128, 128, 80, 0, Math.PI * 2);
            context.fill();
            return new THREE.CanvasTexture(canvas);
        }

        let selectedLatitudeLine, surfaceNormalVector, sunVector;

        function createLatitudinalLine(latitude) {
            const phi = (90 - latitude) * Math.PI / 180;
            const radius = 5 * Math.sin(phi);
            const theta = 0;
            const geometry = new THREE.CircleGeometry(radius, 32);
            geometry.rotateX(Math.PI / 2);
            geometry.translate(0, 0, 5 * Math.cos(phi));
            const material = new THREE.LineBasicMaterial({ color: latitude === 51.4789 ? 0x0000ff : 0x000000 });
            const line = new THREE.Line(geometry, material);
            scene.add(line);
            return line;
        }

        function createVectors() {
            const material = new THREE.LineBasicMaterial({ color: 0x000000 });
            const axis = new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), new THREE.Vector3(0, 0, 0), 5, 0xff0000);
            scene.add(axis);

            const surfaceNormal = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), 5, 0x000000);
            scene.add(surfaceNormal);

            const sunDir = new THREE.ArrowHelper(new THREE.Vector3(0, 0, -1), new THREE.Vector3(0, 0, 0), 5, 0xff0000);
            scene.add(sunDir);

            return { axis, surfaceNormal, sunDir };
        }

        selectedLatitudeLine = createLatitudinalLine(51.4789);
        ({ axis, surfaceNormalVector, sunVector } = createVectors());

        function updateGlobe(season, latitude, time) {
            const delta = 23.5 * Math.cos(2 * Math.PI * season);
            const phi = (90 - latitude) * Math.PI / 180;
            const theta = (time / 24) * 2 * Math.PI;
            earth.rotation.y = theta;

            light.position.set(0, Math.sin(delta * Math.PI / 180), -Math.cos(delta * Math.PI / 180)).normalize();

            selectedLatitudeLine.geometry.dispose();
            selectedLatitudeLine = createLatitudinalLine(latitude);

            surfaceNormalVector.setDirection(new THREE.Vector3(0, 0, 1));
            surfaceNormalVector.position.set(0, 0, 5 * Math.cos(phi));
            surfaceNormalVector.setLength(5 * Math.sin(phi));

            sunVector.setDirection(new THREE.Vector3(0, Math.sin(delta * Math.PI / 180), -Math.cos(delta * Math.PI / 180)));
            sunVector.position.set(0, 0, 5 * Math.cos(phi));
            sunVector.setLength(5 * Math.sin(phi));
        }

        const instantData = [
            { x: [], y: [], mode: 'lines', name: 'Equator', line: { color: 'red' } },
            { x: [], y: [], mode: 'lines', name: 'Selected Latitude', line: { color: 'black' } },
            { x: [], y: [], mode: 'lines', name: 'North Pole', line: { color: 'blue' } },
            { x: [], y: [], mode: 'markers', name: 'Current Time', marker: { color: 'black' } }
        ];

        const cumulativeData = [
            { x: [], y: [], mode: 'lines', name: 'Equator', line: { color: 'red' } },
            { x: [], y: [], mode: 'lines', name: 'Selected Latitude', line: { color: 'black' } },
            { x: [], y: [], mode: 'lines', name: 'North Pole', line: { color: 'blue' } }
        ];

        function updatePlots(season, latitude, time) {
            const delta = 23.5 * Math.cos(2 * Math.PI * season);
            const latitudes = [0, latitude, 90];

            for (let i = 0; i < 3; i++) {
                const lat = latitudes[i];
                const phi = (90 - lat) * Math.PI / 180;
                instantData[i].x = [];
                instantData[i].y = [];
                cumulativeData[i].x = [];
                cumulativeData[i].y = [];
                let cumulativeEnergy = 0;

                for (let t = 0; t <= 24; t++) {
                    const HRA = (t - 12) * 15 * Math.PI / 180;
                    const energy = 1361 * Math.max(0, Math.sin(lat * Math.PI / 180) * Math.sin(delta * Math.PI / 180) + Math.cos(lat * Math.PI / 180) * Math.cos(delta * Math.PI / 180) * Math.cos(HRA));
                    instantData[i].x.push(t);
                    instantData[i].y.push(energy);
                    cumulativeEnergy += energy;
                    cumulativeData[i].x.push(t);
                    cumulativeData[i].y.push(cumulativeEnergy);
                }
            }

            instantData[3].x = [time];
            instantData[3].y = [instantData[1].y.find((_, index) => instantData[1].x[index] === time)];

            Plotly.newPlot('plot-instant', instantData, {
                title: 'energy',
                yaxis: { title: 'watts/m^2', range: [0, 1400] },
                xaxis: { title: 'time (hr)', range: [0, 24] }
            });

            Plotly.newPlot('plot-cumulative', cumulativeData, {
                title: 'energy',
                yaxis: { title: 'watts/m^2', range: [0, 14000] },
                xaxis: { title: 'time (hr)', range: [0, 24] }
            });
        }

        function update() {
            const season = parseFloat(document.getElementById('slider-season').value);
            const latitude = parseFloat(document.getElementById('slider-latitude').value);
            const time = parseFloat(document.getElementById('slider-time').value);

            document.getElementById('value-season').textContent = season.toFixed(3);
            document.getElementById('value-latitude').textContent = latitude.toFixed(2);
            document.getElementById('value-time').textContent = time.toFixed(2);

            updateGlobe(season, latitude, time);
            updatePlots(season, latitude, time);

            renderer.render(scene, camera);
        }

        document.getElementById('slider-season').addEventListener('input', update);
        document.getElementById('slider-latitude').addEventListener('input', update);
        document.getElementById('slider-time').addEventListener('input', update);

        update();
    </script>
</body>
</html>
