            flex-direction: column;
            align-items: center;
            width: 80%;
            max-width: 600px;
            background-color: #fff;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            padding: 20px;
            border-radius: 8px;
        }
        #control-panel {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
        }
        #control-panel span {
            margin-right: 10px;
        }
