            border: 1px solid #ccc;
            width: 100%;
            height: 400px;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <span>Area:</span>
            <input type="range" id="slider-area" min="1" max="101" step="1" value="13">
            <input type="number" id="input-area" min="1" max="101" step="1" value="13">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const congruentData = {
            5: { a: {n: 3, d: 2}, b: {n: 20, d: 3}, c: {n: 41, d: 6} },
            6: { a: {n: 3, d: 1}, b: {n: 4, d: 1}, c: {n: 5, d: 1} },
            7: { a: {n: 35, d: 12}, b: {n: 24, d: 5}, c: {n: 337, d: 60} },
            13: { a: {n: 780, d: 323}, b: {n: 323, d: 30}, c: {n: 106921, d: 9690} },
            79: { a: {n: 233126551, d: 167973000}, b: {n: 335946000, d: 2950969}, c: {n: 56434050774922081, d: 495683115837000} }
        };

        let slider, input;

        function setup() {
            const canvas = createCanvas(600, 400);
            canvas.parent('canvas-container');
            slider = select('#slider-area');
            input = select('#input-area');
            slider.input(updateValue);
            input.input(updateValue);
            updateValue();
        }

        function drawFraction(fractionObject, x, y, rotation, fontSize) {
            push();
            translate(x, y);
            rotate(rotation);
            textSize(fontSize);
            textAlign(CENTER, CENTER);
            noStroke();
            fill('#8B5A2B');
            const numStr = fractionObject.n.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
            const denStr = fractionObject.d.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
            const numWidth = textWidth(numStr);
            const denWidth = textWidth(denStr);
            const lineWidth = max(numWidth, denWidth);
            text(numStr, 0, 0);
            text(denStr, 0, fontSize + 5);
            line(-lineWidth / 2, fontSize + 2, lineWidth / 2, fontSize + 2);
            pop();
        }

        function draw() {
            clear();
            const n = parseInt(input.value());
            if (congruentData[n]) {
                const { a, b, c } = congruentData[n];
                const scale = min((width - 40) / (a.n / a.d), (height - 40) / (b.n / b.d));
                const ax = (width - (a.n / a.d) * scale) / 2;
                const ay = (height - (b.n / b.d) * scale) / 2;
                const bx = ax + (a.n / a.d) * scale;
                const by = ay;
                const cx = ax;
                const cy = ay + (b.n / b.d) * scale;

                fill('#FFF2E5');
                stroke('#000000');
                strokeWeight(2);
                beginShape();
                vertex(ax, ay);
                vertex(bx, by);
                vertex(cx, cy);
                endShape(CLOSE);

                drawFraction(a, ax + (a.n / a.d) * scale / 2, ay + 20, 0, 16);
                drawFraction(b, bx + 20, by + (b.n / b.d) * scale / 2, 0, 16);

                const dx = cx - bx;
                const dy = cy - by;
                const angle = atan2(dy, dx);
                drawFraction(c, (bx + cx) / 2 + 20, (by + cy) / 2, angle, 16);
            } else {
                fill('#FF0000');
                textSize(20);
                textAlign(CENTER, CENTER);
                text(`${n} is not a congruent number`, width / 2, height / 2);
            }
        }

        function updateValue() {
            const value = slider.value();
            input.value(value);
            redraw();
        }
    </script>
</body>
</html>
