                    <label for="slider-r">r</label>
                    <input type="range" id="slider-r" min="0.1" max="2.0" step="0.01" value="1.0">
                    <span id="label-r">1</span>
                </div>
            </div>
        </div>

        <!-- 2. Problem Control Panel -->
        <div id="problem-controls-panel">
            <div class="problem-column">
                <p>primal problem: minimize costs C given volume Q</p>
                <div class="control-item">
                    <label for="slider-Q">Q</label>
                    <input type="range" id="slider-Q" min="1" max="10" step="0.1" value="3.0">
                    <span id="label-Q">3</span>
                </div>
                <button id="btn-Q-from-dual">Q from dual</button>
            </div>
            <div class="problem-column">
                <p>dual problem: maximize volume Q given costs C</p>
                <div class="control-item">
                    <label for="slider-C">C</label>
                    <input type="range" id="slider-C" min="1" max="10" step="0.1" value="4.0">
                    <span id="label-C">4</span>
                </div>
                <button id="btn-C-from-primal">C from primal</button>
            </div>
        </div>

        <!-- 3. Visualization Area -->
        <div id="visualization-area">
            <div id="plot-primal" class="plot-container"></div>
            <div id="plot-dual" class="plot-container"></div>
        </div>

        <!-- 4. Output Display -->
        <div id="output-area">
            <div id="output-primal" class="output-box">x = 3.00, y = 3.00, C = 6.00</div>
            <div id="output-dual" class="output-box">x = 2.00, y = 2.00, Q = 2.00</div>
        </div>

    </div>

    <script>
        const scenePrimal = new THREE.Scene();
        const cameraPrimal = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const rendererPrimal = new THREE.WebGLRenderer();
        rendererPrimal.setSize(window.innerWidth / 2, window.innerHeight / 2);
        document.getElementById('plot-primal').appendChild(rendererPrimal.domElement);

        const sceneDual = new THREE.Scene();
        const cameraDual = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const rendererDual = new THREE.WebGLRenderer();
        rendererDual.setSize(window.innerWidth / 2, window.innerHeight / 2);
        document.getElementById('plot-dual').appendChild(rendererDual.domElement);

        const controlsPrimal = new THREE.OrbitControls(cameraPrimal, rendererPrimal.domElement);
        const controlsDual = new THREE.OrbitControls(cameraDual, rendererDual.domElement);

        const boxHelperPrimal = new THREE.BoxHelper(new THREE.Mesh(), 0x000000);
        scenePrimal.add(boxHelperPrimal);

        const boxHelperDual = new THREE.BoxHelper(new THREE.Mesh(), 0x000000);
        sceneDual.add(boxHelperDual);

        const axesHelperPrimal = new THREE.AxesHelper(10);
        scenePrimal.add(axesHelperPrimal);

        const axesHelperDual = new THREE.AxesHelper(10);
        sceneDual.add(axesHelperDual);

        const ambientLightPrimal = new THREE.AmbientLight(0x404040);
        scenePrimal.add(ambientLightPrimal);

        const ambientLightDual = new THREE.AmbientLight(0x404040);
        sceneDual.add(ambientLightDual);

        cameraPrimal.position.z = 20;
        cameraDual.position.z = 20;

        function update() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const w = parseFloat(document.getElementById('slider-w').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const Q_in = parseFloat(document.getElementById('slider-Q').value);
            const C_in = parseFloat(document.getElementById('slider-C').value);

            // Primal Problem
            const k = (w * beta) / (r * alpha);
            const x_p = Math.pow(Q_in / Math.pow(k, beta), 1 / (alpha + beta));
            const y_p = x_p * k;
            const C_out = w * x_p + r * y_p;

            document.getElementById('label-Q').textContent = Q_in.toFixed(1);
            document.getElementById('label-C').textContent = C_in.toFixed(1);
            document.getElementById('label-alpha').textContent = alpha.toFixed(2);
            document.getElementById('label-beta').textContent = beta.toFixed(2);
            document.getElementById('label-w').textContent = w.toFixed(2);
            document.getElementById('label-r').textContent = r.toFixed(2);
            document.getElementById('output-primal').textContent = `x = ${x_p.toFixed(2)}, y = ${y_p.toFixed(2)}, C = ${C_out.toFixed(2)}`;

            // Dual Problem
            const x_d = (C_in * alpha) / (w * (alpha + beta));
            const y_d = (C_in * beta) / (r * (alpha + beta));
            const Q_out = Math.pow(x_d, alpha) * Math.pow(y_d, beta);

            document.getElementById('output-dual').textContent = `x = ${x_d.toFixed(2)}, y = ${y_d.toFixed(2)}, Q = ${Q_out.toFixed(2)}`;

            // Render plots
            renderPrimal(alpha, beta, w, r, Q_in, x_p, y_p, C_out);
            renderDual(alpha, beta, w, r, C_in, x_d, y_d, Q_out);
        }

        function renderPrimal(alpha, beta, w, r, Q_in, x_p, y_p, C_out) {
            scenePrimal.remove(...scenePrimal.children.filter(child => child.type === 'Mesh' || child.type === 'Line'));

            const isoquantGeometry = new THREE.BufferGeometry();
            const vertices = [];
            for (let x = 0; x <= 10; x += 0.1) {
                const y = Math.pow(Q_in / Math.pow(x, alpha), 1 / beta);
                if (y > 0 && y <= 10) {
                    vertices.push(x, y, 0);
                    vertices.push(x, y, C_out);
                }
            }
            isoquantGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            const isoquantMaterial = new THREE.MeshBasicMaterial({ color: 0xffa500, opacity: 0.5, transparent: true });
            const isoquantMesh = new THREE.Mesh(isoquantGeometry, isoquantMaterial);
            scenePrimal.add(isoquantMesh);

            const isocostGeometry = new THREE.PlaneGeometry(10, 10);
            const isocostMaterial = new THREE.MeshBasicMaterial({ color: 0x808080, opacity: 0.5, transparent: true, side: THREE.DoubleSide });
            const isocostMesh = new THREE.Mesh(isocostGeometry, isocostMaterial);
            isocostMesh.rotation.x = -Math.PI / 2;
            isocostMesh.position.set(5, 5, C_out);
            scenePrimal.add(isocostMesh);

            const solutionGeometry = new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0, 0, 0), new THREE.Vector3(x_p, y_p, C_out)]);
            const solutionMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 2 });
            const solutionLine = new THREE.Line(solutionGeometry, solutionMaterial);
            scenePrimal.add(solutionLine);

            rendererPrimal.render(scenePrimal, cameraPrimal);
        }

        function renderDual(alpha, beta, w, r, C_in, x_d, y_d, Q_out) {
            sceneDual.remove(...sceneDual.children.filter(child => child.type === 'Mesh' || child.type === 'Line'));

            const productionSurfaceGeometry = new THREE.ParametricGeometry((u, v) => {
                const x = u * 10;
                const y = v * 10;
                const z = Math.pow(x, alpha) * Math.pow(y, beta);
                return new THREE.Vector3(x, y, z);
            }, 50, 50);
            const productionSurfaceMaterial = new THREE.MeshBasicMaterial({ color: 0xffa500 });
            const productionSurfaceMesh = new THREE.Mesh(productionSurfaceGeometry, productionSurfaceMaterial);
            sceneDual.add(productionSurfaceMesh);

            const budgetCurtainGeometry = new THREE.BufferGeometry();
            const vertices = [];
            for (let x = 0; x <= 10; x += 0.1) {
                const y = (C_in - w * x) / r;
                if (y > 0 && y <= 10) {
                    vertices.push(x, y, 0);
                    vertices.push(x, y, Q_out);
                }
            }
            budgetCurtainGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            const budgetCurtainMaterial = new THREE.MeshBasicMaterial({ color: 0x808080, opacity: 0.5, transparent: true });
            const budgetCurtainMesh = new THREE.Mesh(budgetCurtainGeometry, budgetCurtainMaterial);
            sceneDual.add(budgetCurtainMesh);

            const intersectionCurveGeometry = new THREE.BufferGeometry();
            const points = [];
            for (let x = 0; x <= 10; x += 0.1) {
                const y = (C_in - w * x) / r;
                if (y > 0 && y <= 10) {
                    const z = Math.pow(x, alpha) * Math.pow(y, beta);
                    points.push(new THREE.Vector3(x, y, z));
                }
            }
            intersectionCurveGeometry.setFromPoints(points);
            const intersectionCurveMaterial = new THREE.LineBasicMaterial({ color: 0xff0000, linewidth: 2 });
            const intersectionCurveLine = new THREE.Line(intersectionCurveGeometry, intersectionCurveMaterial);
            sceneDual.add(intersectionCurveLine);

            const optimalPointGeometry = new THREE.SphereGeometry(0.1, 32, 32);
            const optimalPointMaterial = new THREE.MeshBasicMaterial({ color: 0x808080 });
            const optimalPointMesh = new THREE.Mesh(optimalPointGeometry, optimalPointMaterial);
            optimalPointMesh.position.set(x_d, y_d, Q_out);
            sceneDual.add(optimalPointMesh);

            rendererDual.render(sceneDual, cameraDual);
        }

        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-alpha').value = 0.5;
            document.getElementById('slider-beta').value = 0.5;
            document.getElementById('slider-w').value = 1.0;
            document.getElementById('slider-r').value = 1.0;
            document.getElementById('slider-Q').value = 3.0;
            document.getElementById('slider-C').value = 4.0;
            update();
        });

        document.getElementById('btn-Q-from-dual').addEventListener('click', () => {
            const Q_out = parseFloat(document.getElementById('output-dual').textContent.split('Q = ')[1]);
            document.getElementById('slider-Q').value = Q_out;
            update();
        });

        document.getElementById('btn-C-from-primal').addEventListener('click', () => {
            const C_out = parseFloat(document.getElementById('output-primal').textContent.split('C = ')[1]);
            document.getElementById('slider-C').value = C_out;
            update();
        });

        document.querySelectorAll('input[type="range"]').forEach(input => {
            input.addEventListener('input', update);
        });

        update();
    </script>
</body>
</html>
