<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Flash Distillation Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #f4f4f9;
        }
        #demo-container {
            width: 800px;
            border: 1px solid #ccc;
            border-radius: 8px;
            overflow: hidden;
        }
        #control-panel {
            background-color: #e0e0e0;
            padding: 10px;
            border-bottom: 1px solid #ccc;
        }
        .control-row {
            display: flex;
            align-items: center;
            margin-bottom: 10px;
        }
        .control-row label {
            margin-right: 10px;
        }
        .control-row span {
            margin-left: 10px;
        }
        #visualization-area {
            position: relative;
            width: 100%;
            height: 500px;
        }
    </style>
</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">

        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <!-- Flash Pressure Slider -->
            <div class="control-row">
                <label for="slider-pressure">flash pressure (kPa)</label>
                <input type="range" id="slider-pressure" min="100" max="1000" step="1" value="180">
                <span id="pressure-value">180</span>
            </div>
            <!-- Heat Load Slider -->
            <div class="control-row">
                <label for="slider-heat">heat load (kJ/hr)</label>
                <input type="range" id="slider-heat" min="0" max="10000" step="100" value="0">
                <span id="heat-value">0</span>
            </div>
        </div>

        <!-- Section 2: Visualization Area -->
        <div id="visualization-area">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>

    </div>

    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let canvas;
        let flashPressure = 180;
        let heatLoad = 0;
        let results = {};

        const components = [
            { name: 'n-butane', A: 6.80165, B: 1005.157, C: -27.159, Cp_L: 132, ΔH_vap: 22400 },
            { name: 'n-pentane', A: 6.86177, B: 1184.249, C: -35.223, Cp_L: 161, ΔH_vap: 25800 },
            { name: 'n-heptane', A: 6.90237, B: 1358.59, C: -51.303, Cp_L: 225, ΔH_vap: 31800 },
            { name: 'n-octane', A: 6.92383, B: 1431.149, C: -59.501, Cp_L: 255, ΔH_vap: 34400 }
        ];

        const feedFlow = 10; // kmol/hr
        const feedTemperature = 323.2; // K
        const feedPressure = 1000; // kPa
        const feedMoleFractions = [0.25, 0.25, 0.25, 0.25];

        function setup() {
            canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');
            textSize(12);
            textAlign(CENTER, CENTER);
            calculateAndDraw();
        }

        function draw() {
            background('#F0F0F0');
            drawFlashVessel();
            drawStreams();
            drawTextLabels();
        }

        function drawFlashVessel() {
            fill('#E6E6FA');
            stroke(0);
            rect(350, 200, 100, 100);
            fill(0);
            text("Flash", 400, 250);
        }

        function drawStreams() {
            // Feed Stream
            stroke('#9ACD32');
            line(100, 250, 350, 250);
            drawArrow(350, 250);

            // Vapor Stream
            stroke('#9370DB');
            line(400, 200, 400, 100);
            line(400, 100, 550, 100);
            drawArrow(550, 100);

            // Liquid Stream
            stroke('#FFA500');
            line(400, 300, 400, 400);
            line(400, 400, 550, 400);
            drawArrow(550, 400);

            // Heat Input
            stroke('#FF0000');
            strokeWeight(2);
            line(200, 250, 350, 250);
            drawArrow(350, 250, 10);
            strokeWeight(1);
        }

        function drawArrow(x, y, length = 10) {
            push();
            translate(x, y);
            rotate(PI / 6);
            line(0, 0, length, 0);
            rotate(-PI / 3);
            line(0, 0, length, 0);
            pop();
        }

        function drawTextLabels() {
            // Feed Info
            fill(0);
            text(`Feed Flow: ${feedFlow} kmol/hr`, 100, 100);
            text(`Feed Temp: ${feedTemperature} K`, 100, 120);
            text(`Feed Press: ${feedPressure} kPa`, 100, 140);

            // Vapor Info
            text("Vapor Mole Fractions:", 600, 100);
            for (let i = 0; i < components.length; i++) {
                text(`${components[i].name}: ${results.y ? results.y[i].toFixed(2) : '0.00'}`, 600, 120 + i * 20);
            }

            // Liquid Info
            text("Liquid Mole Fractions:", 600, 250);
            for (let i = 0; i < components.length; i++) {
                text(`${components[i].name}: ${results.x ? results.x[i].toFixed(2) : '0.00'}`, 600, 270 + i * 20);
            }

            // Process Info
            text(`Vapor Fraction: ${results.V_frac ? results.V_frac.toFixed(2) : '0.00'}`, 600, 400);
            text(`Flash Temperature: ${results.T_flash ? results.T_flash.toFixed(2) : '0.00'} K`, 600, 420);
            text(`Flash Pressure: ${flashPressure} kPa`, 600, 440);

            // Heat Info
            text(`Q = ${heatLoad} kJ/hr`, 200, 250);
        }

        function calculateFlash(P_flash, Q_hr) {
            const tolerance = 1e-5;
            let T_min = 273.15; // K
            let T_max = 500; // K
            let T_guess = (T_min + T_max) / 2;
            let V_guess = 0.5;

            while (true) {
                let T = T_guess;
                let V = V_guess;
                let K = components.map(comp => Math.pow(10, comp.A - comp.B / (T + comp.C)) / P_flash);
                let rachfordRiceSum = K.reduce((sum, K_i, i) => sum + feedMoleFractions[i] * (K_i - 1) / (1 + V * (K_i - 1)), 0);

                if (Math.abs(rachfordRiceSum) < tolerance) {
                    break;
                }

                if (rachfordRiceSum > 0) {
                    T_max = T;
                } else {
                    T_min = T;
                }
                T_guess = (T_min + T_max) / 2;
            }

            let Cp_sum = components.reduce((sum, comp, i) => sum + feedMoleFractions[i] * comp.Cp_L, 0);
            let H_sum = components.reduce((sum, comp, i) => sum + feedMoleFractions[i] * comp.ΔH_vap, 0);
            let energyBalance = Q_hr / feedFlow + Cp_sum * (feedTemperature - T_guess) - V_guess * H_sum;

            return {
                T_flash: T_guess,
                V_frac: V_guess,
                x: components.map((comp, i) => feedMoleFractions[i] / (1 + V_guess * (K[i] - 1))),
                y: components.map((comp, i) => K[i] * feedMoleFractions[i] / (1 + V_guess * (K[i] - 1)))
            };
        }

        function calculateAndDraw() {
            results = calculateFlash(flashPressure, heatLoad);
            redraw();
        }

        document.getElementById('slider-pressure').addEventListener('input', function(event) {
            flashPressure = parseFloat(event.target.value);
            document.getElementById('pressure-value').textContent = flashPressure;
            calculateAndDraw();
        });

        document.getElementById('slider-heat').addEventListener('input', function(event) {
            heatLoad = parseFloat(event.target.value);
            document.getElementById('heat-value').textContent = heatLoad;
            calculateAndDraw();
        });
    </script>
</body>
</html>
