    <script src="https://cdn.jsdelivr.net/npm/three@0.140.0/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        let scene, camera, renderer;
        let earth, moon, sun, landSurface;
        let constellationsGroup;
        let isAnimating = { time: false, nodalAngle: false, sunEarthAngle: false, zodiac: false };

        function init() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('scene-canvas') });
            renderer.setSize(window.innerWidth - 300, window.innerHeight);

            // Initialize objects
            earth = createSphere(0.1, 0x0000ff, 0.1);
            moon = createSphere(0.05, 0x808080, 0.06);
            sun = createSphere(0.2, 0xffff00, 0.2);
            landSurface = createLandSurface();
            constellationsGroup = createConstellations();

            scene.add(earth, moon, sun, landSurface, constellationsGroup);

            const light = new THREE.DirectionalLight(0xffffff, 1);
            light.position.set(1, 1, 1);
            light.castShadow = true;
            scene.add(light);

            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            camera.position.z = 2;

            animate();
        }

        function createSphere(radius, color, atmosphereSize) {
            const geometry = new THREE.SphereGeometry(radius, 32, 32);
            const material = new THREE.MeshPhongMaterial({ color: color });
            const sphere = new THREE.Mesh(geometry, material);

            const atmosphereGeometry = new THREE.SphereGeometry(atmosphereSize, 32, 32);
            const atmosphereMaterial = new THREE.MeshBasicMaterial({ color: 0x00ffff, transparent: true, blending: THREE.AdditiveBlending });
            const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);

            return new THREE.Group().add(sphere, atmosphere);
        }

        function createLandSurface() {
            const geometry = new THREE.PlaneGeometry(1, 1);
            const material = new THREE.MeshStandardMaterial({
                map: createNoiseTexture(),
                bumpMap: createNoiseTexture(),
                bumpScale: 0.05
            });
            const land = new THREE.Mesh(geometry, material);
            land.rotation.x = -Math.PI / 2;
            return land;
        }

        function createNoiseTexture() {
            const canvas = document.createElement('canvas');
            canvas.width = 256;
            canvas.height = 256;
            const context = canvas.getContext('2d');
            const imageData = context.createImageData(canvas.width, canvas.height);
            const data = imageData.data;

            for (let i = 0; i < data.length; i += 4) {
                data[i] = data[i + 1] = data[i + 2] = Math.random() * 255;
                data[i + 3] = 255;
            }

            context.putImageData(imageData, 0, 0);
            return new THREE.CanvasTexture(canvas);
        }

        function createConstellations() {
            const group = new THREE.Group();
            // Add stars and lines for constellations here
            return group;
        }

        function animate() {
            requestAnimationFrame(animate);

            if (isAnimating.time) {
                updateSliderValue('slider-time', 0.01);
            }
            if (isAnimating.nodalAngle) {
                updateSliderValue('slider-nodal-angle', 0.01);
            }
            if (isAnimating.sunEarthAngle) {
                updateSliderValue('slider-sun-earth-angle', 0.01);
            }
            if (isAnimating.zodiac) {
