            updateDisplay(sliderId, value);
            updateScene();
        }

        function updateDisplay(sliderId, value) {
            const display = document.getElementById(`${sliderId}-value`);
            display.textContent = sliderId === 'slider-sun-earth-angle' ? value.toFixed(2) : value.toFixed(2);
