    <div id="controls-container">
        <div class="control-group">
            <label for="slider-residence-time">Atmospheric CO2 residence time (years)</label>
            <input type="range" id="slider-residence-time" min="50" max="500" step="1" value="200">
            <span id="value-residence-time">200</span>
        </div>
        <div class="control-group">
            <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
            <input type="range" id="slider-transition-pace" min="0" max="100" step="1" value="25">
            <span id="value-transition-pace">25</span>
        </div>
        <div class="control-group">
            <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
            <input type="range" id="slider-wedges" min="0" max="10" step="0.01" value="4">
            <span id="value-wedges">4.00</span>
        </div>
    </div>
    <div id="plot-div"></div>
    <script>
        const C0 = 850;
        const E0 = 8.5;
        const C_eq = 586;
        const E_floor = 1.5;
        const rise_rate = 0.02;
        const time_step = 0.5;
        const max_time = 300;

        function updateDisplayAndPlot() {
            const t_res = parseFloat(document.getElementById('slider-residence-time').value);
            const t_trans = parseFloat(document.getElementById('slider-transition-pace').value);
            const N_wedges = parseFloat(document.getElementById('slider-wedges').value);

            document.getElementById('value-residence-time').textContent = t_res;
            document.getElementById('value-transition-pace').textContent = t_trans;
            document.getElementById('value-wedges').textContent = N_wedges.toFixed(2);

            const E_peak = E0 + rise_rate * t_trans;
            const t_values = [];
            const E_values = [];
            const C_values = [];

            let C_t_minus_dt = C0;
            for (let t = 0; t <= max_time; t += time_step) {
                t_values.push(t);
                let E_t;
                if (t < t_trans) {
                    E_t = E0 + rise_rate * t;
                } else {
                    const reduction_rate = N_wedges / 50;
                    const E_linear = E_peak - reduction_rate * (t - t_trans);
                    E_t = Math.max(E_floor, E_linear);
                }
                E_values.push(100 * E_t);

                const dC = (E_t - (C_t_minus_dt - C_eq) / t_res) * time_step;
                C_t_minus_dt += dC;
                C_values.push(C_t_minus_dt);
            }

            const trace1 = {
                type: 'scatter',
                mode: 'lines',
                x: t_values,
                y: C_values,
                line: { color: 'blue', width: 2 }
            };

            const trace2 = {
                type: 'scatter',
                mode: 'lines',
                x: t_values,
                y: E_values,
                line: { color: 'purple', width: 2 }
            };

            const trace3 = {
                type: 'scatter',
                mode: 'lines',
                x: [0, 300],
                y: [1200, 1200],
                line: { color: 'black', width: 2, dash: 'dash' }
            };

            const data = [trace1, trace2, trace3];

            const layout = {
                title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                xaxis: { title: 'years', range: [0, 300], zeroline: true },
                yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true },
                showlegend: false,
                margin: { l: 60, r: 20, b: 40, t: 80 },
                paper_bgcolor: '#f9f9f9',
                plot_bgcolor: 'white',
                grid: { color: '#ddd' }
            };

            Plotly.newPlot('plot-div', data, layout);
        }

        document.getElementById('slider-residence-time').addEventListener('input', updateDisplayAndPlot);
        document.getElementById('slider-transition-pace').addEventListener('input', updateDisplayAndPlot);
        document.getElementById('slider-wedges').addEventListener('input', updateDisplayAndPlot);

        updateDisplayAndPlot();
    </script>
</body>
</html>
