<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Pendulum Simulation</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin: 0;
            padding: 0;
        }
        .main-container {
            width: 80%;
            max-width: 800px;
            margin-top: 20px;
        }
        .control-panel {
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .control-panel div {
            display: flex;
            align-items: center;
            justify-content: space-between;
        }
        .control-panel label {
            margin-right: 10px;
        }
        .control-panel input[type="range"] {
            width: 70%;
        }
        .visualization-section {
            margin-top: 20px;
            width: 100%;
            height: 500px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <div>
                <label for="slider-theta0">θ<sub>0</sub></label>
                <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" step="0.01" value="0.5236">
                <span id="value-theta0">π/6</span>
            </div>
            <div>
                <label for="slider-v0">v<sub>0</sub></label>
                <input type="range" id="slider-v0" min="-5" max="5" step="0.1" value="0">
                <span id="value-v0">0</span>
            </div>
            <div>
                <label for="slider-length">connector length</label>
                <input type="range" id="slider-length" min="0.5" max="4" step="0.1" value="2">
                <span id="value-length">2</span>
            </div>
            <div>
                <label for="btn-play-pause">animate</label>
                <div>
                    <button id="btn-play-pause">▶</button>
                    <button id="btn-reset">|<</button>
                </div>
            </div>
        </div>
        <div class="visualization-section" id="visualization"></div>
    </div>

    <script>
        let isPlaying = false;
        let time = 0;
        let x = 0;
        let theta = 0;
        let x_dot = 0;
        let theta_dot = 0;
        const l = 2; // Default length
        const g = 9.8;
        const m = 1; // Mass of the bob
        const M = 1; // Mass of the support
        const dt = 0.02;
        const scale = 50;

        function setup() {
            createCanvas(500, 500).parent('visualization');
            resetSimulation();
        }

        function draw() {
            if (isPlaying) {
                updatePhysics();
            }
            clear();
            background('#F0F0F0');
            drawPendulum();
        }

        function updatePhysics() {
            const theta_ddot = (-m * l * sin(theta) * cos(theta) * theta_dot ** 2 - g * (M + m) * sin(theta)) / (l * (M + m * sin(theta) ** 2));
            theta_dot += theta_ddot * dt;
            theta += theta_dot * dt;
            x_dot = slider_v0.value() - (m * l * cos(theta) * theta_dot) / (M + m);
            x += x_dot * dt;
        }

        function drawPendulum() {
            const px = width / 2 + x * scale;
            const py = height / 2;
            const bx = px + (l * scale) * sin(theta);
            const by = py - (l * scale) * cos(theta);

            fill('red');
            noStroke();
            rectMode(CENTER);
            rect(px, py, 20, 20);

            stroke('black');
            strokeWeight(2);
            line(px, py, bx, by);

            fill('orange');
            stroke('black');
            ellipse(bx, by, 20, 20);
        }

        function resetSimulation() {
            isPlaying = false;
            btn_play_pause.textContent = '▶';
            time = 0;
            x = 0;
            theta = parseFloat(slider_theta0.value());
            x_dot = slider_v0.value();
            theta_dot = 0;
            updateDisplay();
            drawPendulum();
        }

        function updateDisplay() {
            const thetaValue = parseFloat(slider_theta0.value());
            const formattedTheta = formatTheta(thetaValue);
            value_theta0.textContent = formattedTheta;
            value_v0.textContent = slider_v0.value();
            value_length.textContent = slider_length.value();
        }

        function formatTheta(value) {
            const pi = Math.PI;
            const fractions = [
                { value: pi / 2, label: "π/2" },
                { value: pi / 3, label: "π/3" },
                { value: pi / 6, label: "π/6" },
                { value: -pi / 2, label: "-π/2" }
            ];

            for (const frac of fractions) {
                if (Math.abs(value - frac.value) < 0.01) {
                    return frac.label;
                }
            }

            return value.toFixed(2);
        }

        const slider_theta0 = document.getElementById('slider-theta0');
        const slider_v0 = document.getElementById('slider-v0');
        const slider_length = document.getElementById('slider-length');
        const value_theta0 = document.getElementById('value-theta0');
        const value_v0 = document.getElementById('value-v0');
        const value_length = document.getElementById('value-length');
        const btn_play_pause = document.getElementById('btn-play-pause');
        const btn_reset = document.getElementById('btn-reset');

        slider_theta0.addEventListener('input', () => {
            resetSimulation();
            updateDisplay();
        });

        slider_v0.addEventListener('input', () => {
            resetSimulation();
            updateDisplay();
        });

        slider_length.addEventListener('input', () => {
            resetSimulation();
            updateDisplay();
        });

        btn_play_pause.addEventListener('click', () => {
            isPlaying = !isPlaying;
            btn_play_pause.textContent = isPlaying ? '||' : '▶';
        });

        btn_reset.addEventListener('click', resetSimulation);
    </script>
</body>
</html>
