            width: 600px;
            height: 400px;
            border: 1px solid #ccc;
        }
        input[type="range"] {
            width: 100%;
        }
        input[type="text"] {
            width: 50px;
            text-align: right;
            background-color: #f0f0f0;
            border: none;
            padding: 5px;
        }
    </style>
</head>
<body>
    <h1>Deformation Pattern in an Earthquake Source Region</h1>
    <div id="control-panel">
        <div class="slider-group">
            <label for="slider-strike">strike angle φ of fault plane clockwise from North (0° ≤ φ < 360°)</label>
            <input type="range" id="slider-strike" min="0" max="360" step="1" value="180">
            <input type="text" id="value-strike" value="180" readonly>
        </div>
        <div class="slider-group">
            <label for="slider-dip">dip angle δ of fault plane down from horizontal (0° ≤ δ ≤ 90°)</label>
            <input type="range" id="slider-dip" min="0" max="90" step="1" value="60">
            <input type="text" id="value-dip" value="60" readonly>
        </div>
        <div class="slider-group">
            <label for="slider-slip-angle">slip angle λ in fault plane between slip direction and strike (−180° ≤ λ < 180°)</label>
            <input type="range" id="slider-slip-angle" min="-180" max="180" step="1" value="90">
            <input type="text" id="value-slip-angle" value="90" readonly>
        </div>
        <div class="slider-group">
            <label for="slider-slip-amount">amount of slip of hanging wall (0 to 1)</label>
            <input type="range" id="slider-slip-amount" min="0" max="1" step="0.01" value="0.5">
            <input type="text" id="value-slip-amount" value="0.5" readonly>
        </div>
        <button id="btn-reset">↺</button>
    </div>
    <div id="visualization-container">
        <div>
            <input type="checkbox" id="checkbox-box">
            <label for="checkbox-box">box</label>
        </div>
        <div id="canvas-container">
            <canvas id="webgl-canvas"></canvas>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        const canvas = document.getElementById('webgl-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas });
        renderer.setSize(canvas.clientWidth, canvas.clientHeight);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
        camera.position.z = 5;

        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        let sphereGeometry = new THREE.SphereGeometry(1, 64, 32);
        let sphereMaterial = new THREE.MeshStandardMaterial({ vertexColors: THREE.VertexColors });
        let sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphereMesh);

        function updateSphere() {
            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const slipAngle = parseFloat(document.getElementById('slider-slip-angle').value);
            const slipAmount = parseFloat(document.getElementById('slider-slip-amount').value);

            const n = new THREE.Vector3(Math.sin(strike * Math.PI / 180), 0, Math.cos(strike * Math.PI / 180));
            const u = new THREE.Vector3(Math.sin((strike + slipAngle) * Math.PI / 180), Math.sin(dip * Math.PI / 180), Math.cos((strike + slipAngle) * Math.PI / 180)).normalize();
            const P = new THREE.Vector3().addVectors(n, u).normalize();
            const T = new THREE.Vector3().subVectors(u, n).normalize();

            sphereGeometry.vertices.forEach(vertex => {
                const dotP = vertex.dot(P);
                const dotT = vertex.dot(T);
                const displacement = (dotT * dotT - dotP * dotP) * slipAmount;
                vertex.addScaledVector(vertex, displacement);
            });

            sphereGeometry.verticesNeedUpdate = true;

            sphereGeometry.faces.forEach(face => {
                const centroid = new THREE.Vector3();
                centroid.add(face.a).add(face.b).add(face.c).divideScalar(3);
                const dotP = centroid.dot(P);
                const dotT = centroid.dot(T);
                face.color.set(dotP > 0 ? 0xFFFFFF : 0xFFA500);
            });

            sphereGeometry.colorsNeedUpdate = true;
        }

        document.getElementById('slider-strike').addEventListener('input', updateSphere);
        document.getElementById('slider-dip').addEventListener('input', updateSphere);
        document.getElementById('slider-slip-angle').addEventListener('input', updateSphere);
        document.getElementById('slider-slip-amount').addEventListener('input', updateSphere);

        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-strike').value = 180;
            document.getElementById('slider-dip').value = 60;
            document.getElementById('slider-slip-angle').value = 90;
            document.getElementById('slider-slip-amount').value = 0.5;
            updateSphere();
        });

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
</html>
