<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Grover's Algorithm Visualization</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #f0f0f0;
            margin: 0;
        }
        #app-container {
            border: 2px solid #ccc;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            padding: 20px;
            width: 80%;
            max-width: 1200px;
            background-color: white;
        }
        #control-panel {
            display: flex;
            justify-content: space-between;
            margin-bottom: 20px;
        }
        .control-group {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        .value-display {
            margin-top: 5px;
            font-weight: bold;
        }
        #canvas-container {
            position: relative;
            width: 100%;
            height: 400px;
        }
        #probability-display {
            text-align: center;
            font-size: 1.2em;
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database size</label>
                <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
                <div class="value-display">+<span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
                <div class="value-display">+<span id="display-iterations">2</span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display">99.9% success probability</div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let dbSize = 10;
        let iterations = 2;
        let targetIndex = 6;
        let amplitudes = [];
        let canvas;

        function setup() {
            canvas = createCanvas(windowWidth * 0.8, windowHeight * 0.4);
            canvas.parent('canvas-container');
            updateAmplitudes();
            calculateSuccessProbability();
        }

        function draw() {
            clear();
            let sectionHeight = height / 4;
            let barWidth = width / (dbSize + 2);

            // Stage 1: Original Amplitudes
            drawSection("Original Amplitudes", 0, sectionHeight, amplitudes.slice());

            // Stage 2: After First Inversion
            let invertedAmplitudes = amplitudes.slice();
            invertedAmplitudes[targetIndex] *= -1;
            drawSection("After First Inversion", sectionHeight, sectionHeight * 2, invertedAmplitudes);

            // Stage 3: After First Diffusion
            let diffusedAmplitudes = applyDiffusion(invertedAmplitudes);
            drawSection("After First Diffusion", sectionHeight * 2, sectionHeight * 3, diffusedAmplitudes);

            // Stage 4: After k Iterations
            let finalAmplitudes = amplitudes.slice();
            for (let i = 0; i < iterations; i++) {
                finalAmplitudes = applyGroverOperator(finalAmplitudes);
            }
            drawSection("After k Iterations", sectionHeight * 3, sectionHeight * 4, finalAmplitudes);
        }

        function updateAmplitudes() {
            amplitudes = Array(dbSize).fill(1 / Math.sqrt(dbSize));
        }

        function applyGroverOperator(amplitudes) {
            let invertedAmplitudes = amplitudes.slice();
            invertedAmplitudes[targetIndex] *= -1;
            return applyDiffusion(invertedAmplitudes);
        }

        function applyDiffusion(amplitudes) {
            let mean = amplitudes.reduce((sum, a) => sum + a, 0) / amplitudes.length;
            return amplitudes.map(a => 2 * mean - a);
        }

        function drawSection(title, yStart, yEnd, amplitudes) {
            fill(0);
            textSize(16);
            textAlign(CENTER, TOP);
            text(title, width / 2, yStart + 5);

            stroke(0);
            line(0, yStart + sectionHeight / 2, width, yStart + sectionHeight / 2);

            noStroke();
            for (let i = 0; i < amplitudes.length; i++) {
                let x = barWidth * (i + 1);
                let barHeight = amplitudes[i] * 40;
                if (i === targetIndex) {
                    fill('#FFA500');
                } else {
                    fill('#808080');
                }
                rect(x, yStart + sectionHeight / 2 - barHeight / 2, barWidth, barHeight);
            }

            // Draw parentheses for original amplitudes
            if (yStart === 0) {
                fill(0);
                arc(barWidth, yStart + sectionHeight / 2, 20, 20, 0, PI);
                arc(width - barWidth, yStart + sectionHeight / 2, 20, 20, PI, 0);
            }
        }

        function calculateSuccessProbability() {
            let probability = Math.pow(Math.sin((2 * iterations + 1) * Math.asin(1 / Math.sqrt(dbSize))), 2);
            document.getElementById('probability-display').innerText = (probability * 100).toFixed(1) + '% success probability';
        }

        document.getElementById('slider-db-size').addEventListener('input', function(event) {
            dbSize = parseInt(event.target.value);
            document.getElementById('display-db-size').innerText = dbSize;
            updateAmplitudes();
            calculateSuccessProbability();
            redraw();
        });

        document.getElementById('slider-iterations').addEventListener('input', function(event) {
            iterations = parseInt(event.target.value);
            document.getElementById('display-iterations').innerText = iterations;
            calculateSuccessProbability();
            redraw();
        });
    </script>
</body>
</html>
