            flex-grow: 1;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <!-- Main container -->
    <div id="main-container">
        <!-- Left column: Control Panel -->
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-volatility">relative volatility</label>
                <input type="range" id="slider-volatility" min="1.1" max="10" step="0.1" value="2.5">
                <button id="btn-volatility-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-distillate">distillate specification</label>
                <input type="range" id="slider-distillate" min="0.51" max="0.99" step="0.01" value="0.95">
                <button id="btn-distillate-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-bottom">bottom specification</label>
                <input type="range" id="slider-bottom" min="0.01" max="0.49" step="0.01" value="0.05">
                <button id="btn-bottom-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-comp">feed composition</label>
                <input type="range" id="slider-feed-comp" min="0.02" max="0.98" step="0.01" value="0.50">
                <button id="btn-feed-comp-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-quality">feed quality</label>
                <input type="range" id="slider-feed-quality" min="0.0" max="2.0" step="0.05" value="1.0">
                <button id="btn-feed-quality-plus">+</button>
            </div>
        </div>

        <!-- Right column: Visualization Area -->
        <div id="visualization-area">
            <p id="stages-count-display">The number of stages is 8.</p>
            <div id="canvas-container"></div> <!-- p5.js canvas will be attached here -->
        </div>
    </div>

    <script>
        let canvas;
        const margin = 20;
        const width = 600;
        const height = 600;

        function setup() {
            canvas = createCanvas(width, height);
            canvas.parent('canvas-container');
            noLoop();
            drawDiagram();
        }

        function drawDiagram() {
            clear();
            const alpha = parseFloat(document.getElementById('slider-volatility').value);
            const x_D = parseFloat(document.getElementById('slider-distillate').value);
            const x_B = parseFloat(document.getElementById('slider-bottom').value);
            const x_F = parseFloat(document.getElementById('slider-feed-comp').value);
            const q = parseFloat(document.getElementById('slider-feed-quality').value);

            // Draw axes
            stroke('#808080');
            strokeWeight(1);
            line(margin, height - margin, width - margin, height - margin); // X-axis
            line(margin, margin, margin, height - margin); // Y-axis

            // Draw equilibrium curve
            stroke('#FF0000');
            strokeWeight(2);
            beginShape();
            for (let x = 0; x <= 1; x += 0.01) {
                let y = (alpha * x) / (1 + (alpha - 1) * x);
                vertex(map(x, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin));
            }
            endShape();

            // Draw diagonal line
            stroke('#228B22');
            strokeWeight(2);
            line(margin, margin, width - margin, height - margin);

            // Calculate intersection point
            let x_int, y_int;
            if (q === 1) {
                x_int = x_F;
                y_int = x_F;
            } else if (q === 0) {
                x_int = x_F;
                y_int = x_F;
            } else {
                x_int = (x_F * (q - 1)) / (q - x_F);
                y_int = (alpha * x_int) / (1 + (alpha - 1) * x_int);
            }

            // Draw operating and feed lines
            stroke('#00FFFF'); // Rectifying line
            line(map(x_D, 0, 1, margin, width - margin), map(x_D, 0, 1, height - margin, margin), map(x_int, 0, 1, margin, width - margin), map(y_int, 0, 1, height - margin, margin));

            stroke('#FF00FF'); // Stripping line
            line(map(x_B, 0, 1, margin, width - margin), map(x_B, 0, 1, height - margin, margin), map(x_int, 0, 1, margin, width - margin), map(y_int, 0, 1, height - margin, margin));

            stroke('#0000CD'); // Feed line
            line(map(x_F, 0, 1, margin, width - margin), map(x_F, 0, 1, height - margin, margin), map(x_int, 0, 1, margin, width - margin), map(y_int, 0, 1, height - margin, margin));

            // Calculate and draw stages
            let stage_count = 0;
            let x = x_D;
            let y = x_D;

            while (x > x_B) {
                stroke('#000000');
                strokeWeight(1);
                let x_new = (y * (1 + (alpha - 1) * x)) / alpha;
                line(map(x, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin), map(x_new, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin));

                x = x_new;
                stage_count++;

                if (x <= x_B) break;

                if (x > x_int) {
                    y = map(x_new, 0, 1, x_new, x_D);
                } else {
                    y = map(x_new, 0, 1, x_new, x_B);
                }

                line(map(x_new, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin), map(x_new, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin));
            }

            document.getElementById('stages-count-display').textContent = `The number of stages is ${stage_count}.`;
        }

        function constrainFeedComp() {
            const sliderBottom = document.getElementById('slider-bottom');
            const sliderFeedComp = document.getElementById('slider-feed-comp');
            const sliderDistillate = document.getElementById('slider-distillate');

            if (parseFloat(sliderFeedComp.value) <= parseFloat(sliderBottom.value)) {
                sliderFeedComp.value = parseFloat(sliderBottom.value) + 0.01;
            }

            if (parseFloat(sliderFeedComp.value) >= parseFloat(sliderDistillate.value)) {
                sliderFeedComp.value = parseFloat(sliderDistillate.value) - 0.01;
            }

            sliderFeedComp.min = parseFloat(sliderBottom.value) + 0.01;
            sliderFeedComp.max = parseFloat(sliderDistillate.value) - 0.01;
        }

        document.querySelectorAll('input[type="range"]').forEach(input => {
            input.addEventListener('input', () => {
                constrainFeedComp();
                drawDiagram();
            });
        });

        document.querySelectorAll('button[id*="btn"]').forEach(button => {
            button.addEventListener('click', () => {
                const sliderId = button.id.replace('btn-', 'slider-').replace('-plus', '');
                const slider = document.getElementById(sliderId);
                slider.value = parseFloat(slider.value) + parseFloat(slider.step);
                constrainFeedComp();
                drawDiagram();
            });
        });
    </script>
</body>
</html>
