<body>
    <!-- Main container for the demo -->
    <div id="demo-container">

        <!-- Section for user controls -->
        <div id="control-panel">
            <label for="slider-month">month</label>
            <input type="range" id="slider-month" min="0" max="11" step="1" value="(new Date()).getMonth()">

            <label for="perspective-buttons">perspective</label>
            <div id="perspective-buttons">
                <button id="btn-2d" class="active">2D</button>
                <button id="btn-3d">3D</button>
            </div>
        </div>

        <!-- Section for the visualization canvas -->
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>

    </div>

    <!-- CDN Includes -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        let currentMonth = (new Date()).getMonth();
        let is2DView = true;
        let p5Canvas;
        let threeScene, threeCamera, threeRenderer, threeControls;
        let earthOrbit;

        function setup2D() {
            if (threeScene) {
                threeRenderer.dispose();
                threeScene = null;
                threeCamera = null;
                threeRenderer = null;
                threeControls = null;
            }

            p5Canvas = createCanvas(window.innerWidth, window.innerHeight);
            p5Canvas.parent('visualization-area');
            draw2D();
        }

        function draw2D() {
            background(0);
            const tilt = 23.5 * cos(TWO_PI * currentMonth / 12);

            // Draw Earth
            push();
            translate(width / 2, height / 2);
            rotate(tilt * PI / 180);
            fill(65, 105, 225);
            noStroke();
            ellipse(0, 0, 100, 100);
            pop();

            // Draw other components
            // Solar Wind, Bow Shock, Magnetopause, Magnetic Field Lines, Van Allen Belts, Polar Cusps, Magnetotail
        }

        function setup3D() {
            if (p5Canvas) {
                p5Canvas.remove();
                p5Canvas = null;
            }

            threeScene = new THREE.Scene();
            threeCamera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            threeRenderer = new THREE.WebGLRenderer({ antialias: true });
            threeRenderer.setSize(window.innerWidth, window.innerHeight);
            threeRenderer.setClearColor(0x000000);
            document.getElementById('visualization-area').appendChild(threeRenderer.domElement);

            threeControls = new THREE.OrbitControls(threeCamera, threeRenderer.domElement);

            // Add ambient and point lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);

            const pointLight = new THREE.PointLight(0xffffff, 1, 100);
            pointLight.position.set(0, 0, 0);
            threeScene.add(pointLight);

            // Add Sun
            const sunGeometry = new THREE.SphereGeometry(5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            threeScene.add(sun);

            // Add Earth's Orbit
            const orbitGeometry = new THREE.EllipseCurve(
                0, 0, // ax, aY
                20, 20, // xRadius, yRadius
                0, 2 * Math.PI, // aStartAngle, aEndAngle
                false, // aClockwise
                0 // aRotation
            ).createPointsGeometry(50);
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xffffff });
            earthOrbit = new THREE.Line(orbitGeometry, orbitMaterial);
            threeScene.add(earthOrbit);

            // Add Earth
            const earthGeometry = new THREE.SphereGeometry(3, 32, 32);
            const earthMaterial = new THREE.MeshStandardMaterial({ color: 0x4169e1 });
            const earth = new THREE.Mesh(earthGeometry, earthMaterial);
            threeScene.add(earth);

            // Add Earth's Axis
            const axisHelper = new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), earth.position, 5, 0x00ff00);
            earth.add(axisHelper);

            // Add Solar Wind
            const windDirection = new THREE.Vector3(1, 0, 0);
            const windArrow = new THREE.ArrowHelper(windDirection, sun.position, 10, 0xff0000);
            threeScene.add(windArrow);

            // Add Magnetosphere
            const magnetosphereGeometry = new THREE.SphereGeometry(5, 32, 32);
            const magnetosphereMaterial = new THREE.MeshStandardMaterial({ color: 0x0000ff, transparent: true, opacity: 0.4 });
            const magnetosphere = new THREE.Mesh(magnetosphereGeometry, magnetosphereMaterial);
            earth.add(magnetosphere);

            threeCamera.position.z = 30;
            animate3D();
        }

        function animate3D() {
            requestAnimationFrame(animate3D);
            threeControls.update();

            // Update Earth's position and rotation
            const theta = TWO_PI * currentMonth / 12;
            const x = 20 * cos(theta);
            const z = 20 * sin(theta);
            earthOrbit.geometry.attributes.position.array[0] = x;
            earthOrbit.geometry.attributes.position.array[2] = z;
            earthOrbit.geometry.attributes.position.needsUpdate = true;

            earth.position.set(x, 0, z);
            earth.rotation.y += 0.01;

            threeRenderer.render(threeScene, threeCamera);
        }

        document.getElementById('btn-2d').addEventListener('click', () => {
            if (!is2DView) {
                is2DView = true;
                document.getElementById('btn-2d').classList.add('active');
                document.getElementById('btn-3d').classList.remove('active');
                setup2D();
            }
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            if (is2DView) {
                is2DView = false;
                document.getElementById('btn-2d').classList.remove('active');
                document.getElementById('btn-3d').classList.add('active');
                setup3D();
            }
        });

        document.getElementById('slider-month').addEventListener('input', (event) => {
            currentMonth = parseInt(event.target.value);
            if (is2DView) {
                draw2D();
            } else {
                // Update 3D scene
            }
        });

        // Initial setup
        setup2D();
    </script>
</body>
</html>
