</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <div>
                <label for="slider-alpha">parameter α</label>
                <input type="range" id="slider-alpha" value="1.825" min="0.1" max="5.0" step="0.005">
                <span id="display-alpha">1.825</span>
            </div>
            <div>
                <label for="slider-beta">parameter β</label>
                <input type="range" id="slider-beta" value="1.945" min="0.1" max="5.0" step="0.005">
                <span id="display-beta">1.945</span>
            </div>
        </div>
        <div class="visualization-section">
            <h2><span>density function</span> and <span class="orange">distribution function</span></h2>
            <div id="plot-container" style="height: 400px;"></div>
        </div>
        <div class="statistics-display">
            <p>Mean: <span id="mean-value">3.550</span></p>
            <p>Variance: <span id="variance-value">6.904</span></p>
            <p>Moment Generating Function: <span id="mgf-formula">$$ (1 - \beta t)^{-\alpha} = \frac{1}{(1 - \beta t)^{\alpha}} $$</span></p>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        // Helper function for the Gamma function using the Lanczos approximation
        function gamma(z) {
            const p = [
                0.99999999999980993,
                676.5203681218851,
                -1259.1392167224028,
                771.32342877765313,
                -176.61502916214059,
                12.507311721951292,
                -0.13857109526572012,
                9.984369578019574e-6,
                1.5046315309897387e-7
            ];
            const g = 7;
            if (z < 0.5) return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
            z -= 1;
            let a = p[0];
            for (let i = 1; i < p.length; i++) {
                a += p[i] / (z + i);
            }
            return Math.sqrt(2 * Math.PI) * Math.pow(z + g + 0.5, z + 0.5) * Math.exp(-z - g - 0.5) * a;
        }

        // Gamma PDF function
        function gammaPDF(x, alpha, beta) {
            return (Math.pow(x, alpha - 1) * Math.exp(-x / beta)) / (Math.pow(beta, alpha) * gamma(alpha));
        }

        // Gamma CDF function using numerical integration
        function gammaCDF(x, alpha, beta) {
            const step = 0.01;
            let sum = 0;
            for (let i = 0; i <= x / step; i++) {
                sum += gammaPDF(i * step, alpha, beta) * step;
            }
            return sum;
        }

        // Initial plot setup
        function initPlot(alpha, beta) {
            const xValues = Array.from({ length: 250 }, (_, i) => i * 0.1);
            const pdfValues = xValues.map(x => gammaPDF(x, alpha, beta));
            const cdfValues = xValues.map(x => gammaCDF(x, alpha, beta));

            const mean = alpha * beta;
            const yMean = gammaPDF(mean, alpha, beta);

            const trace1 = {
                type: 'scatter',
                mode: 'lines',
                x: xValues,
                y: pdfValues,
                line: { color: 'blue', width: 2.5 }
            };

            const trace2 = {
                type: 'scatter',
                mode: 'lines',
                x: xValues,
                y: cdfValues,
                line: { color: 'orange', width: 2.5 }
            };

            const shape = {
                type: 'line',
                x0: mean,
                x1: mean,
                y0: 0,
                y1: yMean,
                line: { color: 'red', width: 3 }
            };

            const layout = {
                xaxis: { range: [0, 25], title: '' },
                yaxis: { range: [0, 1.1], title: '' },
                showlegend: false,
                margin: { l: 50, r: 50, t: 50, b: 50 }
            };

            Plotly.newPlot('plot-container', [trace1, trace2], layout, { shapes: [shape] });
        }

        // Update plot and statistics
        function updatePlotAndStats(alpha, beta) {
            const xValues = Array.from({ length: 250 }, (_, i) => i * 0.1);
            const pdfValues = xValues.map(x => gammaPDF(x, alpha, beta));
            const cdfValues = xValues.map(x => gammaCDF(x, alpha, beta));

            const mean = alpha * beta;
            const yMean = gammaPDF(mean, alpha, beta);
            const variance = alpha * Math.pow(beta, 2);

            document.getElementById('display-alpha').textContent = alpha.toFixed(3);
            document.getElementById('display-beta').textContent = beta.toFixed(3);
            document.getElementById('mean-value').textContent = mean.toFixed(3);
            document.getElementById('variance-value').textContent = variance.toFixed(3);

            const mgfFormula = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${beta} t)^{${alpha}}} $$`;
            document.getElementById('mgf-formula').innerHTML = mgfFormula;
            MathJax.typeset();

            Plotly.restyle('plot-container', {
                x: [xValues, xValues],
                y: [pdfValues, cdfValues]
            });

            Plotly.relayout('plot-container', {
                shapes: [{
                    type: 'line',
                    x0: mean,
                    x1: mean,
                    y0: 0,
                    y1: yMean,
                    line: { color: 'red', width: 3 }
                }]
            });
        }

        // Event listeners for sliders
        document.getElementById('slider-alpha').addEventListener('input', () => {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            updatePlotAndStats(alpha, beta);
        });

        document.getElementById('slider-beta').addEventListener('input', () => {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            updatePlotAndStats(alpha, beta);
        });

        // Initialize plot and stats on page load
        initPlot(1.825, 1.945);
    </script>
</body>
</html>
