    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let canvas;
        let locator = { x: 0.2, y: 0.22 };
        let isDragging = false;
        const stepSize = 0.005;
        const numSteps = 250;
        const azeotropes = [
            { label: 'A1', position: { x: 0.655, y: 0 } },
            { label: 'A2', position: { x: 0, y: 0.795 } },
            { label: 'A3', position: { x: 0.605, y: 0.345 } },
            { label: 'A4', position: { x: 0.26, y: 0.335 } }
        ];

        function setup() {
            canvas = createCanvas(650, 650);
            canvas.parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);
            drawAxes();
            drawAzeotropes();
            drawDistillationLine();
            drawLocator();
        }

        function drawAxes() {
            stroke(0);
            line(60, 590, 590, 60); // Hypotenuse
            line(60, 590, 60, 60);   // Y-axis
            line(60, 590, 590, 590); // X-axis

            textSize(12);
            fill(255, 0, 0);
            text("methanol", 40, 580);
            text("chloroform", 580, 60);
            text("acetone", 40, 60);

            for (let i = 0; i <= 1; i += 0.2) {
                let x = map(i, 0, 1, 60, 590);
                let y = map(i, 0, 1, 60, 590);
                line(x, 590, x, 595);
                line(60, y, 55, y);
                text(i.toFixed(1), x + 5, 595);
                text(i.toFixed(1), 45, y + 5);
            }
        }

        function drawAzeotropes() {
            fill(153, 0, 0);
            stroke(0);
            azeotropes.forEach(azeotrope => {
                let x = map(azeotrope.position.x, 0, 1, 60, 590);
                let y = map(azeotrope.position.y, 0, 1, 590, 60);
                ellipse(x, y, 12, 12);
                text(azeotrope.label, x + 10, y + 10);
            });
        }

        function drawDistillationLine() {
            let points = [];
            let { x, y } = locator;
            let direction = 1;

            for (let i = 0; i < numSteps; i++) {
                let { dx, dy } = getVector(x, y);
                x += dx * direction * stepSize;
                y += dy * direction * stepSize;
                if (x <= 0 || y <= 0 || x + y >= 1) break;
                points.push({ x, y });
            }

            direction = -1;
            x = locator.x;
            y = locator.y;

            for (let i = 0; i < numSteps; i++) {
                let { dx, dy } = getVector(x, y);
                x += dx * direction * stepSize;
                y += dy * direction * stepSize;
                if (x <= 0 || y <= 0 || x + y >= 1) break;
                points.push({ x, y });
            }

            stroke(0, 0, 255);
            strokeWeight(2);
            beginShape();
            points.forEach(point => {
                let px = map(point.x, 0, 1, 60, 590);
                let py = map(point.y, 0, 1, 590, 60);
                curveVertex(px, py);
            });
            endShape();
        }

        function drawLocator() {
            let x = map(locator.x, 0, 1, 60, 590);
            let y = map(locator.y, 0, 1, 590, 60);
            fill(255);
            stroke(0);
            strokeWeight(2);
            ellipse(x, y, 16, 16);
        }

        function getVector(x, y) {
            const A3 = { x: 0.605, y: 0.345 }; // Unstable node (repeller)
            const M = { x: 0, y: 0 };         // Stable node (attractor)

            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        function mousePressed() {
            let x = map(mouseX, 60, 590, 0, 1);
            let y = map(mouseY, 590, 60, 0, 1);
            let dx = x - locator.x;
            let dy = y - locator.y;
            if (dx * dx + dy * dy < 15 * 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                let x = map(mouseX, 60, 590, 0, 1);
                let y = map(mouseY, 590, 60, 0, 1);
                locator = constrainToDistillationBoundary(x, y);
                redraw();
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function constrainToDistillationBoundary(x, y) {
            let closestPoint = { x: 0, y: 0 };
            let minDistance = Infinity;

            let segments = [
                [azeotropes[1].position, azeotropes[3].position],
                [azeotropes[3].position, azeotropes[0].position]
            ];

            segments.forEach(segment => {
                let { x1, y1 } = segment[0];
                let { x2, y2 } = segment[1];
                let t = ((x - x1) * (x2 - x1) + (y - y1) * (y2 - y1)) / ((x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1));
                t = constrain(t, 0, 1);
                let px = x1 + t * (x2 - x1);
                let py = y1 + t * (y2 - y1);
                let distance = dist(x, y, px, py);
                if (distance < minDistance) {
                    minDistance = distance;
                    closestPoint = { x: px, y: py };
                }
            });

            return closestPoint;
        }
    </script>
</body>
</html>
