    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        camera.position.set(5, 5, 5);
        camera.lookAt(0, 0, 0);

        function createAxes() {
            const materials = [
                new THREE.LineBasicMaterial({ color: 0x888888 }),
                new THREE.LineBasicMaterial({ color: 0x888888 }),
                new THREE.LineBasicMaterial({ color: 0x888888 })
            ];

            const pointsX = [];
            pointsX.push(new THREE.Vector3(-5, 0, 0));
            pointsX.push(new THREE.Vector3(5, 0, 0));

            const pointsY = [];
            pointsY.push(new THREE.Vector3(0, -5, 0));
            pointsY.push(new THREE.Vector3(0, 5, 0));

            const pointsZ = [];
            pointsZ.push(new THREE.Vector3(0, 0, -5));
            pointsZ.push(new THREE.Vector3(0, 0, 5));

            const lineX = new THREE.Line(new THREE.BufferGeometry().setFromPoints(pointsX), materials[0]);
            const lineY = new THREE.Line(new THREE.BufferGeometry().setFromPoints(pointsY), materials[1]);
            const lineZ = new THREE.Line(new THREE.BufferGeometry().setFromPoints(pointsZ), materials[2]);

            scene.add(lineX);
            scene.add(lineY);
            scene.add(lineZ);
        }

        createAxes();

        function createParametricGeometry(a, b, c) {
            const range = 2.0;
            const geometry = new THREE.ParametricGeometry((u, v, target) => {
                let x = a * v * range * Math.cos(u * 2 * Math.PI);
                let y = b * v * range * Math.sin(u * 2 * Math.PI);
                let z = c * (v * range) * (v * range);
                target.set(x, y, z);
            }, 50, 50);

            const maxZ = c * range * range;
            const colorArray = new Uint8Array(geometry.attributes.position.count * 3);

            for (let i = 0; i < geometry.attributes.position.count; i++) {
                const z = geometry.attributes.position.getZ(i);
                const normalizedZ = z / maxZ;
                const r = Math.floor(255 * (1 - normalizedZ));
                const g = Math.floor(255 * (1 - normalizedZ));
                const b = Math.floor(255 * normalizedZ);
                colorArray[i * 3] = r;
                colorArray[i * 3 + 1] = g;
                colorArray[i * 3 + 2] = b;
            }

            geometry.setAttribute('color', new THREE.BufferAttribute(colorArray, 3));

            const material = new THREE.MeshPhongMaterial({ side: THREE.DoubleSide, vertexColors: true });
            const wireframeMaterial = new THREE.MeshBasicMaterial({ wireframe: true, color: 0xcccccc });

            const mesh = new THREE.Mesh(geometry, material);
            const wireframe = new THREE.Mesh(geometry, wireframeMaterial);

            return [mesh, wireframe];
        }

        let [mesh, wireframe] = createParametricGeometry(1.5, 1.5, 1.0);
        scene.add(mesh);
        scene.add(wireframe);

        function updateParaboloid(a, b, c) {
            scene.remove(mesh);
            scene.remove(wireframe);
            [mesh, wireframe] = createParametricGeometry(a, b, c);
            scene.add(mesh);
            scene.add(wireframe);
        }

        document.getElementById('slider-a').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('input-a').value = value;
            updateParaboloid(value, parseFloat(document.getElementById('slider-b').value), parseFloat(document.getElementById('slider-c').value));
        });

        document.getElementById('input-a').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('slider-a').value = value;
            updateParaboloid(value, parseFloat(document.getElementById('slider-b').value), parseFloat(document.getElementById('slider-c').value));
        });

        document.getElementById('slider-b').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('input-b').value = value;
            updateParaboloid(parseFloat(document.getElementById('slider-a').value), value, parseFloat(document.getElementById('slider-c').value));
        });

        document.getElementById('input-b').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('slider-b').value = value;
            updateParaboloid(parseFloat(document.getElementById('slider-a').value), value, parseFloat(document.getElementById('slider-c').value));
        });

        document.getElementById('slider-c').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('input-c').value = value;
            updateParaboloid(parseFloat(document.getElementById('slider-a').value), parseFloat(document.getElementById('slider-b').value), value);
        });

        document.getElementById('input-c').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('slider-c').value = value;
            updateParaboloid(parseFloat(document.getElementById('slider-a').value), parseFloat(document.getElementById('slider-b').value), value);
        });

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
</body>
