<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="j1-selector">
                <span>j₁</span>
                <button onclick="selectJ(0.5)">1/2</button>
                <button onclick="selectJ(1.0)">1</button>
                <button onclick="selectJ(1.5)" class="selected">3/2</button>
                <button onclick="selectJ(2.0)">2</button>
            </div>
            <div id="m1-selector">
                <span>m₁</span>
                <!-- Buttons will be populated dynamically -->
            </div>
            <div id="j2-selector">
                <span>j₂</span>
                <button onclick="selectJ(0.5)">1/2</button>
                <button onclick="selectJ(1.0)" class="selected">1</button>
                <button onclick="selectJ(1.5)">3/2</button>
                <button onclick="selectJ(2.0)">2</button>
            </div>
            <div id="m2-selector">
                <span>m₂</span>
                <!-- Buttons will be populated dynamically -->
            </div>
            <div id="j-selector">
                <span>j</span>
                <!-- Buttons will be populated dynamically -->
            </div>
            <div>
                <span>show precession</span>
                <button id="btn-play-pause" onclick="toggleAnimation()">▶</button>
                <button id="btn-reset" onclick="resetAnimation()">|◀</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="main-canvas"></canvas>
        </div>
        <div id="legend">
            <span style="color: black;">J →</span>
            <span style="color: red;">J₁ →</span>
            <span style="color: blue;">J₂ →</span>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        let scene, camera, renderer, orbitControls;
        let j1 = 1.5, m1 = 0.5, j2 = 1.0, m2 = 0.0, j = 2.5;
        let isAnimating = false;
        let phi_z = 0, phi_j = 0;
        let arrowJ, arrowJ1, arrowJ2;
        let coneJ, coneJ1, coneJ2;

        function init() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('main-canvas').appendChild(renderer.domElement);

            orbitControls = new THREE.OrbitControls(camera, renderer.domElement);
            orbitControls.enableDamping = true;
            orbitControls.dampingFactor = 0.25;
            orbitControls.screenSpacePanning = false;
            orbitControls.minDistance = 1;
            orbitControls.maxDistance = 1000;
            orbitControls.maxPolarAngle = Math.PI / 2;

            camera.position.z = 5;

            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1).normalize();
            scene.add(directionalLight);

            updateSelectors();
            drawVectorsAndCones();
        }

        function selectJ(value, selectorId = null) {
            if (selectorId === 'j1-selector') {
                j1 = value;
                document.querySelector('#j1-selector .selected').classList.remove('selected');
                event.target.classList.add('selected');
                m1 = -j1;
                updateSelectors();
            } else if (selectorId === 'j2-selector') {
                j2 = value;
                document.querySelector('#j2-selector .selected').classList.remove('selected');
                event.target.classList.add('selected');
                m2 = -j2;
                updateSelectors();
            } else {
                j = value;
                document.querySelector('#j-selector .selected').classList.remove('selected');
                event.target.classList.add('selected');
            }
            resetAnimation();
            drawVectorsAndCones();
        }

        function updateSelectors() {
            const m1Selector = document.getElementById('m1-selector');
            m1Selector.innerHTML = '';
            for (let m = -j1; m <= j1; m += 1) {
                const button = document.createElement('button');
                button.textContent = m;
                button.onclick = () => { m1 = m; button.classList.add('selected'); drawVectorsAndCones(); };
                m1Selector.appendChild(button);
            }
            document.querySelector('#m1-selector button').classList.add('selected');

            const m2Selector = document.getElementById('m2-selector');
            m2Selector.innerHTML = '';
            for (let m = -j2; m <= j2; m += 1) {
                const button = document.createElement('button');
                button.textContent = m;
                button.onclick = () => { m2 = m; button.classList.add('selected'); drawVectorsAndCones(); };
                m2Selector.appendChild(button);
            }
            document.querySelector('#m2-selector button').classList.add('selected');

            const jSelector = document.getElementById('j-selector');
            jSelector.innerHTML = '';
            for (let J = Math.abs(j1 - j2); J <= j1 + j2; J += 1) {
                const button = document.createElement('button');
                button.textContent = J;
                button.onclick = () => selectJ(J, 'j-selector');
                jSelector.appendChild(button);
            }
            document.querySelector('#j-selector button').classList.add('selected');
        }

        function drawVectorsAndCones() {
            scene.clear();

            const L_J = Math.sqrt(j * (j + 1));
            const L_J1 = Math.sqrt(j1 * (j1 + 1));
            const L_J2 = Math.sqrt(j2 * (j2 + 1));

            const dirJ = new THREE.Vector3(0, 0, 1).applyAxisAngle(new THREE.Vector3(1, 0, 0), Math.acos(m1 / L_J1));
            const dirJ1 = new THREE.Vector3(1, 0, 0);
            const dirJ2 = new THREE.Vector3(-1, 0, 0);

            if (j >= j1 && j >= j2) {
                const dirJ = new THREE.Vector3(0, 0, 1).applyAxisAngle(new THREE.Vector3(1, 0, 0), Math.acos((m1 + m2) / L_J));
                const dirJ1Local = new THREE.Vector3(1, 0, 0);
                const dirJ2Local = new THREE.Vector3(-1, 0, 0);
                const q = new THREE.Quaternion().setFromUnitVectors(dirJ1Local, dirJ);
                dirJ1.applyQuaternion(q);
                dirJ2.applyQuaternion(q);

                arrowJ = new THREE.ArrowHelper(dirJ, new THREE.Vector3(0, 0, 0), L_J, 0x000000, 0.2, 0.1);
                arrowJ1 = new THREE.ArrowHelper(dirJ1, new THREE.Vector3(0, 0, 0), L_J1, 0xff0000, 0.2, 0.1);
                arrowJ2 = new THREE.ArrowHelper(dirJ2, new THREE.Vector3(0, 0, 0), L_J2, 0x0000ff, 0.2, 0.1);

                const coneJ = new THREE.ConeGeometry(L_J, 0.5, 32);
                coneJ.rotateX(Math.PI / 2);
                coneJ.rotateZ(Math.acos((m1 + m2) / L_J));
                const materialJ = new THREE.MeshBasicMaterial({ color: 0x000000, transparent: true, opacity: 0.5 });
                const meshJ = new THREE.Mesh(coneJ, materialJ);
                scene.add(meshJ);

                const coneJ1 = new THREE.ConeGeometry(L_J1, 0.5, 32);
                coneJ1.rotateX(Math.PI / 2);
                coneJ1.rotateZ(Math.acos(m1 / L_J1));
                const materialJ1 = new THREE.MeshBasicMaterial({ color: 0xff0000, transparent: true, opacity: 0.5 });
                const meshJ1 = new THREE.Mesh(coneJ1, materialJ1);
                meshJ1.position.copy(arrowJ1.position);
                scene.add(meshJ1);

                const coneJ2 = new THREE.ConeGeometry(L_J2, 0.5, 32);
                coneJ2.rotateX(Math.PI / 2);
                coneJ2.rotateZ(Math.acos(m2 / L_J2));
                const materialJ2 = new THREE.MeshBasicMaterial({ color: 0x0000ff, transparent: true, opacity: 0.5 });
                const meshJ2 = new THREE.Mesh(coneJ2, materialJ2);
                meshJ2.position.copy(arrowJ2.position);
                scene.add(meshJ2);
            } else {
                arrowJ1 = new THREE.ArrowHelper(dirJ1, new THREE.Vector3(0, 0, 0), L_J1, 0xff0000, 0.2, 0.1);
                arrowJ2 = new THREE.ArrowHelper(dirJ2, new THREE.Vector3(0, 0, 0), L_J2, 0x0000ff, 0.2, 0.1);

                const coneJ1 = new THREE.ConeGeometry(L_J1, 0.5, 32);
                coneJ1.rotateX(Math.PI / 2);
                coneJ1.rotateZ(Math.acos(m1 / L_J1));
                const materialJ1 = new THREE.MeshBasicMaterial({ color: 0xff0000, transparent: true, opacity: 0.5 });
                const meshJ1 = new THREE.Mesh(coneJ1, materialJ1);
                scene.add(meshJ1);

                const coneJ2 = new THREE.ConeGeometry(L_J2, 0.5, 32);
                coneJ2.rotateX(Math.PI / 2);
                coneJ2.rotateZ(Math.acos(m2 / L_J2));
                const materialJ2 = new THREE.MeshBasicMaterial({ color: 0x0000ff, transparent: true, opacity: 0.5 });
                const meshJ2 = new THREE.Mesh(coneJ2, materialJ2);
                scene.add(meshJ2);
            }

            scene.add(arrowJ1);
            scene.add(arrowJ2);
        }

        function animate() {
            if (isAnimating) {
                phi_z += 0.01;
                phi_j += 0.01;

                const L_J = Math.sqrt(j * (j + 1));
                const L_J1 = Math.sqrt(j1 * (j1 + 1));
                const L_J2 = Math.sqrt(j2 * (j2 + 1));

                if (j >= j1 && j >= j2) {
                    const dirJ = new THREE.Vector3(0, 0, 1).applyAxisAngle(new THREE.Vector3(1, 0, 0), Math.acos((m1 + m2) / L_J)).applyAxisAngle(new THREE.Vector3(0, 0, 1), phi_z);
                    const dirJ1Local = new THREE.Vector3(1, 0, 0).applyAxisAngle(new THREE.Vector3(0, 1, 0), phi_j);
                    const dirJ2Local = new THREE.Vector3(-1, 0, 0).applyAxisAngle(new THREE.Vector3(0, 1, 0), phi_j);
                    const q = new THREE.Quaternion().setFromUnitVectors(dirJ1Local, dirJ);
                    dirJ1.applyQuaternion(q);
                    dirJ2.applyQuaternion(q);

                    arrowJ.setDirection(dirJ);
                    arrowJ1.setDirection(dirJ1);
                    arrowJ2.setDirection(dirJ2);
                } else {
                    arrowJ1.setDirection(new THREE.Vector3(1, 0, 0).applyAxisAngle(new THREE.Vector3(0, 0, 1), phi_z));
                    arrowJ2.setDirection(new THREE.Vector3(-1, 0, 0).applyAxisAngle(new THREE.Vector3(0, 0, 1), phi_z));
                }
            }

            renderer.render(scene, camera);
            requestAnimationFrame(animate);
        }

        function toggleAnimation() {
            isAnimating = !isAnimating;
            document.getElementById('btn-play-pause').textContent = isAnimating ? '❚❚' : '▶';
        }

        function resetAnimation() {
            phi_z = 0;
            phi_j = 0;
            isAnimating = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            drawVectorsAndCones();
        }

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        init();
        animate();
    </script>
</body>
</html>
