</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <div>
                <label for="steps-slider">steps</label>
                <input type="range" id="steps-slider" min="0" max="100" value="61" step="1">
                <span id="steps-value">+ 61</span>
            </div>
            <div>
                <label>box</label>
                <button id="btn-none">None</button>
                <button id="btn-box">Box</button>
                <button id="btn-scattered" class="active">Scattered</button>
            </div>
        </div>
        <div class="viz-container" id="viz-container">
            <div class="grid-display" id="recursive-container">
                <h3>Recursive</h3>
                <div id="canvas-recursive"></div>
            </div>
            <div class="grid-display" id="iterative-container">
                <h3>Iterative</h3>
                <div id="canvas-iterative"></div>
            </div>
        </div>
    </div>

    <script>
        const EMPTY = 0;
        const OBSTACLE = 1;
        const FILLED = 2;

        let recursiveHistory = [];
        let iterativeHistory = [];
        let gridSize = 10;
        let startCell = { x: 4, y: 4 };
        let currentPattern = 'scattered';

        function setupPatterns() {
            let grid = Array.from({ length: gridSize }, () => Array(gridSize).fill(EMPTY));

            switch (currentPattern) {
                case 'none':
                    break;
                case 'box':
                    for (let i = 0; i < gridSize; i++) {
                        grid[0][i] = OBSTACLE;
                        grid[gridSize - 1][i] = OBSTACLE;
                        grid[i][0] = OBSTACLE;
                        grid[i][gridSize - 1] = OBSTACLE;
                    }
                    break;
                case 'scattered':
                    for (let i = 0; i < gridSize; i++) {
                        for (let j = 0; j < gridSize; j++) {
                            if (Math.random() < 0.25 && !(i === startCell.x && j === startCell.y)) {
                                grid[i][j] = OBSTACLE;
                            }
                        }
                    }
                    break;
            }

            return grid;
        }

        function floodFillRecursive(grid, x, y, history) {
            if (x < 0 || x >= gridSize || y < 0 || y >= gridSize || grid[x][y] !== EMPTY) return;
            history.push({ x, y });
            grid[x][y] = FILLED;
            floodFillRecursive(grid, x, y - 1, history); // North
            floodFillRecursive(grid, x - 1, y, history); // West
            floodFillRecursive(grid, x, y + 1, history); // South
            floodFillRecursive(grid, x + 1, y, history); // East
        }

        function floodFillIterative(grid, startX, startY, history) {
            let queue = [{ x: startX, y: startY }];
            while (queue.length > 0) {
                let { x, y } = queue.shift();
                if (x < 0 || x >= gridSize || y < 0 || y >= gridSize || grid[x][y] !== EMPTY) continue;
                history.push({ x, y });
                grid[x][y] = FILLED;
                queue.push({ x, y - 1 }); // North
                queue.push({ x - 1, y }); // West
                queue.push({ x, y + 1 }); // South
                queue.push({ x + 1, y }); // East
            }
        }

        function drawGrid(sketch, grid, history, step) {
            sketch.background(255);
            sketch.stroke(0);
            sketch.strokeWeight(1);

            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    sketch.fill(255);
                    if (grid[i][j] === OBSTACLE) {
                        sketch.fill(0);
                    } else if (grid[i][j] === FILLED) {
                        sketch.fill(0, 0, 255);
                    }
                    sketch.rect(i * 20, j * 20, 20, 20);
                }
            }

            for (let k = 0; k < step; k++) {
                let { x, y } = history[k];
                sketch.fill(0, 0, 255);
                sketch.rect(x * 20, y * 20, 20, 20);
            }
        }

        function setupRecursiveCanvas() {
            let recursiveSketch = new p5((sketch) => {
                sketch.setup = () => {
                    sketch.createCanvas(201, 201);
                };

                sketch.draw = () => {
                    let step = document.getElementById('steps-slider').value;
                    drawGrid(sketch, recursiveGrid, recursiveHistory, step);
                };
            }, 'canvas-recursive');
        }

        function setupIterativeCanvas() {
            let iterativeSketch = new p5((sketch) => {
                sketch.setup = () => {
                    sketch.createCanvas(201, 201);
                };

                sketch.draw = () => {
                    let step = document.getElementById('steps-slider').value;
                    drawGrid(sketch, iterativeGrid, iterativeHistory, step);
                };
            }, 'canvas-iterative');
        }

        function updateSliderMax() {
            let maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
            document.getElementById('steps-slider').max = maxSteps;
        }

        function resetSimulation() {
            recursiveHistory = [];
            iterativeHistory = [];
            let grid = setupPatterns();

            let recursiveGrid = [...grid.map(row => [...row])];
            floodFillRecursive(recursiveGrid, startCell.x, startCell.y, recursiveHistory);

            let iterativeGrid = [...grid.map(row => [...row])];
            floodFillIterative(iterativeGrid, startCell.x, startCell.y, iterativeHistory);

            updateSliderMax();
            document.getElementById('steps-slider').value = 61;
            document.getElementById('steps-value').textContent = `+ 61`;
        }

        document.getElementById('btn-none').addEventListener('click', () => {
            currentPattern = 'none';
            document.getElementById('btn-none').classList.add('active');
            document.getElementById('btn-box').classList.remove('active');
            document.getElementById('btn-scattered').classList.remove('active');
            resetSimulation();
        });

        document.getElementById('btn-box').addEventListener('click', () => {
            currentPattern = 'box';
            document.getElementById('btn-none').classList.remove('active');
            document.getElementById('btn-box').classList.add('active');
            document.getElementById('btn-scattered').classList.remove('active');
            resetSimulation();
        });

        document.getElementById('btn-scattered').addEventListener('click', () => {
            currentPattern = 'scattered';
            document.getElementById('btn-none').classList.remove('active');
            document.getElementById('btn-box').classList.remove('active');
            document.getElementById('btn-scattered').classList.add('active');
            resetSimulation();
        });

        document.getElementById('steps-slider').addEventListener('input', (event) => {
            document.getElementById('steps-value').textContent = `+ ${event.target.value}`;
        });

        resetSimulation();
        setupRecursiveCanvas();
        setupIterativeCanvas();
    </script>
</body>
</html>
