      width: 700px;
      height: 400px;
      border: 1px solid #ccc;
    }
  </style>
</head>
<body>
  <div id="control-panel">
    <div>
      <label for="slider-k">k</label>
      <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
    </div>
    <div>
      <label for="slider-time">time</label>
      <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
    </div>
    <div>
      <label for="slider-vx">initial velocity in x</label>
      <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
    </div>
    <div>
      <label for="slider-dt">Δt</label>
      <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
    </div>
  </div>
  <div id="canvas-container"></div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    let g = 9.8; // gravitational acceleration in m/s^2
    let y0 = 10; // initial height in meters
    let vy0 = 0; // initial vertical velocity in m/s
    let x0 = 0; // initial horizontal position in meters

    function setup() {
      createCanvas(700, 400);
      noLoop();
      redraw(); // Initialize the drawing with default values
    }

    function draw() {
      let k = parseFloat(document.getElementById('slider-k').value);
      let time = parseFloat(document.getElementById('slider-time').value);
      let vx = parseFloat(document.getElementById('slider-vx').value);
      let dt = parseFloat(document.getElementById('slider-dt').value);

      background(0, 0, 0);
      stroke(100, 100, 150);
      strokeWeight(1);
      line(40, 40, 40, 360); // y-axis
      line(40, 360, 660, 360); // x-axis

      // Draw tick marks
      for (let i = 1; i <= 9; i++) {
        line(40 + i * 72, 355, 40 + i * 72, 360);
        line(45, 360 - i * 32, 50, 360 - i * 32);
      }

      let currentTime = 0;
      let currentX = x0;
      let currentY = y0;
      let currentVy = vy0;
      let pathPoints = [];
      let strobePoints = [];

      while (currentTime < time) {
        let vy_impact = -sqrt(k) * currentVy;
        let t_flight = (2 * currentVy) / g;
        let t_strobe = currentTime;

        // Generate trajectory points
        for (let t = 0; t <= t_flight; t += 0.01) {
          let x = currentX + vx * t;
          let y = currentY + currentVy * t - 0.5 * g * t * t;
          pathPoints.push({ x, y });
        }

        // Generate strobe points
        while (t_strobe < currentTime + t_flight) {
          let x = currentX + vx * (t_strobe - currentTime);
          let y = currentY + currentVy * (t_strobe - currentTime) - 0.5 * g * (t_strobe - currentTime) * (t_strobe - currentTime);
          strobePoints.push({ x, y });
          t_strobe += dt;
        }

        // Update state for next bounce
        currentTime += t_flight;
        currentX += vx * t_flight;
        currentVy = sqrt(k) * vy_impact;
        currentY = 0;
      }

      // Scale and draw path points
      let maxX = max(pathPoints.map(pt => pt.x));
      let maxY = max(pathPoints.map(pt => pt.y));
      let scaleX = (620 / maxX);
      let scaleY = (320 / maxY);

      stroke(255, 0, 0);
      strokeWeight(2);
      noFill();
      beginShape();
      for (let pt of pathPoints) {
        vertex(40 + pt.x * scaleX, 360 - pt.y * scaleY);
      }
      endShape();

      // Draw strobe points
      fill(255, 255, 0);
      noStroke();
      for (let pt of strobePoints) {
        ellipse(40 + pt.x * scaleX, 360 - pt.y * scaleY, 8, 8);
      }
    }

    // Attach input event listeners to sliders
    document.getElementById('slider-k').addEventListener('input', redraw);
    document.getElementById('slider-time').addEventListener('input', redraw);
    document.getElementById('slider-vx').addEventListener('input', redraw);
    document.getElementById('slider-dt').addEventListener('input', redraw);
  </script>
</body>
</html>
