    <script>
        let canvas;
        const centerX = 250;
        const centerY = 250;
        const radius = 200;
        const tolerance = 15;

        let angleA = 7 * Math.PI / 6;
        let angleB = 11 * Math.PI / 6;
        let angleC = Math.PI / 2;

        let activePoint = null;

        function setup() {
            canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');
