                { label: 'A', angle: angleA },
                { label: 'B', angle: angleB },
                { label: 'C', angle: angleC },
                { label: "A'", angle: calculateArcMidpoint(angleB, angleC) },
                { label: "B'", angle: calculateArcMidpoint(angleA, angleC) },
                { label: "C'", angle: calculateArcMidpoint(angleA, angleB) }
            ];

            points.forEach(({ label, angle }, index) => {
                const x = centerX + radius * Math.cos(angle);
                const y = centerY + radius * Math.sin(angle);

                fill('#FFA500');
                stroke('#000000');
                strokeWeight(1);
                ellipse(x, y, 12, 12);

                if (index < 3) {
                    // Draw crosshair for interactive points
                    line(x - 4, y, x + 4, y);
                    line(x, y - 4, x, y + 4);
                }

                fill('#000000');
                noStroke();
                textSize(16);
                textAlign(CENTER, CENTER);
                text(label, x + 15 * Math.cos(angle), y + 15 * Math.sin(angle));
            });
        }

        function drawLines() {
            const points = [
                { label: 'A', angle: angleA },
                { label: 'B', angle: angleB },
                { label: 'C', angle: angleC },
                { label: "A'", angle: calculateArcMidpoint(angleB, angleC) },
                { label: "B'", angle: calculateArcMidpoint(angleA, angleC) },
                { label: "C'", angle: calculateArcMidpoint(angleA, angleB) }
            ];

            const [A, B, C, APrime, BPrime, CPrime] = points.map(({ angle }) => {
                return {
                    x: centerX + radius * Math.cos(angle),
                    y: centerY + radius * Math.sin(angle)
                };
            });

            stroke('#6A5ACD');
            strokeWeight(4);
            line(A.x, A.y, B.x, B.y);
            line(B.x, B.y, C.x, C.y);
            line(C.x, C.y, A.x, A.y);

            stroke('#FFA500');
            strokeWeight(4);
            line(A.x, A.y, APrime.x, APrime.y);
            line(B.x, B.y, BPrime.x, BPrime.y);
            line(C.x, C.y, CPrime.x, CPrime.y);

            line(APrime.x, APrime.y, BPrime.x, BPrime.y);
            line(BPrime.x, BPrime.y, CPrime.x, CPrime.y);
            line(CPrime.x, CPrime.y, APrime.x, APrime.y);
        }

        function calculateArcMidpoint(angle1, angle2) {
            const avgSin = (Math.sin(angle1) + Math.sin(angle2)) / 2;
            const avgCos = (Math.cos(angle1) + Math.cos(angle2)) / 2;
            return Math.atan2(avgSin, avgCos);
        }

        function mousePressed() {
            const points = [
                { label: 'A', angle: angleA },
                { label: 'B', angle: angleB },
                { label: 'C', angle: angleC }
            ];

            for (const { label, angle } of points) {
                const x = centerX + radius * Math.cos(angle);
                const y = centerY + radius * Math.sin(angle);
                if (dist(mouseX, mouseY, x, y) < tolerance) {
                    activePoint = label;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (activePoint) {
                const angle = Math.atan2(mouseY - centerY, mouseX - centerX);
                if (activePoint === 'A') {
                    angleA = angle;
                } else if (activePoint === 'B') {
                    angleB = angle;
                } else if (activePoint === 'C') {
                    angleC = angle;
                }
            }
        }

        function mouseReleased() {
            activePoint = null;
        }
    </script>
</body>
</html>
