<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>2D Kinematic Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            height: 100vh;
        }
        #control-panel {
            width: 200px;
            background-color: #f0f0f0;
            padding: 20px;
            box-sizing: border-box;
        }
        .slider-container {
            display: flex;
            align-items: center;
            margin-bottom: 10px;
        }
        .slider-container input[type="range"] {
            flex-grow: 1;
        }
        .slider-container button {
            margin-left: 10px;
            padding: 5px;
            cursor: pointer;
        }
        .toggle-switch {
            display: flex;
            margin-bottom: 10px;
        }
        .toggle-switch input[type="radio"] {
            display: none;
        }
        .toggle-switch label {
            padding: 5px 10px;
            border: 1px solid #ccc;
            cursor: pointer;
            margin-right: 5px;
        }
        .toggle-switch input[type="radio"]:checked + label {
            background-color: #007bff;
            color: white;
        }
        #canvas-container {
            flex-grow: 1;
            position: relative;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time</label>
            <div class="slider-container">
                <input type="range" id="slider-time" min="0" max="6.283" step="0.01" value="0.4">
                <button id="btn-reset-time" title="Reset time">+</button>
            </div>

            <label>position</label>
            <div id="toggle-position" class="toggle-switch">
                <input type="radio" id="radio-position-off" name="position" value="off" checked>
                <label for="radio-position-off">off</label>
                <input type="radio" id="radio-position-on" name="position" value="on">
                <label for="radio-position-on">on</label>
            </div>

            <label>velocity</label>
            <div id="toggle-velocity" class="toggle-switch">
                <input type="radio" id="radio-velocity-off" name="velocity" value="off" checked>
                <label for="radio-velocity-off">off</label>
                <input type="radio" id="radio-velocity-on" name="velocity" value="on">
                <label for="radio-velocity-on">on</label>
            </div>

            <label>acceleration</label>
            <div id="toggle-acceleration" class="toggle-switch">
                <input type="radio" id="radio-acceleration-off" name="acceleration" value="off">
                <label for="radio-acceleration-off">off</label>
                <input type="radio" id="radio-acceleration-on" name="acceleration" value="on" checked>
                <label for="radio-acceleration-on">on</label>
            </div>

            <label for="select-coords">coordinate system</label>
            <select id="select-coords">
                <option value="rectangular">rectangular</option>
                <option value="normal-tangential">normal-tangential</option>
                <option value="polar">polar</option>
            </select>

            <label>instantaneous circle</label>
            <div id="toggle-circle" class="toggle-switch">
                <input type="radio" id="radio-circle-off" name="circle" value="off" checked>
                <label for="radio-circle-off">off</label>
                <input type="radio" id="radio-circle-on" name="circle" value="on">
                <label for="radio-circle-on">on</label>
            </div>
        </div>

        <div id="canvas-container">
            <script>
                let t = 0.4;
                let showPosition = false;
                let showVelocity = false;
                let showAcceleration = true;
                let coordSystem = 'rectangular';
                let showCircle = false;

                function setup() {
                    createCanvas(window.innerWidth - 200, window.innerHeight);
                }

                function draw() {
                    background(255);
                    stroke(0);
                    line(-width / 2, 0, width / 2, 0); // x-axis
                    line(0, -height / 2, 0, height / 2); // y-axis

                    textSize(12);
                    textAlign(CENTER, TOP);
                    text("x(t)", width / 2, 10);
                    text("y(t)", -10, -height / 2);

                    let x = cos(t);
                    let y = sin(2 * t);

                    let vx = -sin(t);
                    let vy = 2 * cos(2 * t);

                    let ax = -cos(t);
                    let ay = -4 * sin(2 * t);

                    let r = createVector(x, y);
                    let v = createVector(vx, vy);
                    let a = createVector(ax, ay);

                    let vNorm = v.copy().normalize();
                    let nNorm = createVector(-v.y, v.x).normalize();

                    let rNorm = r.copy().normalize();
                    let thetaNorm = createVector(-r.y, r.x).normalize();

                    fill(0);
                    ellipse(x * width / 2, -y * width / 2, 5, 5);

                    if (showPosition) {
                        stroke(0);
                        line(0, 0, x * width / 2, -y * width / 2);
                    }

                    if (showVelocity) {
                        stroke(150, 0, 0);
                        drawArrow(r, v, 10, 10);
                    }

                    if (showAcceleration) {
                        stroke(255, 0, 255);
                        drawArrow(r, a, 10, 10);
                    }

                    stroke(0, 0, 0, 100);
                    strokeWeight(1);
                    if (coordSystem === 'rectangular') {
                        drawArrow(r, createVector(1, 0), 10, 10);
                        drawArrow(r, createVector(0, 1), 10, 10);
                        text('î', (r.x + 1) * width / 2, -(r.y + 0.1) * width / 2);
                        text('ĵ', (r.x + 0.1) * width / 2, -(r.y + 1) * width / 2);
                    } else if (coordSystem === 'normal-tangential') {
                        drawArrow(r, vNorm, 10, 10);
                        drawArrow(r, nNorm, 10, 10);
                        text('ê_T', (r.x + vNorm.x) * width / 2, -(r.y + vNorm.y) * width / 2);
                        text('ê_N', (r.x + nNorm.x) * width / 2, -(r.y + nNorm.y) * width / 2);
                    } else if (coordSystem === 'polar') {
                        drawArrow(r, rNorm, 10, 10);
                        drawArrow(r, thetaNorm, 10, 10);
                        text('ê_r', (r.x + rNorm.x) * width / 2, -(r.y + rNorm.y) * width / 2);
                        text('ê_θ', (r.x + thetaNorm.x) * width / 2, -(r.y + thetaNorm.y) * width / 2);
                    }

                    if (showCircle) {
                        let curvature = abs(v.x * a.y - v.y * a.x) / pow(v.mag(), 3);
                        let radius = 1 / curvature;
                        let center = r.add(nNorm.mult(radius));
                        noFill();
                        stroke(128, 128, 128, 100);
                        strokeWeight(1);
                        circle(center.x * width / 2, -center.y * width / 2, radius * 2 * width);
                    }
                }

                function drawArrow(base, vec, headSize, headLen) {
                    push();
                    translate(base.x * width / 2, -base.y * width / 2);
                    line(0, 0, vec.x * width / 2, -vec.y * width / 2);
                    rotate(vec.heading());
                    translate(vec.mag() * width / 2 - headLen, 0);
                    triangle(0, headSize / 2, 0, -headSize / 2, headLen, 0);
                    pop();
                }

                document.getElementById('slider-time').addEventListener('input', (e) => {
                    t = parseFloat(e.target.value);
                    redraw();
                });

                document.getElementById('btn-reset-time').addEventListener('click', () => {
                    document.getElementById('slider-time').value = 0.4;
                    t = 0.4;
                    redraw();
                });

                document.querySelectorAll('#toggle-position input').forEach(input => {
                    input.addEventListener('change', (e) => {
                        showPosition = e.target.value === 'on';
                        redraw();
                    });
                });

                document.querySelectorAll('#toggle-velocity input').forEach(input => {
                    input.addEventListener('change', (e) => {
                        showVelocity = e.target.value === 'on';
                        redraw();
                    });
                });

                document.querySelectorAll('#toggle-acceleration input').forEach(input => {
                    input.addEventListener('change', (e) => {
                        showAcceleration = e.target.value === 'on';
                        redraw();
                    });
                });

                document.getElementById('select-coords').addEventListener('change', (e) => {
                    coordSystem = e.target.value;
                    redraw();
                });

                document.querySelectorAll('#toggle-circle input').forEach(input => {
                    input.addEventListener('change', (e) => {
                        showCircle = e.target.value === 'on';
                        redraw();
                    });
                });
            </script>
        </div>
    </div>
</body>
</html>
