        #animation-canvas-container {
            flex-grow: 1;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-amplitude">Amplitude</label>
                <input type="range" id="slider-amplitude" min="0.1" max="1.0" step="0.001" value="0.3">
                <span id="amplitude-value">0.3</span>
            </div>
            <div class="control-group">
                <label for="slider-stiffness">Stiffness</label>
                <input type="range" id="slider-stiffness" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="stiffness-value">1.0</span>
            </div>
            <div class="control-group">
                <label for="slider-mass">Mass</label>
                <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="mass-value">1.0</span>
            </div>
            <div id="time-controls">
                <label for="time-controls">Time</label>
                <button id="btn-step-back">◀</button>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-forward">▶|</button>
            </div>
            <button id="btn-reset">x</button>
        </div>
        <div id="visualization-container">
            <div id="plot-canvas-container"></div>
            <div id="animation-canvas-container"></div>
        </div>
    </div>

    <script>
        let amplitude = 0.3;
        let stiffness = 1.0;
        let mass = 1.0;
        let time = 0;
        let isPlaying = false;
        let angularFrequency;
        let positionHistory = [];
        let plotCanvas;
        let animationCanvas;

        function setup() {
            plotCanvas = createCanvas(600, 400).parent('plot-canvas-container');
            animationCanvas = createCanvas(300, 400).parent('animation-canvas-container');
            calculateAngularFrequency();
            updateValueDisplays();
        }

        function draw() {
            if (isPlaying) {
                time += 0.05;
                updatePositionHistory();
            }
            drawPlot();
            drawAnimation();
        }

        function calculateAngularFrequency() {
            angularFrequency = sqrt(stiffness / mass);
        }

        function updateValueDisplays() {
            document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
            document.getElementById('stiffness-value').textContent = stiffness.toFixed(3);
            document.getElementById('mass-value').textContent = mass.toFixed(3);
        }

        function updatePositionHistory() {
            const y = amplitude * sin(angularFrequency * time);
            positionHistory.push({ time, y });
            if (time > plotCanvas.width / 10) {
                plotCanvas.resizeCanvas(plotCanvas.width + 100, plotCanvas.height);
            }
        }

        function drawPlot() {
            plotCanvas.clear();
            plotCanvas.stroke(0);
            plotCanvas.line(0, plotCanvas.height / 2, plotCanvas.width, plotCanvas.height / 2); // x-axis
            plotCanvas.line(0, plotCanvas.height / 2, 0, 0); // y-axis

            plotCanvas.stroke(200);
            for (let i = 0; i <= plotCanvas.width; i += 20) {
                plotCanvas.line(i, plotCanvas.height / 2 - 5, i, plotCanvas.height / 2 + 5);
                plotCanvas.text(nf(i / 10, 1, 1), i, plotCanvas.height / 2 + 15);
            }
            for (let j = -1; j <= 1; j += 0.5) {
                plotCanvas.line(0, plotCanvas.height / 2 - j * plotCanvas.height / 4, 10, plotCanvas.height / 2 - j * plotCanvas.height / 4);
                plotCanvas.text(nf(j, 1, 1), 15, plotCanvas.height / 2 - j * plotCanvas.height / 4 + 3);
            }

            plotCanvas.stroke('#6363B2');
            plotCanvas.noFill();
            beginShape();
            for (let i = 0; i < positionHistory.length; i++) {
                const { time, y } = positionHistory[i];
                vertex(time * 10, plotCanvas.height / 2 - y * plotCanvas.height / 4);
            }
            endShape();
        }

        function drawAnimation() {
            animationCanvas.clear();
            animationCanvas.stroke(0);
            animationCanvas.line(animationCanvas.width / 2, 0, animationCanvas.width / 2, animationCanvas.height); // y-axis

            animationCanvas.stroke(200);
            for (let j = -1; j <= 1; j += 0.5) {
                animationCanvas.line(animationCanvas.width / 2 - 5, animationCanvas.height / 2 - j * animationCanvas.height / 2, animationCanvas.width / 2 + 5, animationCanvas.height / 2 - j * animationCanvas.height / 2);
                animationCanvas.text(nf(j, 1, 1), animationCanvas.width / 2 + 10, animationCanvas.height / 2 - j * animationCanvas.height / 2 + 3);
            }

            const y = amplitude * sin(angularFrequency * time);
            animationCanvas.stroke(0);
            animationCanvas.line(0, animationCanvas.height / 2 - 0.6 * animationCanvas.height / 2, animationCanvas.width / 2, animationCanvas.height / 2 - y * animationCanvas.height / 2); // Spring
            animationCanvas.fill('#6363B2');
            animationCanvas.rect(animationCanvas.width / 2 - 10, animationCanvas.height / 2 - y * animationCanvas.height / 2 - 10, 20, 20); // Mass
        }

        document.getElementById('slider-amplitude').addEventListener('input', () => {
            amplitude = parseFloat(document.getElementById('slider-amplitude').value);
            resetSimulation();
        });

        document.getElementById('slider-stiffness').addEventListener('input', () => {
            stiffness = parseFloat(document.getElementById('slider-stiffness').value);
            resetSimulation();
        });

        document.getElementById('slider-mass').addEventListener('input', () => {
            mass = parseFloat(document.getElementById('slider-mass').value);
            resetSimulation();
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isPlaying = !isPlaying;
            document.getElementById('btn-play-pause').textContent = isPlaying ? '||' : '▶';
        });

        document.getElementById('btn-step-back').addEventListener('click', () => {
            if (!isPlaying) {
                time = max(0, time - 0.1);
                updatePositionHistory();
            }
        });

        document.getElementById('btn-step-forward').addEventListener('click', () => {
            if (!isPlaying) {
                time += 0.1;
                updatePositionHistory();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            amplitude = 0.3;
            stiffness = 1.0;
            mass = 1.0;
            time = 0;
            isPlaying = false;
            positionHistory = [];
            document.getElementById('btn-play-pause').textContent = '▶';
            updateValueDisplays();
            plotCanvas.clear();
            animationCanvas.clear();
        });

        function resetSimulation() {
            time = 0;
            isPlaying = false;
            positionHistory = [];
            calculateAngularFrequency();
            updateValueDisplays();
            plotCanvas.clear();
            animationCanvas.clear();
        }
    </script>
</body>
</html>
