<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Distance Transform Demo</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            font-family: Arial, sans-serif;
        }
        .main-container {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        .control-panel {
            margin-bottom: 20px;
        }
        .control-panel span {
            margin-right: 10px;
        }
        .control-panel button {
            padding: 5px 10px;
            cursor: pointer;
            margin-right: 5px;
            background-color: #D3D3D3;
            border: none;
        }
        .control-panel button.active {
            background-color: #A9A9A9;
        }
        #grid-container {
            display: grid;
            grid-template-columns: repeat(7, 1fr);
            grid-template-rows: repeat(9, 1fr);
            gap: 1px;
        }
        .cell {
            width: 40px;
            height: 40px;
            display: flex;
            justify-content: center;
            align-items: center;
            cursor: pointer;
            background-color: #D3D3D3;
            border: 1px solid #000;
        }
        .cell.cell-on {
            background-color: #FFD700;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <span>distance function</span>
            <button id="btn-manhattan" class="active">Manhattan</button>
            <button id="btn-euclidean">squared Euclidean</button>
            <button id="btn-chebyshev">Chebyshev</button>
        </div>
        <div id="grid-container"></div>
    </div>

    <script>
        const currentDistanceFunction = 'manhattan';
        let gridState = Array.from({ length: 9 }, () => Array(7).fill(false));
        gridState[2][1] = true;
        gridState[6][4] = true;

        const btnManhattan = document.getElementById('btn-manhattan');
        const btnEuclidean = document.getElementById('btn-euclidean');
        const btnChebyshev = document.getElementById('btn-chebyshev');
        const gridContainer = document.getElementById('grid-container');

        btnManhattan.addEventListener('click', () => setActiveButton(btnManhattan));
        btnEuclidean.addEventListener('click', () => setActiveButton(btnEuclidean));
        btnChebyshev.addEventListener('click', () => setActiveButton(btnChebyshev));

        function setActiveButton(button) {
            btnManhattan.classList.remove('active');
            btnEuclidean.classList.remove('active');
            btnChebyshev.classList.remove('active');
            button.classList.add('active');
            currentDistanceFunction = button.id.slice(3);
            recalculateDistances();
        }

        function initializeGrid() {
            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    const cell = document.createElement('div');
                    cell.className = 'cell';
                    cell.dataset.row = r;
                    cell.dataset.col = c;
                    cell.textContent = '';
                    cell.addEventListener('click', toggleCell);
                    gridContainer.appendChild(cell);
                }
            }
            recalculateDistances();
        }

        function toggleCell(event) {
            const { row, col } = event.target.dataset;
            gridState[row][col] = !gridState[row][col];
            recalculateDistances();
        }

        function recalculateDistances() {
            const cells = gridContainer.querySelectorAll('.cell');
            cells.forEach(cell => {
                const { row, col } = cell.dataset;
                const distance = calculateDistance(row, col);
                cell.textContent = distance;
                cell.className = gridState[row][col] ? 'cell cell-on' : 'cell cell-off';
            });
        }

        function calculateDistance(r, c) {
            if (gridState[r][c]) return 0;

            let minDistance = Infinity;
            const sources = gridState.flatMap((row, rowIndex) => row.map((isOn, colIndex) => isOn ? [rowIndex, colIndex] : null)).filter(Boolean);

            for (const [sr, sc] of sources) {
                let distance;
                switch (currentDistanceFunction) {
                    case 'manhattan':
                        distance = Math.abs(r - sr) + Math.abs(c - sc);
                        break;
                    case 'euclidean':
                        distance = Math.pow(r - sr, 2) + Math.pow(c - sc, 2);
                        break;
                    case 'chebyshev':
                        distance = Math.max(Math.abs(r - sr), Math.abs(c - sc));
                        break;
                }
                if (distance < minDistance) {
                    minDistance = distance;
                }
            }

            return minDistance;
        }

        initializeGrid();
    </script>
</body>
</html>
