            height: 100%;
        }
    </style>
</head>
<body>
    <!-- Main container -->
    <div id="main-container">

        <!-- Left column: Control Panel -->
        <div id="control-panel">
            <!-- Data Generation Model Selection -->
            <div>
                <label>data generation model</label>
                <div>
                    <button id="btn-model-ep" class="active">Eyring–Polanyi</button>
                    <button id="btn-model-ar">Arrhenius</button>
                </div>
            </div>

            <!-- T_max Upper Limit Selection -->
            <div>
                <label>\(T_{max}\) upper limit</label>
                <div>
                    <button id="btn-tmax-low" class="active">low (1,000 °C)</button>
                    <button id="btn-tmax-high">high (6,000 °C)</button>
                </div>
            </div>

            <!-- Data Generation Sliders -->
            <div>
                <label for="slider-n">\(n\)</label>
                <input type="range" id="slider-n" min="2" max="20" value="6" step="1">
                <span id="label-n">6</span>
            </div>
            <div>
                <label for="slider-tmin">\(T_{min}\) (°C)</label>
                <input type="range" id="slider-tmin" min="0" max="5999" value="25" step="1">
                <span id="label-tmin">25</span>
            </div>
            <div>
                <label for="slider-tmax">\(T_{max}\) (°C)</label>
                <input type="range" id="slider-tmax" min="1" max="1200" value="100" step="1">
                <span id="label-tmax">100</span>
            </div>

            <!-- Model Parameter Sliders -->
            <div>
                <label>data generation model parameters</label>
                <div>
                    <label for="slider-tref">\(T_{ref}\) (°C)</label>
                    <input type="range" id="slider-tref" min="0" max="6000" value="50" step="1">
                    <span id="label-tref">50</span>
                </div>
                <div>
                    <label for="slider-param" id="label-param-name">\(a_{EP}\)</label>
                    <input type="range" id="slider-param" min="1" max="6000" value="4000" step="1">
                    <span id="label-param-value">4000</span>
                </div>
            </div>
        </div>

        <!-- Right column: Visualization Area -->
        <div id="visualization-area">
            <div id="fit-results">\($a_{Ar} = 4,345.\) \($r^2 = 1.000$\)</div>
            <div id="plot-container"></div>
        </div>
    </div>

    <!-- CDN Scripts -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        // Initial state
        const update = () => {
            const model = document.querySelector('#btn-model-ep').classList.contains('active') ? 'ep' : 'ar';
            const tmaxLimit = document.querySelector('#btn-tmax-low').classList.contains('active') ? 'low' : 'high';

            // Button logic
            document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
            document.querySelector(`#btn-model-${model}`).classList.add('active');
            document.querySelector(`#btn-tmax-${tmaxLimit}`).classList.add('active');

            // Slider constraint logic
            const sliderTmax = document.getElementById('slider-tmax');
            if (tmaxLimit === 'low') {
                sliderTmax.max = 1200;
            } else {
                sliderTmax.max = 6000;
            }

            const sliderTmin = document.getElementById('slider-tmin');
            const sliderTref = document.getElementById('slider-tref');
            const sliderParam = document.getElementById('slider-param');

            if (parseInt(sliderTmin.value) > parseInt(sliderTmax.value)) {
                sliderTmin.value = sliderTmax.value;
            }
            if (parseInt(sliderTmin.value) > parseInt(sliderTref.value)) {
                sliderTref.value = sliderTmin.value;
            }
            if (parseInt(sliderTref.value) > parseInt(sliderTmax.value)) {
                sliderTref.value = sliderTmax.value;
            }

            // Dynamic label logic
            const labelParamName = document.getElementById('label-param-name');
            if (model === 'ep') {
                labelParamName.textContent = '\\(a_{EP}\\)';
            } else {
                labelParamName.textContent = '\\(a_{Ar}\\)';
            }
            MathJax.typeset();

            // Update span labels
            document.getElementById('label-n').textContent = sliderN.value;
            document.getElementById('label-tmin').textContent = sliderTmin.value;
            document.getElementById('label-tmax').textContent = sliderTmax.value;
            document.getElementById('label-tref').textContent = sliderTref.value;
            document.getElementById('label-param-value').textContent = sliderParam.value;

            // Data generation
            const n = parseInt(sliderN.value);
            const tMin = parseInt(sliderTmin.value);
            const tMax = parseInt(sliderTmax.value);
            const tRef = parseInt(sliderTref.value);
            const aParam = parseInt(sliderParam.value);

            const tValues = [];
            const yValues = [];

            for (let i = 0; i < n; i++) {
                tValues.push(tMin + (tMax - tMin) * i / (n - 1));
                const tK = tValues[i] + 273.15;
                const tRefK = tRef + 273.15;
                let y;
                if (model === 'ep') {
                    y = (tK / tRefK) * Math.exp(aParam * (1 / tRefK - 1 / tK));
                } else {
                    y = Math.exp(aParam * (1 / tRefK - 1 / tK));
                }
                yValues.push(y);
            }

            // Data fitting
            const aFit = fitData(tValues, yValues, model);
            const r2 = calculateR2(tValues, yValues, aFit, model);

            // Display update
            const fitResults = document.getElementById('fit-results');
            fitResults.textContent = `\\(${model === 'ep' ? 'a_{Ar}' : 'a_{EP}'} = ${aFit.toFixed(0)}\\) \\($r^2 = ${r2.toFixed(3)}\\)`;
            MathJax.typeset();

            // Plot redraw
            drawPlot(tValues, yValues, aFit, model);
        };

        const fitData = (tValues, yValues, model) => {
            const tRefK = parseInt(document.getElementById('slider-tref').value) + 273.15;
            let bestA = 0;
            let minRSS = Infinity;

            for (let a = 0; a <= 10000; a++) {
                let rss = 0;
                for (let i = 0; i < tValues.length; i++) {
                    const tK = tValues[i] + 273.15;
                    let yFit;
                    if (model === 'ep') {
                        yFit = (tK / tRefK) * Math.exp(a * (1 / tRefK - 1 / tK));
                    } else {
                        yFit = Math.exp(a * (1 / tRefK - 1 / tK));
                    }
                    rss += Math.pow(yValues[i] - yFit, 2);
                }
                if (rss < minRSS) {
                    minRSS = rss;
                    bestA = a;
                }
            }

            return bestA;
        };

        const calculateR2 = (tValues, yValues, aFit, model) => {
            const tRefK = parseInt(document.getElementById('slider-tref').value) + 273.15;
            const meanY = yValues.reduce((sum, val) => sum + val, 0) / yValues.length;
            let ssTot = 0;
            let ssRes = 0;

            for (let i = 0; i < tValues.length; i++) {
                const tK = tValues[i] + 273.15;
                let yFit;
                if (model === 'ep') {
                    yFit = (tK / tRefK) * Math.exp(aFit * (1 / tRefK - 1 / tK));
                } else {
                    yFit = Math.exp(aFit * (1 / tRefK - 1 / tK));
                }
                ssTot += Math.pow(yValues[i] - meanY, 2);
                ssRes += Math.pow(yValues[i] - yFit, 2);
            }

            return 1 - (ssRes / ssTot);
        };

        const drawPlot = (tValues, yValues, aFit, model) => {
            const tRefK = parseInt(document.getElementById('slider-tref').value) + 273.15;
            const fitTrace = {
                x: [],
                y: [],
                mode: 'lines',
                name: 'Fit',
                line: { color: 'green' }
            };

            for (let i = 0; i < 200; i++) {
                const t = tValues[0] + (tValues[tValues.length - 1] - tValues[0]) * i / 199;
                const tK = t + 273.15;
                let yFit;
                if (model === 'ep') {
                    yFit = (tK / tRefK) * Math.exp(aFit * (1 / tRefK - 1 / tK));
                } else {
                    yFit = Math.exp(aFit * (1 / tRefK - 1 / tK));
                }
                fitTrace.x.push(t);
                fitTrace.y.push(yFit);
            }

            const trace1 = {
                x: tValues,
                y: yValues,
                mode: 'markers',
                name: 'Data',
                marker: { color: 'blue', size: 8, opacity: 0.6 }
            };

            const layout = {
                title: `${model === 'ep' ? 'Eyring-Polanyi' : 'Arrhenius'} model fit`,
                xaxis: { title: 'T °C' },
                yaxis: { title: 'k(T) / k(T_ref)' },
                legend: { orientation: 'h', x: 0.5, y: -0.1 }
            };

            Plotly.newPlot('plot-container', [trace1, fitTrace], layout);
        };

        // Event listeners
        document.getElementById('btn-model-ep').addEventListener('click', update);
        document.getElementById('btn-model-ar').addEventListener('click', update);
        document.getElementById('btn-tmax-low').addEventListener('click', update);
        document.getElementById('btn-tmax-high').addEventListener('click', update);
        document.getElementById('slider-n').addEventListener('input', update);
        document.getElementById('slider-tmin').addEventListener('input', update);
        document.getElementById('slider-tmax').addEventListener('input', update);
        document.getElementById('slider-tref').addEventListener('input', update);
        document.getElementById('slider-param').addEventListener('input', update);

        // Initial update
        update();
    </script>
</body>
</html>
