<body>
    <div id="controls-container">
        <span id="n-display">6</span>
        <button id="btn-n-3">3</button>
        <button id="btn-n-4">4</button>
        <button id="btn-n-5">5</button>
        <button id="btn-n-6">6</button>
        <button id="btn-n-7">7</button>
        <button id="btn-n-8">8</button>
        <button id="btn-n-9">9</button>
        <button id="btn-n-10">10</button>
    </div>
    <div id="canvas-container"></div>

    <script>
        let n = 6;
        const colors = ['#fefd00', '#ee1f24', '#0048a1'];

        function setup() {
            createCanvas(800, 600).parent('canvas-container');
            updateControls();
            drawScene();
        }

        function drawScene() {
            background(255);
            const R = 150;
            const cx = width / 4;
            const cy = height / 2;
            const angleStep = PI / n;

            // Draw the polygon on the left side
            for (let j = 1; j <= 2 * n; j++) {
                let x1 = cx + R * cos((j - 1) * angleStep - PI / 2);
                let y1 = cy + R * sin((j - 1) * angleStep - PI / 2);
                let x2 = cx + R * cos(j * angleStep - PI / 2);
                let y2 = cy + R * sin(j * angleStep - PI / 2);

                fill(colors[(j - 1) % 3]);
                stroke(0);
                triangle(cx, cy, x1, y1, x2, y2);
            }

            // Draw the rectangles on the right side
            const startX = width / 2;
            const startY = height / 2;
            const s = 2 * R * sin(angleStep / 2);
            const a = R * cos(angleStep / 2);

            for (let k = 1; k <= Math.floor(n / 2); k++) {
                let x = startX + (k - 1) * (s + 10);
                let y = startY - a;

                fill(255);
                stroke(0);
                rect(x, y, s, 2 * a);

                fill(colors[0]);
                stroke(0);
                triangle(x, y, x + s, y, x + s / 2, y - a);

                fill(colors[1]);
                stroke(0);
                triangle(x, y + 2 * a, x + s, y + 2 * a, x + s / 2, y + a);

                fill(colors[2]);
                stroke(0);
                triangle(x, y, x, y + 2 * a, x - s / 2, y + a);

                fill(colors[0]);
                stroke(0);
                triangle(x + s, y, x + s, y + 2 * a, x + s / 2, y + a);
            }

            if (n % 2 !== 0) {
                let x = startX + Math.floor(n / 2) * (s + 10);
                let y = startY - a;

                fill(255);
                stroke(0);
                rect(x, y, s, 2 * a);

                fill(colors[2]);
                stroke(0);
                triangle(x, y, x + s, y, x + s / 2, y - a);

                fill(colors[1]);
                stroke(0);
                triangle(x, y + 2 * a, x + s, y + 2 * a, x + s / 2, y + a);
            }
        }

        function updateControls() {
            const buttons = document.querySelectorAll('#controls-container button');
            buttons.forEach(button => {
                button.classList.remove('active');
                if (parseInt(button.id.split('-')[2]) === n) {
                    button.classList.add('active');
                }
            });
            document.getElementById('n-display').textContent = n;
        }

        function handleButtonClick(event) {
            n = parseInt(event.target.id.split('-')[2]);
            updateControls();
            drawScene();
        }

        document.querySelectorAll('#controls-container button').forEach(button => {
            button.addEventListener('click', handleButtonClick);
        });
    </script>
</body>
</html>
