    <script>
        let workspaceSketch, phaseSpaceSketch;
        const canvasSize = 400;
        const robotBase = { x: 200, y: 200 };
        const link1Length = 75;
        const link2Length = 50;
        const linkWidth = 10;
        const obstacles = [
            { x: 200, y: 100, radius: 15 },
            { x: 200, y: 300, radius: 15 }
        ];
        let startConfig = { q1: -1.5, q2: 0.5 };
        let goalConfig = { q1: 1.5, q2: 1.0 };
        let isDraggingObstacle1 = false, isDraggingObstacle2 = false;
        let isDraggingStart = false, isDraggingGoal = false;
        let path = [];
        let pathLength = 0;

        function setup() {
            workspaceSketch = new p5((p) => {
                p.setup = () => {
                    p.createCanvas(canvasSize, canvasSize);
                    p.angleMode(p.RADIANS);
                };

                p.draw = () => {
                    p.background(240);
                    drawRobot(p);
                    drawObstacles(p);
                };

                p.mousePressed = () => {
                    if (p.dist(p.mouseX, p.mouseY, obstacles[0].x, obstacles[0].y) < obstacles[0].radius) {
                        isDraggingObstacle1 = true;
                    } else if (p.dist(p.mouseX, p.mouseY, obstacles[1].x, obstacles[1].y) < obstacles[1].radius) {
                        isDraggingObstacle2 = true;
                    }
                };

                p.mouseDragged = () => {
                    if (isDraggingObstacle1) {
                        obstacles[0].x = p.mouseX;
                        obstacles[0].y = p.mouseY;
                    } else if (isDraggingObstacle2) {
                        obstacles[1].x = p.mouseX;
                        obstacles[1].y = p.mouseY;
                    }
                    recalculate();
                };

                p.mouseReleased = () => {
                    isDraggingObstacle1 = false;
                    isDraggingObstacle2 = false;
                };
            }, 'canvas-workspace');

            phaseSpaceSketch = new p5((p) => {
                p.setup = () => {
                    p.createCanvas(canvasSize, canvasSize);
                    p.angleMode(p.RADIANS);
                };

                p.draw = () => {
                    p.background(240);
                    drawPhaseSpace(p);
                };

                p.mousePressed = () => {
                    if (p.dist(p.mouseX, p.mouseY, mapToGrid(startConfig.q1, startConfig.q2).x, mapToGrid(startConfig.q1, startConfig.q2).y) < 5) {
                        isDraggingStart = true;
                    } else if (p.dist(p.mouseX, p.mouseY, mapToGrid(goalConfig.q1, goalConfig.q2).x, mapToGrid(goalConfig.q1, goalConfig.q2).y) < 5) {
                        isDraggingGoal = true;
                    }
                };

                p.mouseDragged = () => {
                    if (isDraggingStart) {
                        let config = mapFromGrid(p.mouseX, p.mouseY);
                        if (!isInCollision(config.q1, config.q2)) {
                            startConfig = config;
                            recalculatePath();
                        }
                    } else if (isDraggingGoal) {
                        let config = mapFromGrid(p.mouseX, p.mouseY);
                        if (!isInCollision(config.q1, config.q2)) {
                            goalConfig = config;
                            recalculate();
                        }
                    }
                };

                p.mouseReleased = () => {
                    isDraggingStart = false;
                    isDraggingGoal = false;
                };
            }, 'canvas-phase-space');

            document.getElementById('slider-q').addEventListener('input', (e) => {
                document.getElementById('value-q').textContent = e.target.value;
                recalculate();
            });

            document.getElementById('slider-p').addEventListener('input', (e) => {
                document.getElementById('value-p').textContent = parseFloat(e.target.value).toFixed(2);
                updateRobotPosition(parseFloat(e.target.value));
            });

            document.getElementById('btn-reset').addEventListener('click', () => {
                document.getElementById('slider-q').value = 2;
                document.getElementById('slider-p').value = 1;
                document.getElementById('value-q').textContent = '2';
                document.getElementById('value-p').textContent = '1.00';
                obstacles[0] = { x: 200, y: 100, radius: 15 };
                obstacles[1] = { x: 200, y: 300, radius: 15 };
                startConfig = { q1: -1.5, q2: 0.5 };
                goalConfig = { q1: 1.5, q2: 1.0 };
                recalculate();
            });

            recalculate();
        }

        function drawRobot(p) {
            p.push();
            p.translate(robotBase.x, robotBase.y);
            p.stroke(0);
            p.fill(128);
            p.rect(-linkWidth / 2, -link1Length / 2, linkWidth, link1Length);
            p.rotate(startConfig.q1);
            p.rect(-linkWidth / 2, link1Length - linkWidth / 2, linkWidth, link2Length);
            p.pop();

            p.textAlign(p.CENTER, p.CENTER);
            p.text('Robot Arm', robotBase.x, robotBase.y + link1Length + 20);

            p.stroke(0);
            p.noFill();
            p.arc(robotBase.x, robotBase.y, 100, 100, 0, startConfig.q1);
            p.arc(robotBase.x, robotBase.y, 100, 100, startConfig.q1, startConfig.q1 + startConfig.q2);
        }

        function drawObstacles(p) {
            obstacles.forEach(obstacle => {
                p.fill(150, 100, 0);
                p.ellipse(obstacle.x, obstacle.y, obstacle.radius * 2);
            });
        }

        function drawPhaseSpace(p) {
            let q = parseInt(document.getElementById('slider-q').value);
            let N = 10 * Math.pow(2, q);
            let cellSize = canvasSize / N;

            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    let q1 = p.map(i, 0, N, -Math.PI, Math.PI);
                    let q2 = p.map(j, 0, N, -Math.PI, Math.PI);
                    let config = { q1: q1, q2: q2 };
                    if (isInCollision(q1, q2)) {
                        p.fill(204, 0, 0);
                    } else {
                        let distance = getDistance(q1, q2);
                        p.fill(distance * 255);
                    }
                    p.rect(i * cellSize, j * cellSize, cellSize, cellSize);
                }
            }

            p.stroke(0, 255, 0);
            p.strokeWeight(2);
            path.forEach((point, index) => {
                if (index > 0) {
                    p.line(point.x, point.y, path[index - 1].x, path[index - 1].y);
                }
            });

            p.fill(0, 255, 0);
            p.noStroke();
            let startGrid = mapToGrid(startConfig.q1, startConfig.q2);
            let goalGrid = mapToGrid(goalConfig.q1, goalConfig.q2);
            p.circle(startGrid.x, startGrid.y, 10);
            p.circle(goalGrid.x, goalGrid.y, 10);

            p.stroke(255);
            p.line(startGrid.x - 5, startGrid.y, startGrid.x + 5, startGrid.y);
            p.line(startGrid.x, startGrid.y - 5, startGrid.x, startGrid.y + 5);
            p.line(goalGrid.x - 5, goalGrid.y, goalGrid.x + 5, goalGrid.y);
            p.line(goalGrid.x, goalGrid.y - 5, goalGrid.x, goalGrid.y + 5);

            document.getElementById('phase-space-title').textContent = `robot phase space, path length = ${pathLength.toFixed(2)} rads`;
        }

        function isInCollision(q1, q2) {
            let end1 = { x: robotBase.x + link1Length * Math.cos(q1), y: robotBase.y + link1Length * Math.sin(q1) };
            let end2 = { x: end1.x + link2Length * Math.cos(q1 + q2), y: end1.y + link2Length * Math.sin(q1 + q2) };

            for (let obstacle of obstacles) {
                if (segmentCircleIntersection(end1, end2, obstacle)) {
                    return true;
                }
            }

            return false;
        }

        function segmentCircleIntersection(A, B, circle) {
            let d = p5.Vector.sub(B, A);
            let f = p5.Vector.sub(A, circle);
            let a = d.dot(d);
            let b = 2 * f.dot(d);
            let c = f.dot(f) - circle.radius * circle.radius;
            let discriminant = b * b - 4 * a * c;

            if (discriminant < 0) {
                return false;
            } else {
                let t1 = (-b + Math.sqrt(discriminant)) / (2 * a);
                let t2 = (-b - Math.sqrt(discriminant)) / (2 * a);
                if ((t1 >= 0 && t1 <= 1) || (t2 >= 0 && t2 <= 1)) {
                    return true;
                }
            }

            return false;
        }

        function getDistance(q1, q2) {
            // Placeholder for actual BFS or similar algorithm
            return Math.random(); // Replace with actual distance
        }

        function mapToGrid(q1, q2) {
            let q = parseInt(document.getElementById('slider-q').value);
            let N = 10 * Math.pow(2, q);
            let cellSize = canvasSize / N;
            return {
                x: p.map(q1, -Math.PI, Math.PI, 0, N) * cellSize,
                y: p.map(q2, -Math.PI, Math.PI, 0, N) * cellSize
            };
        }

        function mapFromGrid(x, y) {
            let q = parseInt(document.getElementById('slider-q').value);
            let N = 10 * Math.pow(2, q);
            let cellSize = canvasSize / N;
            return {
                q1: p.map(x / cellSize, 0, N, -Math.PI, Math.PI),
                q2: p.map(y / cellSize, 0, N, -Math.PI, Math.PI)
            };
        }

        function recalculate() {
            // Placeholder for full recalculation
            path = [{ x: 0, y: 0 }, { x: canvasSize, y: canvasSize }];
            pathLength = 0; // Replace with actual path length calculation
            workspaceSketch.redraw();
            phaseSpaceSketch.redraw();
        }

        function recalculatePath() {
            // Placeholder for path recalculation
            path = [{ x: 0, y: 0 }, { x: canvasSize, y: canvasSize }];
            pathLength = 0; // Replace with actual path length calculation
            workspaceSketch.redraw();
            phaseSpaceSketch.redraw();
        }

        function updateRobotPosition(P) {
            let q1 = startConfig.q1 + P * (goalConfig.q1 - startConfig.q1);
            let q2 = startConfig.q2 + P * (goalConfig.q2 - startConfig.q2);
            workspaceSketch.redraw();
        }

        window.addEventListener('load', setup);
    </script>
