                    <input type="range" id="slider-num1" min="0" max="20" step="0.1" value="1.1">
                    <span>+</span>
                </div>
            </div>
            <hr>
            <div>
                <label>multiply by</label>
                <div class="btn-group">
                    <label>integers only</label>
                    <button id="btn-int-yes" class="active">yes</button>
                    <button id="btn-int-no">no</button>
                </div>
                <div class="slider-container">
                    <label for="slider-num2">numerical value</label>
                    <input type="range" id="slider-num2" min="0" max="20" step="1" value="2">
                    <span>+</span>
                </div>
            </div>
        </div>
        <div id="output-display">
            <p>Start with: 1.1 × 2</p>
            <p>Convert to: 1. × 2. = 2.</p>
            <p>Estimate as: 1 × 2 = 2</p>
            <p>Result is: 2.2</p>
        </div>
    </div>

    <script>
        const selectMethod = document.getElementById('select-method');
        const sliderNum1 = document.getElementById('slider-num1');
        const btnIntYes = document.getElementById('btn-int-yes');
        const btnIntNo = document.getElementById('btn-int-no');
        const sliderNum2 = document.getElementById('slider-num2');
        const outputDisplay = document.getElementById('output-display');

        function updateDisplay() {
            const method = selectMethod.value;
            const num1 = parseFloat(sliderNum1.value);
            const num2 = parseFloat(sliderNum2.value);

            let outputContent = '';

            if (method === 'estimation') {
                const num1AsInt = Math.floor(num1 * 10);
                const num2AsInt = Math.floor(num2 * 10);
                const productOfInts = (num1AsInt / 10) * (num2AsInt / 10);
                const productOfRounded = Math.round(num1) * Math.round(num2);

                outputContent += `<p>Start with: ${num1} × ${num2}</p>`;
                outputContent += `<p>Convert to: ${num1AsInt / 10}. × ${num2AsInt / 10}. = ${productOfInts.toFixed(1)}</p>`;
                outputContent += `<p>Estimate as: ${Math.round(num1)} × ${Math.round(num2)} = ${productOfRounded}</p>`;
                outputContent += `<p>Result is: ${num1 * num2}</p>`;
            } else if (method === 'place values') {
                const num1AsInt = Math.floor(num1 * 10);
                const num2AsInt = Math.floor(num2 * 10);
                const num1Factor = num1 === Math.floor(num1) ? 1 : 10;
                const num2Factor = num2 === Math.floor(num2) ? 1 : 10;
                const productOfInts = num1AsInt * num2AsInt;
                const productOfFactors = num1Factor * num2Factor;

                outputContent += `<p>Start with: ${num1} × ${num2}</p>`;
                if (num1Factor !== 1 || num2Factor !== 1) {
                    outputContent += `<p>Rewrite as: (${num1AsInt / num1Factor} ÷ ${num1Factor}) × (${num2AsInt / num2Factor} ÷ ${num2Factor})</p>`;
                    outputContent += `<p>= (${num1AsInt / num1Factor} × ${num2AsInt / num2Factor}) ÷ (${num1Factor} × ${num2Factor})</p>`;
                }
                outputContent += `<p>= ${productOfInts / productOfFactors}</p>`;
                outputContent += `<p>= ${num1 * num2}</p>`;
            }

            outputDisplay.innerHTML = outputContent;
        }

        selectMethod.addEventListener('change', updateDisplay);
        sliderNum1.addEventListener('input', updateDisplay);
        btnIntYes.addEventListener('click', () => {
            sliderNum2.step = 1;
            sliderNum2.value = Math.round(sliderNum2.value);
            btnIntYes.classList.add('active');
            btnIntNo.classList.remove('active');
            updateDisplay();
        });
        btnIntNo.addEventListener('click', () => {
            sliderNum2.step = 0.1;
            btnIntYes.classList.remove('active');
            btnIntNo.classList.add('active');
            updateDisplay();
        });
        sliderNum2.addEventListener('input', updateDisplay);

        // Initial call to update display
        updateDisplay();
    </script>
</body>
</html>
