        }
        #label-left, #label-right {
            color: blue;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <select id="select-vibration-mode">
                <option value="symmetric-stretch" selected>symmetric stretch</option>
                <option value="asymmetric-stretch">stretch (triply degenerate)</option>
                <option value="bending-e">bend (doubly degenerate)</option>
                <option value="bending-f2">bend (triply degenerate)</option>
                <option value="ir-spectrum">IR spectrum</option>
            </select>
            <span>run</span>
            <button id="btn-play">►</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|◄</button>
        </div>
        <div id="visualization-area">
            <div id="three-js-canvas-container"></div>
            <div id="plotly-chart-container"></div>
        </div>
        <div id="description-area">
            <p id="label-left">stretching A₁ Raman active 2916 cm⁻¹</p>
            <p id="label-right">no change in the molecular dipole moment</p>
        </div>
    </div>

    <!-- Three.js and Plotly CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Three.js setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth / 2, window.innerHeight);
        document.getElementById('three-js-canvas-container').appendChild(renderer.domElement);

        // Additional camera for the second viewport
        const camera2 = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer2 = new THREE.WebGLRenderer();
        renderer2.setSize(window.innerWidth / 2, window.innerHeight);
        document.getElementById('three-js-canvas-container').appendChild(renderer2.domElement);

        // Orbit controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        const controls2 = new THREE.OrbitControls(camera2, renderer2.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        // Initial molecule setup
        const carbonMaterial = new THREE.MeshLambertMaterial({ color: 0x000000 });
        const hydrogenMaterial = new THREE.MeshLambertMaterial({ color: 0xadd8e6 });

        const carbon = new THREE.Mesh(new THREE.SphereGeometry(0.1, 32, 32), carbonMaterial);
        scene.add(carbon);

        const hydrogenPositions = [
            new THREE.Vector3(1, 1, 1),
            new THREE.Vector3(1, -1, -1),
            new THREE.Vector3(-1, 1, -1),
            new THREE.Vector3(-1, -1, 1)
        ];

        const hydrogens = hydrogenPositions.map(pos => {
            const hydrogen = new THREE.Mesh(new THREE.SphereGeometry(0.05, 32, 32), hydrogenMaterial);
            hydrogen.position.copy(pos);
            scene.add(hydrogen);
            return hydrogen;
        });

        const bonds = hydrogenPositions.map(pos => {
            const bond = new THREE.Mesh(new THREE.CylinderGeometry(0.01, 0.01, pos.length(), 32), new THREE.MeshBasicMaterial({ color: 0x808080 }));
            bond.position.copy(carbon.position);
            bond.lookAt(pos);
            scene.add(bond);
            return bond;
        });

        // Dipole vectors
        const vectorScene = new THREE.Scene();
        const vectorCamera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const vectorRenderer = new THREE.WebGLRenderer();
        vectorRenderer.setSize(window.innerWidth / 2, window.innerHeight);
        document.getElementById('three-js-canvas-container').appendChild(vectorRenderer.domElement);

        const vectors = hydrogenPositions.map(pos => {
            const vector = new THREE.ArrowHelper(new THREE.Vector3().copy(pos).normalize(), new THREE.Vector3(0, 0, 0), pos.length(), 0x0000ff);
            vectorScene.add(vector);
            return vector;
        });

        let netDipole = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 0), new THREE.Vector3(0, 0, 0), 0, 0xff0000);
        vectorScene.add(netDipole);

        // Plotly setup
        let plotData = null;
        let plotLayout = null;

        function renderPlot() {
            if (!plotData || !plotLayout) {
                const x = Array.from({ length: 3601 }, (_, i) => 400 + i);
                const baseline = 0.95;
                const noise = x.map(() => baseline + Math.random() * 0.05 - 0.025);
                const peak1 = x.map(xi => baseline - 0.8 * Math.exp(-Math.pow((xi - 1306) / 50, 2)));
                const peak2 = x.map(xi => baseline - 0.8 * Math.exp(-Math.pow((xi - 3019) / 50, 2)));

                plotData = [{
                    x: x,
                    y: noise.map((n, i) => n - peak1[i] - peak2[i]),
                    type: 'scatter',
                    mode: 'lines'
                }];

                plotLayout = {
                    xaxis: { title: 'wavenumber (cm⁻¹)', range: [400, 4000] },
                    yaxis: { title: 'Transmittance', range: [0, 1.1] },
                    annotations: [
                        { x: 1306, y: 0.1, text: 'ν F₂ 1306 cm⁻¹', showarrow: true, arrowhead: 1, ax: 0, ay: -20, font: { color: 'red' } },
                        { x: 3019, y: 0.1, text: 'ν F₂ 3019 cm⁻¹', showarrow: true, arrowhead: 1, ax: 0, ay: -20, font: { color: 'red' } }
                    ],
                    images: [
                        // Add images here
                    ]
                };

                Plotly.newPlot('plotly-chart-container', plotData, plotLayout);
            }
        }

        // Animation variables
        let animationFrameId = null;
        let t = 0;
        const frequency = 10; // Hz
        const amplitude = 0.2;

        function animate() {
            t += 0.01;
            const mode = document.getElementById('select-vibration-mode').value;
            switch (mode) {
                case 'symmetric-stretch':
                    hydrogens.forEach(h => {
                        h.position.copy(hydrogenPositions.find(p => p.equals(h.position)).clone().setLength(1 + amplitude * Math.sin(frequency * t)));
                    });
                    break;
                case 'asymmetric-stretch':
                    hydrogens[0].position.setLength(1 + amplitude * Math.sin(frequency * t));
                    hydrogens[1].position.setLength(1 - amplitude * Math.sin(frequency * t));
                    break;
                case 'bending-e':
                    hydrogens.forEach((h, i) => {
                        h.position.applyAxisAngle(new THREE.Vector3(0, 1, 0), amplitude * Math.sin(frequency * t));
                    });
                    break;
                case 'bending-f2':
                    hydrogens.forEach((h, i) => {
                        h.position.applyAxisAngle(new THREE.Vector3(0, 1, 0), amplitude * Math.sin(frequency * t));
                    });
                    break;
            }

            bonds.forEach((bond, i) => {
                bond.position.copy(carbon.position);
                bond.lookAt(hydrogens[i].position);
            });

            vectors.forEach((vector, i) => {
                vector.setDirection(hydrogens[i].position.clone().sub(carbon.position).normalize());
                vector.setLength(hydrogens[i].position.distanceTo(carbon.position));
            });

            netDipole.setDirection(new THREE.Vector3().addVectors(...vectors.map(v => v.direction)).normalize());
            netDipole.setLength(Math.max(0.1, vectors.reduce((sum, v) => sum + v.length, 0)));

            renderer.render(scene, camera);
            renderer2.render(vectorScene, vectorCamera);
            animationFrameId = requestAnimationFrame(animate);
        }

        // Event listeners
        document.getElementById('select-vibration-mode').addEventListener('change', () => {
            const mode = document.getElementById('select-vibration-mode').value;
            if (mode === 'ir-spectrum') {
                document.getElementById('three-js-canvas-container').style.display = 'none';
                document.getElementById('description-area').style.display = 'none';
                document.getElementById('plotly-chart-container').style.display = 'block';
                document.getElementById('btn-play').disabled = true;
                document.getElementById('btn-pause').disabled = true;
                document.getElementById('btn-reset').disabled = true;
                renderPlot();
            } else {
                document.getElementById('three-js-canvas-container').style.display = 'block';
                document.getElementById('description-area').style.display = 'block';
                document.getElementById('plotly-chart-container').style.display = 'none';
                document.getElementById('btn-play').disabled = false;
                document.getElementById('btn-pause').disabled = false;
                document.getElementById('btn-reset').disabled = false;
                cancelAnimationFrame(animationFrameId);
                t = 0;
                animate();
            }

            updateLabels(mode);
        });

        document.getElementById('btn-play').addEventListener('click', () => {
            if (!animationFrameId) {
                animate();
            }
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            cancelAnimationFrame(animationFrameId);
            animationFrameId = null;
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            cancelAnimationFrame(animationFrameId);
            animationFrameId = null;
            t = 0;
            animate();
        });

        // Update labels based on mode
        function updateLabels(mode) {
            const labels = {
                'symmetric-stretch': {
                    left: 'stretching A₁ Raman active 2916 cm⁻¹',
                    right: 'no change in the molecular dipole moment'
                },
                'asymmetric-stretch': {
                    left: 'stretching F₂ IR active 3019 cm⁻¹',
                    right: 'change in the molecular dipole moment'
                },
                'bending-e': {
                    left: 'bending E Raman active 1534 cm⁻¹',
                    right: 'no change in the molecular dipole moment'
                },
                'bending-f2': {
                    left: 'bending F₂ IR active 1306 cm⁻¹',
                    right: 'change in the molecular dipole moment'
                }
            };

            const labelData = labels[mode] || labels['symmetric-stretch'];
            document.getElementById('label-left').textContent = labelData.left;
            document.getElementById('label-right').textContent = labelData.right;
        }

        // Initial setup
        animate();
        updateLabels('symmetric-stretch');
    </script>
</body>
</html>
