            width: 30px;
            height: 30px;
            border-radius: 50%;
            background-color: #4CAF50;
            color: white;
            border: none;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 16px;
        }
        #canvas-material-bar {
            border: 1px solid #000;
        }
        #div-plot {
            width: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time (seconds)</label>
            <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
            <span id="span-time-value">9.8</span>
            <button id="btn-reset">+</button>
        </div>
        <canvas id="canvas-material-bar" width="600" height="80"></canvas>
        <div id="div-plot"></div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        const canvas = document.getElementById('canvas-material-bar');
        const ctx = canvas.getContext('2d');
        const sliderTime = document.getElementById('slider-time');
        const spanTimeValue = document.getElementById('span-time-value');
        const btnReset = document.getElementById('btn-reset');
        const divPlot = document.getElementById('div-plot');

        function stefanDistance(t) {
            return 0.155 * Math.sqrt(t);
        }

        function drawMaterialBar(t) {
            const s = stefanDistance(t);
            const scale = canvas.width / 16;

            // Clear canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Glass block
            const glassGradient = ctx.createLinearGradient(0, 0, 6 * scale, 0);
            glassGradient.addColorStop(0, '#FF0000');
            glassGradient.addColorStop(1, '#FFFFFF');
            ctx.fillStyle = glassGradient;
            ctx.fillRect(0, 0, 6 * scale, canvas.height);

            // Melted water
            ctx.fillStyle = '#00BFFF';
            ctx.fillRect(6 * scale, 0, s * scale, canvas.height);

            // Ice block
            ctx.fillStyle = '#003366';
            ctx.fillRect((6 + s) * scale, 0, (16 - 6 - s) * scale, canvas.height);

            // Axis
            ctx.strokeStyle = '#000';
            ctx.beginPath();
            ctx.moveTo(0, canvas.height);
            ctx.lineTo(canvas.width, canvas.height);
            ctx.stroke();

            // Labels
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';
            ctx.fillText('0', 0, canvas.height + 20);
            ctx.fillText('5', 5 * scale, canvas.height + 20);
            ctx.fillText('10', 10 * scale, canvas.height + 20);
            ctx.fillText('15', 15 * scale, canvas.height + 20);
        }

        function updatePlot(t) {
            const s = stefanDistance(t);
            Plotly.relayout(divPlot, {
                shapes: [{
                    type: 'line',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: s,
                    line: {
                        color: '#000000',
                        width: 2
                    }
                }]
            });
        }

        function updateVisualization(t) {
            spanTimeValue.textContent = t.toFixed(1);
            drawMaterialBar(t);
            updatePlot(t);
        }

        function initializePlot() {
            const xValues = Array.from({ length: 101 }, (_, i) => i);
            const yValues = xValues.map(t => stefanDistance(t));

            const trace = {
                x: xValues,
                y: yValues,
                mode: 'lines',
                line: {
                    color: '#FFA500'
                }
            };

            const layout = {
                title: 'Stefan distance versus time',
                xaxis: {
                    title: 'time (s)',
                    range: [0, 100]
                },
                yaxis: {
                    title: 'distance (mm)',
                    range: [0, 1.6]
                },
                shapes: [{
                    type: 'line',
                    x0: 9.8,
                    y0: 0,
                    x1: 9.8,
                    y1: stefanDistance(9.8),
                    line: {
                        color: '#000000',
                        width: 2
                    }
                }]
            };

            Plotly.newPlot(divPlot, [trace], layout);
        }

        function initialize() {
            sliderTime.value = 9.8;
            initializePlot();
            updateVisualization(9.8);
        }

        sliderTime.addEventListener('input', () => {
            updateVisualization(parseFloat(sliderTime.value));
        });

        btnReset.addEventListener('click', () => {
            sliderTime.value = 0;
            sliderTime.dispatchEvent(new Event('input'));
        });

        initialize();
    </script>
</body>
</html>
