        function drawClock(timeObject, faceColor, p) {
            p.background(255);
            p.strokeWeight(2);
            p.fill(0);
            p.circle(p.width / 2, p.height / 2, p.min(p.width, p.height) - 10);

            p.fill(faceColor);
