                p.line(p.width / 2, p.height / 2,
                       p.width / 2 + (p.min(p.width, p.height) - 50) * Math.cos(Math.PI / 30 * i),
                       p.height / 2 + (p.min(p.width, p.height) - 50) * Math.sin(Math.PI / 30 * i));
            }

            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(16);
            for (let i = 1; i <= 12; i++) {
                p.text(i.toString(), p.width / 2 + (p.min(p.width, p.height) - 60) * Math.cos(Math.PI / 6 * i),
                         p.height / 2 + (p.min(p.width, p.height) - 60) * Math.sin(Math.PI / 6 * i));
            }

            const hourAngle = (timeObject.getHours() % 12 + timeObject.getMinutes() / 60) * Math.PI / 6;
            const minuteAngle = (timeObject.getMinutes() + timeObject.getSeconds() / 60) * Math.PI / 30;
            const secondAngle = timeObject.getSeconds() * Math.PI / 30;

            p.strokeWeight(4);
            p.line(p.width / 2, p.height / 2,
                   p.width / 2 + (p.min(p.width, p.height) / 2) * Math.cos(hourAngle),
                   p.height / 2 + (p.min(p.width, p.height) / 2) * Math.sin(hourAngle));

            p.strokeWeight(2);
            p.line(p.width / 2, p.height / 2,
                   p.width / 2 + (p.min(p.width, p.height) / 1.5) * Math.cos(minuteAngle),
                   p.height / 2 + (p.min(p.width, p.height) / 1.5) * Math.sin(minuteAngle));

            p.strokeWeight(1);
            p.stroke(255, 0, 0);
            p.line(p.width / 2, p.height / 2,
                   p.width / 2 + (p.min(p.width, p.height) / 1.2) * Math.cos(secondAngle),
                   p.height / 2 + (p.min(p.width, p.height) / 1.2) * Math.sin(secondAngle));

            p.fill(0);
            p.noStroke();
            p.circle(p.width / 2, p.height / 2, 5);
        }

        function calculateSolarTime() {
            const d = parseInt(document.getElementById('date-slider').value);
            const B = (360 / 365.24) * (d - 81);
            const EoT = 9.87 * Math.sin(2 * B * Math.PI / 180) - 7.53 * Math.cos(B * Math.PI / 180) - 1.5 * Math.sin(B * Math.PI / 180);
            const localTime = new Date();
            const UTCTime = localTime.getTime() + localTime.getTimezoneOffset() * 60000;
            const TimeOffsetMinutes = EoT + 4 * userLongitude;
            const SolarTimeMilliseconds = UTCTime + TimeOffsetMinutes * 60000;
            return new Date(SolarTimeMilliseconds);
        }

        function updateClocks() {
            const localTime = new Date();
            document.getElementById('local-time-digital').textContent = localTime.toLocaleTimeString();

            const solarTime = calculateSolarTime();
            document.getElementById('solar-time-digital').textContent = solarTime.toLocaleTimeString();

            const offsetMinutes = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
            document.getElementById('offset-display').textContent = `offset of solar time from local time: ${offsetMinutes} minutes`;
        }

        function updateDateDisplay() {
            const d = parseInt(document.getElementById('date-slider').value);
            const date = new Date(currentDate.getFullYear(), 0, 1);
            date.setDate(date.getDate() + d);
            document.getElementById('date-display').textContent = date.toLocaleDateString('en-US', { weekday: 'long', day: 'numeric', month: 'long' });
        }

        function setToday() {
            const today = new Date();
            currentDayOfYear = Math.floor((today - new Date(today.getFullYear(), 0, 0)) / 86400000);
            document.getElementById('date-slider').value = currentDayOfYear;
            updateDateDisplay();
        }

        window.addEventListener('load', setup);
    </script>
</body>
</html>
