<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Gosper Tiling Visualization</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #demo-container {
            width: 80%;
            max-width: 1200px;
            display: flex;
            flex-direction: column;
        }
        #control-panel {
            display: flex;
            justify-content: space-around;
            margin-bottom: 20px;
        }
        #control-panel div {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #canvas-container {
            border: 1px solid #ccc;
        }
    </style>
    <!-- p5.js library from CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">
        <!-- Control panel section -->
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" value="2" min="0" max="6" step="1">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow">DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <!-- Visualization canvas container -->
        <div id="canvas-container"></div>
    </div>

    <script>
        let steps = 2;
        let zoom = false;
        let colorScheme = 'DarkRainbow';
        let palette = ['#1E4363', '#2E6341'];

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            canvas.parent(canvasContainer);
            angleMode(DEGREES);
            noLoop();

            document.getElementById('input-steps').addEventListener('change', (event) => {
                steps = parseInt(event.target.value);
                redraw();
            });

            document.getElementById('checkbox-zoom').addEventListener('change', (event) => {
                zoom = event.target.checked;
                redraw();
            });

            document.getElementById('select-color').addEventListener('change', (event) => {
                colorScheme = event.target.value;
                switch (colorScheme) {
                    case 'DarkRainbow':
                        palette = ['#1E4363', '#2E6341'];
                        break;
                    case 'SiennaTones':
                        palette = ['#A0522D', '#CD853F', '#D2B48C'];
                        break;
                    case 'RustTones':
                        palette = ['#B7410E', '#000033', '#FFFFFF'];
                        break;
                    case 'FuchsiaTones':
                        palette = ['#C71585', '#000000', '#FFFFFF'];
                        break;
                    case 'Grays':
                        palette = ['#404040', '#808080', '#C0C0C0'];
                        break;
                }
                redraw();
            });
        }

        function draw() {
            clear();
            const centerX = width / 2;
            const centerY = height / 2;
            const baseSize = 200;

            translate(centerX, centerY);

            if (zoom) {
                scale(Math.pow(Math.sqrt(7), steps));
            } else {
                scale(baseSize / (Math.pow(Math.sqrt(7), steps) * 2));
            }

            drawTiling(steps, 0, 0, 1, 0);
        }

        function drawTiling(level, centerX, centerY, size, angleRad) {
            if (level === 0) {
                const angle = angleRad + 30;
                const halfSize = size / 2;
                const vertices = [
                    { x: centerX - halfSize * cos(angle), y: centerY - halfSize * sin(angle) },
                    { x: centerX + halfSize * cos(angle), y: centerY - halfSize * sin(angle) },
                    { x: centerX + halfSize * cos(angle + 120), y: centerY + halfSize * sin(angle + 120) },
                    { x: centerX - halfSize * cos(angle + 120), y: centerY + halfSize * sin(angle + 120) }
                ];

                fill(palette[Math.floor(angle / 120) % palette.length]);
                beginShape();
                for (let vertex of vertices) {
                    vertex(vertex.x, vertex.y);
                }
                endShape(CLOSE);

                // Optional: Draw faint diagonal lines
                stroke(255, 100);
                line(vertices[0].x, vertices[0].y, vertices[2].x, vertices[2].y);
                line(vertices[1].x, vertices[1].y, vertices[3].x, vertices[3].y);
                noStroke();
            } else {
                const subSize = size / Math.sqrt(7);
                const subAngle = atan(sqrt(3) / 5);

                const subCenters = [
                    { x: centerX, y: centerY - subSize * cos(subAngle) },
                    { x: centerX + subSize * sin(subAngle), y: centerY - subSize * cos(subAngle) },
                    { x: centerX + subSize * sin(subAngle), y: centerY + subSize * cos(subAngle) },
                    { x: centerX, y: centerY + subSize * cos(subAngle) },
                    { x: centerX - subSize * sin(subAngle), y: centerY + subSize * cos(subAngle) },
                    { x: centerX - subSize * sin(subAngle), y: centerY - subSize * cos(subAngle) },
                    { x: centerX, y: centerY }
                ];

                for (let i = 0; i < subCenters.length; i++) {
                    const subCenter = subCenters[i];
                    const subAngleRad = subAngle + i * 60;
                    drawTiling(level - 1, subCenter.x, subCenter.y, subSize, subAngleRad);
                }
            }
        }
    </script>
</body>
</html>
