    <script>
        const magnetOrientations = [3, 2, 4, 5, 0];
        const orientationSymbols = ['→', '←', '↑', '↓', '⊙', '⊗'];
        let plotType = 'stream';
        let strength = 5;

        function setup() {
            createCanvas(800, 600).parent('visualization-container');
            updateOrientationDisplays();
            redraw();
        }

        function draw() {
            clear();
            translate(width / 2, height / 2);
            drawMagnets();
            if (plotType === 'stream') {
                drawStreamPlot();
            } else if (plotType === 'density') {
                drawDensityPlot();
            }
        }

        function drawMagnets() {
            const magnetSize = 50; // pixels
            const magnetSpacing = 70; // pixels
            fill(150);
            stroke(0);
            for (let i = 0; i < 5; i++) {
                push();
                translate((i - 2) * magnetSpacing, 0);
                quad(-magnetSize / 2, -magnetSize / 2, magnetSize / 2, -magnetSize / 2,
                     magnetSize / 2, magnetSize / 2, -magnetSize / 2, magnetSize / 2);
                line(-magnetSize / 2, -magnetSize / 2, magnetSize / 2, magnetSize / 2);
                line(-magnetSize / 2, magnetSize / 2, magnetSize / 2, -magnetSize / 2);
                pop();

                push();
                translate((i - 2) * magnetSpacing, 0);
                fill(0);
                textSize(14);
                textAlign(CENTER, CENTER);
                text(orientationSymbols[magnetOrientations[i]], 0, 0);
                pop();
            }
        }

        function drawStreamPlot() {
            const numSteps = 20;
            const stepSize = 10;
            const gridSize = 20;
            for (let x = -width / 2; x < width / 2; x += gridSize) {
                for (let y = -height / 2; y < height / 2; y += gridSize) {
                    let px = x;
                    let py = y;
                    stroke(0, 0, 255);
                    for (let i = 0; i < numSteps; i++) {
                        let [bx, by] = calculateField(px, py);
                        let angle = atan2(by, bx);
                        line(px, py, px + stepSize * cos(angle), py + stepSize * sin(angle));
                        px += stepSize * cos(angle);
                        py += stepSize * sin(angle);
                        if (px < -width / 2 || px > width / 2 || py < -height / 2 || py > height / 2) break;
                    }
                }
            }
        }

        function drawDensityPlot() {
            loadPixels();
            const gridSize = 10;
            let minMag = Infinity;
            let maxMag = -Infinity;
            for (let x = 0; x < width; x += gridSize) {
                for (let y = 0; y < height; y += gridSize) {
                    let [bx, by] = calculateField(x - width / 2, y - height / 2);
                    let mag = sqrt(bx * bx + by * by);
                    minMag = min(minMag, mag);
                    maxMag = max(maxMag, mag);
                }
            }

            for (let x = 0; x < width; x += gridSize) {
                for (let y = 0; y < height; y += gridSize) {
                    let [bx, by] = calculateField(x - width / 2, y - height / 2);
                    let mag = sqrt(bx * bx + by * by);
                    let normMag = map(mag, minMag, maxMag, 0, 1);
                    let color = lerpColor(color(255, 255, 200), color(0, 0, 255), normMag);
                    for (let i = 0; i < gridSize; i++) {
                        for (let j = 0; j < gridSize; j++) {
                            set(x + i, y + j, color);
                        }
                    }
                }
            }
            updatePixels();
        }

        function calculateField(x, y) {
            const mu0 = 1; // constant of proportionality
            let bx = 0, by = 0;
            const magnetSpacing = 70; // pixels
            const magnetSize = 50; // pixels
            for (let i = 0; i < 5; i++) {
                let rix = (i - 2) * magnetSpacing;
                let ri = createVector(rix, 0, 0);
                let pi = createVector(x, y, 0);
                let r = pi.copy().sub(ri);
                let rmag = r.mag();
                let mi = createVector(0, 0, 0);
                switch (magnetOrientations[i]) {
                    case 0: mi = createVector(1, 0, 0); break;
                    case 1: mi = createVector(-1, 0, 0); break;
                    case 2: mi = createVector(0, 1, 0); break;
                    case 3: mi = createVector(0, -1, 0); break;
                    case 4: mi = createVector(0, 0, 1); break;
                    case 5: mi = createVector(0, 0, -1); break;
                }
                mi.mult(strength);

                let b = mi.cross(r).mult(mu0 / (rmag * rmag * rmag));
                bx += b.x;
                by += b.y;
            }
            return [bx, by];
        }

        function updateOrientationDisplays() {
            for (let i = 0; i < 5; i++) {
                document.getElementById(`magnet-orientation-display-${i}`).textContent = orientationSymbols[magnetOrientations[i]];
            }
        }

        document.querySelectorAll('.magnet-control button').forEach(button => {
            button.addEventListener('click', () => {
                const index = parseInt(button.id.split('-')[3]);
                const increment = button.textContent === '🔼' ? 1 : -1;
                magnetOrientations[index] = (magnetOrientations[index] + increment + 6) % 6;
                updateOrientationDisplays();
                redraw();
            });
        });

        document.getElementById('slider-strength').addEventListener('input', (event) => {
            strength = parseInt(event.target.value);
            document.getElementById('slider-strength-value').textContent = strength;
            redraw();
        });

        document.getElementById('btn-stream-plot').addEventListener('click', () => {
            plotType = 'stream';
            document.getElementById('btn-stream-plot').classList.add('active');
            document.getElementById('btn-density-plot').classList.remove('active');
            redraw();
        });

        document.getElementById('btn-density-plot').addEventListener('click', () => {
            plotType = 'density';
            document.getElementById('btn-stream-plot').classList.remove('active');
            document.getElementById('btn-density-plot').classList.add('active');
            redraw();
        });
    </script>
</body>
</html>
