            { xA: 0.1, xB: 0.6, xC: 0.3 },
            { xA: 0.0, xB: 0.7, xC: 0.3 }
        ];

        const tieLines = [
            { alpha: { xA: 0.3, xB: 0.4, xC: 0.3 }, beta: { xA: 0.2, xB: 0.5, xC: 0.3 } },
            { alpha: { xA: 0.2, xB: 0.5, xC: 0.3 }, beta: { xA: 0.1, xB: 0.6, xC: 0.3 } },
            { alpha: { xA: 0.1, xB: 0.6, xC: 0.3 }, beta: { xA: 0.0, xB: 0.7, xC: 0.3 } }
        ];

