    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <script>
        // Seedable PRNG
        function SeededRandom(seed) {
            this.seed = seed;
        }

        SeededRandom.prototype.next = function() {
            this.seed = (this.seed * 1664525 + 1013904223) >>> 0;
            return this.seed / 0xFFFFFFFF;
        };

        // Helper function to generate random points
        function generatePoints(N, seed) {
            const rng = new SeededRandom(seed);
            const points = [];
            for (let i = 0; i < N; i++) {
                points.push([rng.next(), rng.next()]);
            }
            return points;
        }

        // Nearest Neighbor heuristic
        function nearestNeighbor(points) {
            const N = points.length;
            const visited = new Array(N).fill(false);
            const tour = [];
            let current = 0;
            tour.push(current);
            visited[current] = true;

            while (tour.length < N) {
                let next = -1;
                let minDist = Infinity;
                for (let i = 0; i < N; i++) {
                    if (!visited[i]) {
                        const dist = distance(points[current], points[i]);
                        if (dist < minDist) {
                            minDist = dist;
                            next = i;
                        }
                    }
                }
                tour.push(next);
                visited[next] = true;
                current = next;
            }

            tour.push(tour[0]); // Close the tour
            return tour;
        }

        // Distance function
        function distance(a, b) {
            return Math.sqrt((a[0] - b[0]) ** 2 + (a[1] - b[1]) ** 2);
        }

        // 2-Opt algorithm
        function solveTwoOpt(points, tour) {
            const N = points.length;
            let improved = true;
            while (improved) {
                improved = false;
                for (let i = 1; i < N - 2; i++) {
                    for (let k = i + 2; k < N - 1; k++) {
                        if (distance(points[tour[i]], points[tour[k + 1]]) + distance(points[tour[k]], points[tour[i + 1]]) <
                            distance(points[tour[i]], points[tour[i + 1]]) + distance(points[tour[k]], points[tour[k + 1]])) {
                            reverseSubarray(tour, i + 1, k);
                            improved = true;
                        }
                    }
                }
            }
            return tour;
        }

        // Reverse subarray in place
        function reverseSubarray(arr, start, end) {
            while (start < end) {
                [arr[start], arr[end]] = [arr[end], arr[start]];
                start++;
                end--;
            }
        }

        // 3-Opt algorithm (simplified)
        function solve3Opt(points, tour) {
            const N = points.length;
            let improved = true;
            while (improved) {
                improved = false;
                for (let i = 1; i < N - 2; i++) {
                    for (let j = i + 1; j < N - 1; j++) {
                        for (let k = j + 1; k < N; k++) {
                            const d1 = distance(points[tour[i]], points[tour[j]]);
                            const d2 = distance(points[tour[j]], points[tour[k]]);
                            const d3 = distance(points[tour[k]], points[tour[i]]);
                            const d4 = distance(points[tour[i]], points[tour[k]]);
                            const d5 = distance(points[tour[j]], points[tour[i]]);
                            const d6 = distance(points[tour[k]], points[tour[j]]);

                            if (d1 + d2 + d3 > d4 + d5 + d6) {
                                reverseSubarray(tour, i + 1, j);
                                reverseSubarray(tour, j + 1, k);
                                improved = true;
                            }
                        }
                    }
                }
            }
            return tour;
        }

        // Or-Opt algorithm (simplified)
        function solveOrOpt(points, tour) {
            const N = points.length;
            let improved = true;
            while (improved) {
                improved = false;
                for (let i = 1; i < N - 1; i++) {
                    for (let j = 1; j < N - 1; j++) {
                        if (i !== j) {
                            const d1 = distance(points[tour[i]], points[tour[i + 1]]);
                            const d2 = distance(points[tour[j]], points[tour[j + 1]]);
                            const d3 = distance(points[tour[i]], points[tour[j + 1]]);
                            const d4 = distance(points[tour[j]], points[tour[i + 1]]);

                            if (d1 + d2 > d3 + d4) {
                                const removed = tour.splice(i + 1, 1);
                                tour.splice(j + 1, 0, removed[0]);
                                improved = true;
                            }
                        }
                    }
                }
            }
            return tour;
        }

        // Christofides algorithm (simplified)
        function solveCCA(points, tour) {
            // Placeholder for Christofides algorithm implementation
            return tour;
        }

        // OrZweig algorithm (simplified as 2-Opt)
        function solveOrZweig(points, tour) {
            return solveTwoOpt(points, tour);
        }

        // P5.js setup
        let leftSketch, rightSketch;
        let points, tourOrZweig, tourOrOpt, tourTwoOpt, tourCCA, tour3Opt;

        function setup() {
            leftSketch = new p5(sketch => {
                sketch.setup = () => {
                    sketch.createCanvas(350, 300);
                };
                sketch.draw = () => {
                    sketch.background(255);
                    drawPlot(sketch, points, tourOrZweig, '#0072B2');
                };
            }, 'canvas-left-wrapper');

            rightSketch = new p5(sketch => {
                sketch.setup = () => {
                    sketch.createCanvas(350, 300);
                };
                sketch.draw = () => {
                    sketch.background(255);
                    drawPlot(sketch, points, tour3Opt, '#E69F00');
                };
            }, 'canvas-right-wrapper');
        }

        function drawPlot(sketch, points, tour, color) {
            const margin = 20;
            const scaleX = (sketch.width - 2 * margin) / 1;
            const scaleY = (sketch.height - 2 * margin) / 1;

            sketch.stroke(0);
            sketch.textSize(12);
            for (let i = 0; i <= 1; i += 0.2) {
                sketch.line(margin, margin + (1 - i) * scaleY, margin + scaleX, margin + (1 - i) * scaleY);
                sketch.line(margin + i * scaleX, margin, margin + i * scaleX, margin + scaleY);
                sketch.text(i.toFixed(1), margin + i * scaleX + 5, margin + scaleY + 15);
            }

            sketch.fill('red');
            sketch.noStroke();
            points.forEach(point => {
                sketch.ellipse(margin + point[0] * scaleX, margin + (1 - point[1]) * scaleY, 5);
            });

            sketch.stroke(color);
            sketch.strokeWeight(2);
            sketch.noFill();
            for (let i = 0; i < tour.length - 1; i++) {
                const p1 = points[tour[i]];
                const p2 = points[tour[i + 1]];
                sketch.line(margin + p1[0] * scaleX, margin + (1 - p1[1]) * scaleY, margin + p2[0] * scaleX, margin + (1 - p2[1]) * scaleY);
            }
        }

        // Main calculation and rendering process
        function calculateAndRender() {
            const seed = parseInt(document.getElementById('seed-input').value);
            const N = parseInt(document.getElementById('slider-points').value);
            points = generatePoints(N, seed);
            const initialTour = nearestNeighbor(points);

            const startTimeOrZweig = performance.now();
            tourOrZweig = solveOrZweig(points, [...initialTour]);
            const endTimeOrZweig = performance.now();
            const timingOrZweig = (endTimeOrZweig - startTimeOrZweig) / 1000;
            document.getElementById('timing-orzweig').textContent = timingOrZweig.toFixed(6);
            document.getElementById('result-orzweig').textContent = tourLength(points, tourOrZweig).toFixed(6);

            const startTimeOrOpt = performance.now();
            tourOrOpt = solveOrOpt(points, [...initialTour]);
            const endTimeOrOpt = performance.now();
            const timingOrOpt = (endTimeOrOpt - startTimeOrOpt) / 1000;
            document.getElementById('timing-oropt').textContent = timingOrOpt.toFixed(6);
            document.getElementById('result-oropt').textContent = tourLength(points, tourOrOpt).toFixed(6);

            const startTimeTwoOpt = performance.now();
            tourTwoOpt = solveTwoOpt(points, [...initialTour]);
            const endTimeTwoOpt = performance.now();
            const timingTwoOpt = (endTimeTwoOpt - startTimeTwoOpt) / 1000;
            document.getElementById('timing-twoopt').textContent = timingTwoOpt.toFixed(6);
            document.getElementById('result-twoopt').textContent = tourLength(points, tourTwoOpt).toFixed(6);

            const startTimeCCA = performance.now();
            tourCCA = solveCCA(points, [...initialTour]);
            const endTimeCCA = performance.now();
            const timingCCA = (endTimeCCA - startTimeCCA) / 1000;
            document.getElementById('timing-cca').textContent = timingCCA.toFixed(6);
            document.getElementById('result-cca').textContent = tourLength(points, tourCCA).toFixed(6);

            const startTime3Opt = performance.now();
            tour3Opt = solve3Opt(points, [...initialTour]);
            const endTime3Opt = performance.now();
            const timing3Opt = (endTime3Opt - startTime3Opt) / 1000;
            document.getElementById('timing-3opt').textContent = timing3Opt.toFixed(6);
            document.getElementById('result-3opt').textContent = tourLength(points, tour3Opt).toFixed(6);

            const minMathResult = Math.min(
                parseFloat(document.getElementById('result-orzweig').textContent),
                parseFloat(document.getElementById('result-oropt').textContent),
                parseFloat(document.getElementById('result-twoopt').textContent),
                parseFloat(document.getElementById('result-cca').textContent)
            );
            const result3Opt = parseFloat(document.getElementById('result-3opt').textContent);

            if (result3Opt < minMathResult) {
                document.getElementById('winner-display').textContent = '3-Opt wins';
            } else if (minMathResult < result3Opt) {
                document.getElementById('winner-display').textContent = 'Mathematica wins';
            } else {
                document.getElementById('winner-display').textContent = 'Tie';
            }

            leftSketch.redraw();
            rightSketch.redraw();
        }

        // Tour length calculation
        function tourLength(points, tour) {
            let length = 0;
            for (let i = 0; i < tour.length - 1; i++) {
                length += distance(points[tour[i]], points[tour[i + 1]]);
            }
            return length;
        }

        // Event listeners
        document.getElementById('btn-random-set').addEventListener('click', () => {
            const seed = Math.floor(Math.random() * 99999) + 1;
            document.getElementById('seed-input').value = seed;
            calculateAndRender();
        });

        document.getElementById('slider-points').addEventListener('change', calculateAndRender);

        document.getElementById('btn-reset-seed').addEventListener('click', () => {
            document.getElementById('seed-input').value = 0;
            calculateAndRender();
        });

        document.querySelectorAll('input[name="math-method"]').forEach(radio => {
            radio.addEventListener('change', () => {
                document.getElementById('left-plot-title').textContent = radio.value;
                leftSketch.redraw();
            });
        });

        // Initial calculation and rendering
        calculateAndRender();
    </script>
