            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            background-color: #f0f0f0;
        }
        #main-container {
            border: 2px solid #333;
            padding: 20px;
            display: flex;
            gap: 20px;
        }
        #control-panel {
            width: 200px;
        }
        #control-panel div {
            margin-bottom: 10px;
        }
        #visualization-area {
            flex-grow: 1;
        }
        #canvas-container {
            position: relative;
            width: 512px;
            height: 512px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <!-- Include p5.js from CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <!-- Main container -->
    <div id="main-container">

        <!-- Left Column: Control Panel -->
        <div id="control-panel">
            <!-- Initialization Slider -->
            <div>
                <label for="slider-initialization">initialization</label>
                <input type="range" id="slider-initialization" min="0" max="65535" step="1" value="462">
                <span id="value-initialization">+ 462</span>
            </div>

            <!-- Max Steps Slider -->
            <div>
                <label for="slider-max-steps">max steps</label>
                <input type="range" id="slider-max-steps" min="1" max="50" step="1" value="9">
                <span id="value-max-steps">+ 9</span>
            </div>

            <!-- Checkboxes -->
            <div>
                <input type="checkbox" id="checkbox-cycle4">
                <label for="checkbox-cycle4">length 4 cycle</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-cycle2">
                <label for="checkbox-cycle2">length 2 cycles</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-cycle1">
                <label for="checkbox-cycle1">length 1 cycle points</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-all-points">
                <label for="checkbox-all-points">all map points</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-modify">
                <label for="checkbox-modify">modify initialization</label>
            </div>
        </div>

        <!-- Right Column: Visualization Area -->
        <div id="visualization-area">
            <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
            <div id="canvas-container"></div> <!-- p5.js will attach canvas here -->
        </div>

    </div>

    <script>
        let canvas;
        let initializationValue = 462;
        let maxSteps = 9;
        let cycle4Checked = false;
        let cycle2Checked = false;
        let cycle1Checked = false;
        let allPointsChecked = false;
        let modifyChecked = false;

        function setup() {
            canvas = createCanvas(512, 512);
            canvas.parent('canvas-container');
            noLoop();
            updateSimulation();
        }

        function draw() {
            background('#e0f0e0');

            if (allPointsChecked) {
                for (let n = 0; n <= 65535; n++) {
                    let nNext = f(n);
                    let x = (nNext & 255) * (width / 256);
                    let y = ((nNext >> 8) & 255) * (height / 256);
                    fill('#808080');
                    noStroke();
                    ellipse(x, y, 2, 2);
                }
            }

            let path = generatePath(initializationValue, maxSteps);
            for (let i = 0; i < path.length - 1; i++) {
                let x1 = (path[i] & 255) * (width / 256);
                let y1 = ((path[i] >> 8) & 255) * (height / 256);
                let x2 = (path[i + 1] & 255) * (width / 256);
                let y2 = ((path[i + 1] >> 8) & 255) * (height / 256);
                stroke('#800080');
                strokeWeight(2);
                line(x1, y1, x2, y2);
            }
            fill('#FF0000');
            noStroke();
            ellipse((path[0] & 255) * (width / 256), ((path[0] >> 8) & 255) * (height / 256), 5, 5);
            fill('#0000FF');
            noStroke();
            ellipse((path[path.length - 1] & 255) * (width / 256), ((path[path.length - 1] >> 8) & 255) * (height / 256), 5, 5);

            if (cycle1Checked) {
                let fixedPoints = findFixedPoints();
                for (let n of fixedPoints) {
                    let x = (n & 255) * (width / 256);
                    let y = ((n >> 8) & 255) * (height / 256);
                    fill('#00FFFF');
                    noStroke();
                    ellipse(x, y, 4, 4);
                }
            }

            if (cycle2Checked) {
                let cyclePairs = [
                    [10922, 21845], [13107, 26214], [13653, 27306], [15339, 30678],
                    [21844, 43691], [26213, 52427], [27305, 54611], [30677, 61355],
                    [43690, 54613]
                ];
                for (let [n1, n2] of cyclePairs) {
                    let x1 = (n1 & 255) * (width / 256);
                    let y1 = ((n1 >> 8) & 255) * (height / 256);
                    let x2 = (n2 & 255) * (width / 256);
                    let y2 = ((n2 >> 8) & 255) * (height / 256);
                    stroke('#008000');
                    strokeWeight(2);
                    line(x1, y1, x2, y2);
                }
            }

            if (cycle4Checked) {
                let cyclePoints = [43690, 54612, 32768, 49152];
                for (let i = 0; i < cyclePoints.length; i++) {
                    let x1 = (cyclePoints[i] & 255) * (width / 256);
                    let y1 = ((cyclePoints[i] >> 8) & 255) * (height / 256);
                    let x2 = (cyclePoints[(i + 1) % cyclePoints.length] & 255) * (width / 256);
                    let y2 = ((cyclePoints[(i + 1) % cyclePoints.length] >> 8) & 255) * (height / 256);
                    stroke('#008000');
                    strokeWeight(2);
                    line(x1, y1, x2, y2);
                }
                let minX = Math.min(...cyclePoints.map(n => (n & 255) * (width / 256)));
                let minY = Math.min(...cyclePoints.map(n => ((n >> 8) & 255) * (height / 256)));
                let maxX = Math.max(...cyclePoints.map(n => (n & 255) * (width / 256)));
                let maxY = Math.max(...cyclePoints.map(n => ((n >> 8) & 255) * (height / 256)));
                stroke('#FFA500');
                    noFill();
                    rect(minX, minY, maxX - minX, maxY - minY);
            }
        }

        function f(n) {
            return (n + bitReverse15(Math.floor(n / 2))) % 65536;
        }

        function bitReverse15(n) {
            let reversed = 0;
            for (let i = 0; i < 15; i++) {
                reversed = (reversed << 1) | (n & 1);
                n >>= 1;
            }
            return reversed;
        }

        function generatePath(start, steps) {
            let path = [start];
            for (let i = 0; i < steps; i++) {
                path.push(f(path[i]));
            }
            return path;
        }

        function findFixedPoints() {
            let fixedPoints = [];
            for (let n = 0; n <= 65535; n++) {
                if (f(n) === n) {
                    fixedPoints.push(n);
                }
            }
            return fixedPoints;
        }

        function updateSimulation() {
            let path = generatePath(initializationValue, maxSteps);
            let cycleInfo = detectCycle(path);
            document.getElementById('info-text').textContent = cycleInfo;
            redraw();
        }

        function detectCycle(path) {
            let seen = new Map();
            for (let i = 0; i < path.length; i++) {
                if (seen.has(path[i])) {
                    let cycleLength = i - seen.get(path[i]);
                    return `result of initial condition ${initializationValue} repeats in ${cycleLength} steps`;
                }
                seen.set(path[i], i);
            }
            return `result of initial condition ${initializationValue} does not repeat in ${maxSteps + 1} steps`;
        }

        document.getElementById('slider-initialization').addEventListener('input', (e) => {
            initializationValue = parseInt(e.target.value);
            document.getElementById('value-initialization').textContent = `+ ${initializationValue}`;
            updateSimulation();
        });

        document.getElementById('slider-max-steps').addEventListener('input', (e) => {
            maxSteps = parseInt(e.target.value);
            document.getElementById('value-max-steps').textContent = `+ ${maxSteps}`;
            updateSimulation();
        });

        document.getElementById('checkbox-cycle4').addEventListener('change', (e) => {
            cycle4Checked = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-cycle2').addEventListener('change', (e) => {
            cycle2Checked = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-cycle1').addEventListener('change', (e) => {
            cycle1Checked = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-all-points').addEventListener('change', (e) => {
            allPointsChecked = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-modify').addEventListener('change', (e) => {
            modifyChecked = e.target.checked;
            // No visual change required for this checkbox
        });
    </script>
</body>
</html>
