    <script>
        const simulationParams = {
            reflux_ratio: {
                composition: {
                    benzene: {
                        'Cond': { initial: 0.98, Kp: -0.1, tau: 1.2 },
                        '5': { initial: 0.8872, Kp: 0.02, tau: 0.5 },
                        'Reb': { initial: 0.02, Kp: 0.05, tau: 1.8 }
                    },
                    toluene: {
                        'Cond': { initial: 0.01, Kp: -0.2, tau: 1.5 },
                        '5': { initial: 0.1, Kp: 0.03, tau: 0.6 },
                        'Reb': { initial: 0.03, Kp: 0.06, tau: 1.9 }
                    },
                    pxylene: {
                        'Cond': { initial: 0.002, Kp: -0.3, tau: 1.3 },
                        '5': { initial: 0.01, Kp: 0.04, tau: 0.7 },
                        'Reb': { initial: 0.005, Kp: 0.07, tau: 2.0 }
                    }
                },
                temperature: {
