                    'Reb': { initial: 250, Kp: 15, tau: 1.8 }
                },
                molar_hold_up: {
                    'Cond': { initial: 10, Kp: -0.5, tau: 1.2 },
                    '5': { initial: 8, Kp: 0.5, tau: 0.5 },
                    'Reb': { initial: 2, Kp: 1, tau: 1.8 }
                }
            },
            reboil_ratio: {
                composition: {
                    benzene: {
                        'Cond': { initial: 0.95, Kp: -0.05, tau: 1.1 },
                        '5': { initial: 0.85, Kp: 0.01, tau: 0.4 },
