            const component = document.querySelector('input[name="component"]:checked').value;

            const params = simulationParams[stepType][plotType][component][stage];
            const { initial, Kp, tau } = params;

            const timeArray = Array.from({ length: 201 }, (_, i) => i * 0.1);
            const yData = timeArray.map(t => t < 10 ? initial : initial + Kp * stepPercent * (1 - Math.exp(-(t - 10) / tau)));

            const trace = {
                x: timeArray,
                y: yData,
                mode: 'lines',
