        controls.enableDamping = true;

        // Set camera position
        camera.position.z = 10;

        // Render loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Handle UI interactions
        document.getElementById('select-family').addEventListener('change', () => {
            const family = document.getElementById('select-family').value;
            const constellations = family === 'all' ? Object.keys(constellationData) : constellationFamilies[family];
            const selectConstellation = document.getElementById('select-constellation');
            selectConstellation.innerHTML = '<option value="None">None</option>' + constellations.map(c => `<option value="${c}">${c}</option>`).join('');
            selectConstellation.value = family === 'Zodiac' ? 'Cygnus' : constellations[0];
        });

        document.getElementById('select-constellation').addEventListener('change', () => {
