            // Update constellation lines and stars
        });

        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', () => {
                const object = scene.getObjectById(parseInt(checkbox.id.replace('check-', '')));
                if (object) object.visible = checkbox.checked;
            });
        });

        document.getElementById('slider-ra').addEventListener('input', () => {
            const ra = parseFloat(document.getElementById('slider-ra').value);
            const dec = parseFloat(document.getElementById('slider-dec').value);
            updateZenith(ra, dec);
        });

        document.getElementById('slider-dec').addEventListener('input', () => {
            const ra = parseFloat(document.getElementById('slider-ra').value);
            const dec = parseFloat(document.getElementById('slider-dec').value);
            updateZenith(ra, dec);
        });

        function updateZenith(ra, dec) {
            // Convert RA and Dec to Cartesian coordinates
            const x = 5 * Math.cos(THREE.MathUtils.degToRad(dec)) * Math.cos(THREE.MathUtils.degToRad(ra));
            const y = 5 * Math.cos(THREE.MathUtils.degToRad(dec)) * Math.sin(THREE.MathUtils.degToRad(ra));
            const z = 5 * Math.sin(THREE.MathUtils.degToRad(dec));
            zenith.position.set(x, y, z);
            zenithLine.geometry.attributes.position.setXYZ(1, x, y, z);
            zenithLine.geometry.attributes.position.needsUpdate = true;
        }

        // Initialize constellation options
        document.getElementById('select-family').dispatchEvent(new Event('change'));
