    <script>
        // Fast Fourier Transform (FFT) implementation
        function fft(real, imag) {
            const N = real.length;
            if (N <= 1) return;

            const evenRe = [];
            const evenIm = [];
            const oddRe = [];
            const oddIm = [];

            for (let k = 0; k < N / 2; k++) {
                evenRe[k] = real[2 * k];
                evenIm[k] = imag[2 * k];
                oddRe[k] = real[2 * k + 1];
                oddIm[k] = imag[2 * k + 1];
            }

            fft(evenRe, evenIm);
            fft(oddRe, oddIm);

            const pi = Math.PI;
            const t = -2 * pi / N;
            const s = Math.sin(t);
            let c = Math.cos(t);
            let s2;
            let c2;

            for (let k = 0; k < N / 2; k++) {
                s2 = s * c;
                c2 = c * c - s * s;
                s = s2;
                c = c2;

                const t = evenRe[k];
                const u = evenIm[k];
                const v = oddRe[k];
                const w = oddIm[k];

                real[k] = t + c * v - s * w;
                imag[k] = u + c * w + s * v;
                real[k + N / 2] = t - c * v + s * w;
                imag[k + N / 2] = u - c * w - s * v;
            }
        }

        // Global variables
        let normalized_mag = null;

        // Initialize plots
        function initPlots() {
            Plotly.newPlot('plot-signal', [{
                x: [],
                y: [],
                mode: 'lines'
            }], {
                title: 'Signal',
                xaxis: { title: 'time[s]', range: [0, 0.005], autorange: false },
                yaxis: { title: '', range: [-2, 2], autorange: false },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });

            Plotly.newPlot('plot-spectrum', [{
                x: [],
                y: [],
                type: 'bar',
                marker: { width: 5 }
            }], {
                title: 'Magnitude Spectrum (DFT)',
                xaxis: { title: 'frequency[Hz]', range: [0, 8000], autorange: false },
                yaxis: { title: '' },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });
        }

        // Update simulation
        function updateSimulation() {
            const f = 1000; // Input signal frequency
            const A = 1; // Input signal amplitude
            const N = 2048; // Number of samples
            const fs = 32000; // Sampling frequency

            const c1 = parseFloat(document.getElementById('slider-c1').value);
            const c2 = parseFloat(document.getElementById('slider-c2').value);
            const clippingEnabled = document.getElementById('checkbox-clipping').checked;
            const level = parseFloat(document.getElementById('slider-level').value);

            const t = Array.from({ length: N }, (_, i) => i / fs);
            const x = t.map(time => A * Math.sin(2 * Math.PI * f * time));
            const y = x.map(val => val + c1 * val ** 2 + c2 * val ** 3);

            if (clippingEnabled) {
                y.forEach((val, i) => y[i] = Math.max(-level, Math.min(level, val)));
            }

            Plotly.react('plot-signal', [{
                x: t.slice(0, Math.floor(fs * 0.005)),
                y: y.slice(0, Math.floor(fs * 0.005)),
                mode: 'lines'
            }]);

            const real = [...y];
            const imag = Array(N).fill(0);
            fft(real, imag);

            normalized_mag = real.slice(0, N / 2).map(mag => mag / N);
            normalized_mag[0] *= 2;
            normalized_mag[N / 2 - 1] *= 2;

            updateSpectrumScale();
        }

        // Update spectrum scale
        function updateSpectrumScale() {
            const freq = Array.from({ length: normalized_mag.length }, (_, k) => k * fs / N);
            const scale = document.querySelector('.active').textContent.toLowerCase();

            let yData;
            let yRange;

            if (scale === 'linear') {
                yData = normalized_mag;
                yRange = [0, 1.1];
            } else if (scale === 'decibel') {
                yData = normalized_mag.map(mag => mag > 0 ? 20 * Math.log10(mag) : -100);
                yRange = [-100, 0];
            }

            Plotly.react('plot-spectrum', [{
                x: freq,
                y: yData,
                type: 'bar',
                marker: { width: 5 }
            }], {
                yaxis: { range: yRange }
            });
        }

        // Event listeners
        document.getElementById('slider-c1').addEventListener('input', updateSimulation);
        document.getElementById('reset-c1').addEventListener('click', () => {
            document.getElementById('slider-c1').value = 0;
            updateSimulation();
        });
        document.getElementById('slider-c2').addEventListener('input', updateSimulation);
        document.getElementById('reset-c2').addEventListener('click', () => {
            document.getElementById('slider-c2').value = 0;
            updateSimulation();
        });
        document.getElementById('checkbox-clipping').addEventListener('change', () => {
            const levelControlGroup = document.getElementById('level-control-group');
            levelControlGroup.style.display = levelControlGroup.style.display === 'none' ? 'block' : 'none';
            updateSimulation();
        });
        document.getElementById('slider-level').addEventListener('input', updateSimulation);
        document.getElementById('btn-linear').addEventListener('click', () => {
            document.getElementById('btn-linear').classList.add('active');
            document.getElementById('btn-decibel').classList.remove('active');
            updateSpectrumScale();
        });
        document.getElementById('btn-decibel').addEventListener('click', () => {
            document.getElementById('btn-decibel').classList.add('active');
            document.getElementById('btn-linear').classList.remove('active');
            updateSpectrumScale();
        });

        // Initial setup
        initPlots();
        updateSimulation();
    </script>
</body>
