<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Optical Simulation</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        .container {
            display: flex;
            height: 100vh;
        }
        #control-panel {
            width: 250px;
            padding: 20px;
            background-color: #f4f4f4;
        }
        #control-panel div {
            margin-bottom: 10px;
        }
        #canvas-container {
            flex-grow: 1;
            display: flex;
            justify-content: center;
            align-items: center;
        }
        canvas {
            border: 1px solid #000;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div class="container">
        <div id="control-panel">
            <div>
                <label for="slider-ray-count">Ray Count:</label>
                <input type="range" id="slider-ray-count" min="1" max="50" value="15" step="1">
                <span id="ray-count-value">15</span>
            </div>
            <div>
                <label for="slider-beam-width">Beam Width:</label>
                <input type="range" id="slider-beam-width" min="0.1" max="2.0" value="1.25" step="0.01">
                <span id="beam-width-value">1.25</span>
            </div>
            <div>
                <label for="slider-incident-angle">Incident Angle (°):</label>
                <input type="range" id="slider-incident-angle" min="-90" max="90" value="-45" step="1">
                <span id="incident-angle-value">-45</span>
            </div>
            <div>
                <label for="slider-refraction-ratio">Refraction Ratio:</label>
                <input type="range" id="slider-refraction-ratio" min="1.0" max="3.0" value="2.0" step="0.01">
                <span id="refraction-ratio-value">2.0</span>
            </div>
            <div>
                <label><input type="checkbox" id="checkbox-injected" checked> Injected</label>
                <label><input type="checkbox" id="checkbox-refracted" checked> Refracted</label>
                <label><input type="checkbox" id="checkbox-reflected" checked> Reflected</label>
                <label><input type="checkbox" id="checkbox-ejected" checked> Ejected</label>
                <label><input type="checkbox" id="checkbox-normals"> Normals</label>
                <label><input type="checkbox" id="checkbox-intersections"> Intersections</label>
                <label><input type="checkbox" id="checkbox-beam-top" checked> Beam Top Half</label>
                <label><input type="checkbox" id="checkbox-beam-bottom" checked> Beam Bottom Half</label>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let canvas;
        const rayCount = document.getElementById('slider-ray-count');
        const beamWidth = document.getElementById('slider-beam-width');
        const incidentAngle = document.getElementById('slider-incident-angle');
        const refractionRatio = document.getElementById('slider-refraction-ratio');

        const rayCountValue = document.getElementById('ray-count-value');
        const beamWidthValue = document.getElementById('beam-width-value');
        const incidentAngleValue = document.getElementById('incident-angle-value');
        const refractionRatioValue = document.getElementById('refraction-ratio-value');

        rayCount.addEventListener('input', () => {
            rayCountValue.textContent = rayCount.value;
            redrawCanvas();
        });

        beamWidth.addEventListener('input', () => {
            beamWidthValue.textContent = beamWidth.value.toFixed(2);
            redrawCanvas();
        });

        incidentAngle.addEventListener('input', () => {
            incidentAngleValue.textContent = incidentAngle.value;
            redrawCanvas();
        });

        refractionRatio.addEventListener('input', () => {
            refractionRatioValue.textContent = refractionRatio.value.toFixed(2);
            redrawCanvas();
        });

        function setup() {
            canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);
            scale(height / 5);

            // Draw axes
            stroke(150);
            line(-2.5, 0, 2.5, 0); // X-axis
            line(0, -2.5, 0, 2.5); // Y-axis

            // Draw tick marks and labels
            textSize(12);
            textAlign(CENTER, CENTER);
            for (let i = -2; i <= 2; i++) {
                line(i, -0.1, i, 0.1);
                text(i, i, 0.2);
                line(-0.1, i, 0.1, i);
                text(i, 0.2, i);
            }

            // Draw sphere
            noFill();
            stroke(0);
            ellipse(0, 0, 2, 2);

            // Ray tracing
            const N = parseInt(rayCount.value);
            const w = parseFloat(beamWidth.value);
            const angle = radians(parseFloat(incidentAngle.value));
            const n1 = 1;
            const n2 = parseFloat(refractionRatio.value);

            for (let i = 0; i < N; i++) {
                const t = map(i, 0, N - 1, -w / 2, w / 2);
                const dir = p5.Vector.fromAngle(angle + atan(t / 1));
                let ray = { pos: createVector(-2, 0), dir: dir };

                // First intersection
                let intersection = intersectCircle(ray);
                if (intersection) {
                    drawRay(ray.pos, intersection, color(255, 0, 0)); // Injected
                    if (document.getElementById('checkbox-intersections').checked) {
                        fill(255, 0, 0);
                        noStroke();
                        ellipse(intersection.x, intersection.y, 0.05, 0.05);
                    }

                    // First refraction
                    const normal = intersection.copy().normalize();
                    const theta1 = dir.angleBetween(normal);
                    const theta2 = asin(n1 * sin(theta1) / n2);
                    const refractedDir = p5.Vector.sub(dir, normal.mult(2 * dot(dir, normal))).rotate(theta2);
                    ray = { pos: intersection, dir: refractedDir };

                    if (document.getElementById('checkbox-refracted').checked) {
                        drawRay(ray.pos, projectRay(ray), color(0, 255, 0)); // Refracted
                    }

                    // Second intersection
                    intersection = intersectCircle(ray);
                    if (intersection) {
                        if (document.getElementById('checkbox-intersections').checked) {
                            fill(0, 255, 0);
                            noStroke();
                            ellipse(intersection.x, intersection.y, 0.05, 0.05);
                        }

                        // Reflection
                        const reflectedDir = p5.Vector.sub(ray.dir, normal.mult(2 * dot(ray.dir, normal)));
                        ray = { pos: intersection, dir: reflectedDir };

                        if (document.getElementById('checkbox-reflected').checked) {
                            drawRay(ray.pos, projectRay(ray), color(0, 0, 255)); // Reflected
                        }

                        // Third intersection
                        intersection = intersectCircle(ray);
                        if (intersection) {
                            if (document.getElementById('checkbox-intersections').checked) {
                                fill(0, 0, 255);
                                noStroke();
                                ellipse(intersection.x, intersection.y, 0.05, 0.05);
                            }

                            // Second refraction
                            const normal2 = intersection.copy().normalize();
                            const theta3 = reflectedDir.angleBetween(normal2);
                            const theta4 = asin(n2 * sin(theta3) / n1);
                            const ejectedDir = p5.Vector.sub(ray.dir, normal2.mult(2 * dot(ray.dir, normal2))).rotate(theta4);
                            ray = { pos: intersection, dir: ejectedDir };

                            if (document.getElementById('checkbox-ejected').checked) {
                                drawRay(ray.pos, projectRay(ray), color(0, 0, 0)); // Ejected
                            }
                        }

                        if (document.getElementById('checkbox-normals').checked) {
                            drawNormal(intersection, normal, color(255, 0, 0));
                            drawNormal(intersection, normal2, color(0, 255, 0));
                        }
                    }
                }
            }
        }

        function drawRay(start, end, color) {
            stroke(color);
            line(start.x, start.y, end.x, end.y);
        }

        function intersectCircle(ray) {
            const A = ray.dir.x * ray.dir.x + ray.dir.y * ray.dir.y;
            const B = 2 * (ray.dir.x * ray.pos.x + ray.dir.y * ray.pos.y);
            const C = ray.pos.x * ray.pos.x + ray.pos.y * ray.pos.y - 1;

            const discriminant = B * B - 4 * A * C;
            if (discriminant < 0) return null;

            const t1 = (-B + sqrt(discriminant)) / (2 * A);
            const t2 = (-B - sqrt(discriminant)) / (2 * A);

            if (t1 > 0) return p5.Vector.add(ray.pos, ray.dir.mult(t1));
            if (t2 > 0) return p5.Vector.add(ray.pos, ray.dir.mult(t2));

            return null;
        }

        function projectRay(ray) {
            const A = ray.dir.x * ray.dir.x + ray.dir.y * ray.dir.y;
            const B = 2 * (ray.dir.x * ray.pos.x + ray.dir.y * ray.pos.y);
            const C = ray.pos.x * ray.pos.x + ray.pos.y * ray.pos.y - 1;

            const discriminant = B * B - 4 * A * C;
            if (discriminant < 0) return null;

            const t1 = (-B + sqrt(discriminant)) / (2 * A);
            const t2 = (-B - sqrt(discriminant)) / (2 * A);

            if (t1 > 0) return p5.Vector.add(ray.pos, ray.dir.mult(t1));
            if (t2 > 0) return p5.Vector.add(ray.pos, ray.dir.mult(t2));

            return null;
        }

        function drawNormal(point, normal, color) {
            stroke(color);
            strokeWeight(1);
            line(point.x, point.y, point.x + normal.x * 0.1, point.y + normal.y * 0.1);
        }

        function redrawCanvas() {
            draw();
        }
    </script>
</body>
</html>
