                </div>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        function factorial(n) {
            if (n === 0 || n === 1) return 1;
            let result = 1;
            for (let i = 2; i <= n; i++) {
                result *= i;
            }
            return result;
        }

        function updatePlot() {
            const numElements = parseInt(document.getElementById('slider-elements').value);
            const scaleType = document.querySelector('input[name="scale-type"]:checked').id;
            const rangeType = document.querySelector('input[name="range-type"]:checked').id;

            const x = Array.from({ length: numElements }, (_, i) => i + 1);

            const traces = [
                {
                    x: x,
                    y: x.map(v => v * v),
                    mode: 'lines',
                    name: 'bubble',
                    visible: document.getElementById('check-bubble').checked,
                    line: { color: 'green', width: 5 }
                },
                {
                    x: x,
                    y: x.map(v => 0.25 * v * v),
                    mode: 'lines',
                    name: 'selection',
                    visible: document.getElementById('check-selection').checked,
                    line: { color: 'orange', dash: 'dot' }
                },
                {
                    x: x,
                    y: x.map(v => v * factorial(v)),
                    mode: 'lines',
                    name: 'insertion',
                    visible: document.getElementById('check-insertion').checked || document.getElementById('check-bogo').checked,
                    line: { color: 'red', width: 2 }
                },
                {
                    x: x,
                    y: x.map(v => v * Math.log2(v)),
                    mode: 'lines',
                    name: 'merge',
                    visible: document.getElementById('check-merge').checked,
                    line: { color: 'blue', width: 2 }
                },
                {
                    x: x,
                    y: x.map(v => 1.2 * v * Math.log2(v)),
                    mode: 'lines',
                    name: 'quick',
                    visible: document.getElementById('check-quick').checked,
                    line: { color: 'purple', width: 2 }
                },
                {
                    x: x,
                    y: x.map(v => 1.5 * v * Math.log2(v)),
                    mode: 'lines',
                    name: 'heap',
                    visible: document.getElementById('check-heap').checked,
                    line: { color: 'cyan', width: 2 }
                }
            ];

            const layout = {
                title: 'time complexity',
                xaxis: {
                    title: 'number of elements',
                    range: rangeType === 'radio-range-fixed' ? [0, 20] : undefined,
                    autorange: rangeType === 'radio-range-automatic'
                },
                yaxis: {
                    title: 'operations',
                    type: scaleType === 'radio-scale-logarithmic' ? 'log' : 'linear',
                    range: rangeType === 'radio-range-fixed' ? [0, 500] : undefined,
                    autorange: rangeType === 'radio-range-automatic'
                },
                legend: {
                    orientation: 'h',
                    yanchor: 'bottom',
                    y: 1.02,
                    xanchor: 'right',
                    x: 1
                }
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        document.addEventListener('DOMContentLoaded', () => {
            updatePlot();

            document.getElementById('slider-elements').addEventListener('input', () => {
                const value = document.getElementById('slider-elements').value;
                document.getElementById('display-elements').textContent = `+ ${value}`;
                updatePlot();
            });

            document.querySelectorAll('input[name="scale-type"]').forEach(input => {
                input.addEventListener('change', updatePlot);
            });

            document.querySelectorAll('input[name="range-type"]').forEach(input => {
                input.addEventListener('change', updatePlot);
            });

            document.querySelectorAll('.control-group input[type="checkbox"]').forEach(input => {
                input.addEventListener('change', updatePlot);
            });
        });
    </script>
</body>
